insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae', 'cae', '', 'Économie Circulaire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2', 'cae', '2', 'Patrimoine de la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1', 'cae', '2.1', 'Suivi et stratégie patrimoniale', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1', 'cae', '2.1.1', 'Mettre en place une comptabilité énergétique et des émissions de GES des bâtiments publics', 'La collectivité dispose d’un bilan énergétique et technique de tous les bâtiments et équipements publics significatifs. Ce bilan inclut des éléments sur les consommations d’énergie, les gaz à effet de serre, les énergies renouvelables, la qualité de l’air intérieur.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1', 'cae', '2.1.1.1', 'Mettre en place un socle de connaissance initiale sur son patrimoine', 'Le  - Action des Collectivités Territoriales pour l’Efficacité Energétique - met à disposition un outil de simulation énergétique qui permet de tester les solutions envisageables selon les typologies de bâtiments pour évaluer les coûts, la rentabilité du projet, le gain d’économies d’énergie et d’émissions de CO2.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1.1', 'cae', '2.1.1.1.1', 'Établir une liste de tous les bâtiments et équipements (avec surface, année de construction, installations techniques, type d’occupation, …) en intégrant les éléments renseignés sur la plateforme OPERAT dans le cadre des exigences du décret tertiaire (pour les bâtiments > 1000m2)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1.2', 'cae', '2.1.1.1.2', 'Réaliser l''inventaire des contrats d’exploitation ou de maintenance de chaque bâtiment, et des principaux travaux effectués depuis leur livraison', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1.3', 'cae', '2.1.1.1.3', 'Identifier les gros consommateurs (viser les bâtiments générant 50% des consommations) et réaliser un audit énergétique.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1.4', 'cae', '2.1.1.1.4', 'Effectuer un relevé des surfaces chauffées et/ou climatisées', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1.5', 'cae', '2.1.1.1.5', 'Identifier tous les contrats de fourniture d''énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1.6', 'cae', '2.1.1.1.6', 'Saisir toutes les factures d''énergie des 3 dernières années (ou demander aux gestionnaires du réseau de distribution un détail pluriannuel des consommations des bâtiments de la collectivité si pas de suivi existant)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.1.7', 'cae', '2.1.1.1.7', 'Partager et co-construire les objectifs et le diagnostic avec les partenaires si la gestion des bâtiments est déléguée', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.2', 'cae', '2.1.1.2', 'S’organiser pour effectuer le suivi régulier des consommations énergétiques du patrimoine bâti', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.2.1', 'cae', '2.1.1.2.1', 'Déterminer la personne/structure chargée d''effectuer le relevé de la consommation énergétique (tous les agents énergétiques)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.2.2', 'cae', '2.1.1.2.2', 'Déterminer la personne/structure à qui il faut transmettre la consommation d''énergie pour la détermination de l''indice énergétique est déterminée', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.2.3', 'cae', '2.1.1.2.3', 'Mener une réflexion sur le type et le positionnement des compteurs et sous-compteurs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.2.4', 'cae', '2.1.1.2.4', 'Indiquer les tâches dans la fiche de poste gestionnaire des bâtiments et équipements', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3', 'cae', '2.1.1.3', 'Faire un bilan complet, allant au-delà de la réglementation', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.1', 'cae', '2.1.1.3.1', 'Augmenter chaque année la part de bâtiments diagnostiqués, jusqu''à connaissance quasi-totale du patrimoine (viser plus de 80% des consommations). Les bâtiments en propriété, en location, et en DSP doivent progressivement être connus.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.2', 'cae', '2.1.1.3.2', 'Élargir le bilan aux bâtiments communaux pour les EPCI, notamment en zone rurale ou faiblement dotés en patrimoine', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.3', 'cae', '2.1.1.3.3', 'Corriger les indices énergétiques du climat (chaleur ou besoin en climatisation), et les décomposer en chaleur/froid, électricité, CO2', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.4', 'cae', '2.1.1.3.4', 'Détailler l''analyse du type d''utilisation d''électricité (chauffage central, eau chaude sanitaire, air conditionné, cuisson, éclairage, électricité spécifique)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.5', 'cae', '2.1.1.3.5', 'Effectuer un relevé des consommations d''eau (par ex. par personne ou par m3/an, cf. mesure 2.3.2) en distinguant les consommations d''eau chaude (connaissances des volumes journaliers ou mensuels) et en identifiant les principaux bâtiments appropriés à une production d''eau chaude solaire collective (logement collectif social, maison de retraite, secteur hospitalier)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.6', 'cae', '2.1.1.3.6', 'Etudier les matériaux et les techniques de construction et de finition', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.7', 'cae', '2.1.1.3.7', 'Réaliser un bilan initial de la qualité de l''air intérieur des bâtiments (mesure de confinement a minima, et dans l''idéal de plusieurs polluants dans les ERP par anticipation des futures exigences réglementaires), identifier les éléments ou comportements émissifs ou dégradant la qualité de l''air', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.3.8', 'cae', '2.1.1.3.8', 'Compléter l''analyse par des visites et des entretiens avec les utilisateurs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.4', 'cae', '2.1.1.4', 'Effectuer un suivi des consommations régulier et multi-flux', '(fait dans le domaine 6 via CEP)- assurer un suivi précis des consommations des bâtiments en demandant aux gestionnaires du réseau de distribution une vision agrégée des consommations de tout ou partie des bâtiments de la collectivité
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.4.1', 'cae', '2.1.1.4.1', 'Saisir tous les gros consommateurs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.4.2', 'cae', '2.1.1.4.2', 'Effectuer le relevé chaque mois pour les bâtiments les plus consommateurs/les grandes installations ; effectuer des relevés périodiques pour les installations plus petites (surface chauffée < 500 m2).', 'Pour les installations plus petites (surface chauffée < 500 m2) des relevés plus espacés sont tolérés.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.4.3', 'cae', '2.1.1.4.3', 'Mesurer régulièrement la qualité de l''air intérieur (a minima mesure du confinement via indicateur du CO2 une fois par an) dans les bâtiments abritant des populations sensibles. Aller au-delà du renouvellement de la surveillance règlementaire tous les 7 ans dans les ERP.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.5', 'cae', '2.1.1.5', 'Elargir le périmètre des bâtiments pris en compte', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.5.1', 'cae', '2.1.1.5.1', 'Prendre en compte le patrimoine satellite (en délégation)', 'Exemples : stades, patinoires, parkings souterrains, palais des expositions, CCAS/CIAS...)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.6', 'cae', '2.1.1.6', 'Optimiser le suivi, l''entretien et l''exploitation des installations techniques', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.6.1', 'cae', '2.1.1.6.1', 'Assurer l''optimisation de l''exploitation par un spécialiste au cours des 4 dernières années', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.6.2', 'cae', '2.1.1.6.2', 'Intégrer des clauses d’intéressement aux contrats d’exploitation/maintenance des installations techniques d’énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.6.3', 'cae', '2.1.1.6.3', 'Confier une mission de suivi d’exploitation à un bureau d’étude pour les collectivités non dotées des compétences suffisantes en interne', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.6.4', 'cae', '2.1.1.6.4', 'Mettre en place un outil performant de suivi lorsque le patrimoine le justifie (bâtiments complexes, consommations importantes)', 'Un outil performant de suivi a été mis en place, relié par exemple à des compteurs communicants ou une GTC/GTB (gestion technique centralisée ou gestion technique du bâtiment). Transformer progressivement ses bâtiments les plus consommateurs en bâtiments "smart grid ready" (bâtiments acteurs du système énergétique, maîtrisant dynamiquement sa consommation, sa production et son stockage d''énergie).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.7', 'cae', '2.1.1.7', 'Communiquer', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.7.1', 'cae', '2.1.1.7.1', 'Afficher le diagnostic de performance énergétique (DPE) sur des bâtiments non concernés par l''obligation d''affichage', 'En plus des obligations règlementaires d''affichage sur la plateforme OPERAT pour les bâtiments tertiaires.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.7.2', 'cae', '2.1.1.7.2', 'Transmettre aux concierges, gardiens d''immeubles et/ou utilisateurs des bâtiments l''évaluation de la comptabilité énergétique (feed-back à leurs efforts)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.7.3', 'cae', '2.1.1.7.3', 'Ajouter le reporting des consommations du patrimoine dans le rapport annuel Développement Durable, s’il existe.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.7.4', 'cae', '2.1.1.7.4', 'Faire un bilan annuel par bâtiment de la dépense d''énergie de la commune et en présenter une synthèse aux élus et aux services', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.1.8', 'cae', '2.1.1.8', 'Identifier le potentiel d''économie (énergie et coût) et de développement des énergies renouvelables', 'Le diagnostic et le suivi réalisé permettent :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2', 'cae', '2.1.2', 'Elaborer une stratégie patrimoniale incluant un programme de rénovation', 'La collectivité formule une stratégie patrimoniale permettant de planifier et de phaser son évolution à 5 ou 10 ans (extension, réhabilitation, démolition, réaffectation, vente...), en cohérence avec des objectifs de réduction des émissions GES et de consommation énergétique de ses bâtiments. Cette stratégie est déclinée plus précisément en un programme de rénovation prenant en compte :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.1', 'cae', '2.1.2.1', 'Acter la volonté politique de mettre en place une stratégie patrimoniale ou un plan de rénovation', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.1.1', 'cae', '2.1.2.1.1', 'Disposer d''une délibération politique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.1.2', 'cae', '2.1.2.1.2', 'Disposer d''une décision budgétaire (enveloppe de travaux ou enveloppe d’ingénierie pour prestation externe d’élaboration de la stratégie)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.1.3', 'cae', '2.1.2.1.3', 'Affecter des ressources humaines en interne', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.2', 'cae', '2.1.2.2', 'Mettre en œuvre une stratégie de rationalisation de son patrimoine', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.2.1', 'cae', '2.1.2.2.1', 'Réaliser l’état des lieux technique et des usages (occupation), des besoins et de leurs dynamiques, en intégrant les éléments renseignés sur la plateforme OPERAT dans le cadre des exigences du décret tertiaire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.2.2', 'cae', '2.1.2.2.2', 'Adopter et mettre en œuvre la stratégie de rationalisation avec des premières réalisations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.2.3', 'cae', '2.1.2.2.3', 'Parvenir à maîtriser les surfaces de son patrimoine, idéalement les réduire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.2.4', 'cae', '2.1.2.2.4', 'Réaffecter pour tout ou partie dans le programme de rénovation le produit des cessions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.3', 'cae', '2.1.2.3', 'Être ambitieux et opérationnel dans la stratégie de rénovation', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.3.1', 'cae', '2.1.2.3.1', 'Définir des objectifs de long terme (2050) et court moyen terme (2030) et tracer une trajectoire cible avec les rythmes et performances à atteindre pour la massification de la rénovation énergétique du patrimoine communal (objectifs quantifiés en nombre ou % de bâtiments à rénover, avec un échéancier) sur la base d’un état des lieux initial', 'Se référer à la mesure 2.1.1 pour l''état des lieux initial.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.3.2', 'cae', '2.1.2.3.2', 'Intégrer dans le programme d’actions des mesures d’urgence/court terme (les bâtiments principaux sont traités en priorité) et des mesures à long terme pour les équipements ayant un potentiel d''économie (type de mesures, coûts prévus, moment de la mise en œuvre) ainsi qu''une estimation des potentiels d''économie d’énergie et de gaz à effet de serre et une estimation de la production d’énergies renouvelables générée', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.3.3', 'cae', '2.1.2.3.3', 'Associer le programme à une programmation pluriannuelle des investissements (PPI)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.3.4', 'cae', '2.1.2.3.4', 'Viser a minima le niveau BBC-rénovation en Métropole (éventuellement en plusieurs étapes « BBC-compatibles ») et une consommation énergétique inférieure à 300 kWhEP/m².an dans les DOM (correspondant à la classe A du DPE-G pour les bâtiments tertiaires existants sauf santé et hôtels) pour les rénovations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.4', 'cae', '2.1.2.4', 'Elaborer la stratégie dans une approche transversale climat-air-énergie', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.4.1', 'cae', '2.1.2.4.1', 'Prévoir des travaux permettant une meilleure adaptation du bâti au changement climatique (confort d''été, risques naturels...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.4.2', 'cae', '2.1.2.4.2', 'Limiter les antagonismes entre l''amélioration de la performance énergétique et la qualité de l''air intérieur et extérieur', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.4.3', 'cae', '2.1.2.4.3', 'Favoriser le recours aux filières locales d’écorénovation, contribuer à la montée en compétences des professionnels locaux', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.5', 'cae', '2.1.2.5', 'Étudier des moyens de financement innovants pour la mise en œuvre du plan pluriannuel de travaux', 'Ces éléments sont également évalués, dans une approche plus globale sur le financement de la politique climat-air-énergie, dans la mesure 5.2.1
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.5.1', 'cae', '2.1.2.5.1', 'Utiliser les Certificats d’Economie d’Énergie (CEE)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.5.2', 'cae', '2.1.2.5.2', 'Mettre en œuvre un contrat de performance énergétique (CPE), par exemple via un marché public de partenariat énergétique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.5.3', 'cae', '2.1.2.5.3', 'Mettre en œuvre un dispositif de type INTRACTING, forme de convention de performance interne à la collectivité (CPE interne)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.5.4', 'cae', '2.1.2.5.4', 'Instaurer un fonds local d’aide aux travaux via les ressources issues du développement des énergies renouvelables sur le territoire ou fonds d’amorçage gérés par l’EPCI (principe des économies d’énergies réalisées sur les premières opérations qui permettent de refinancer le fonds d’amorçage pour accompagner de nouveaux projets de rénovation)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.6', 'cae', '2.1.2.6', 'Suivre et adapter le plan pluriannuel de travaux', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.6.1', 'cae', '2.1.2.6.1', 'Renforcer le suivi énergétique des bâtiments concernés par le plan (si la compétence gestion et suivi est déléguée, la collectivité suit activement les activités du gestionnaire et dispose d’une vision de la situation existante). Le suivi est a minima annuel pour les bâtiments ciblés par le décret tertiaire.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.6.2', 'cae', '2.1.2.6.2', 'Suivre un ratio financier sur l''effort de rénovation en euros travaux/m² de surface totale (bâtiments régulés thermiquement)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.6.3', 'cae', '2.1.2.6.3', 'Vérifier l’atteinte des objectifs du plan et adapter/renforcer les actions si nécessaire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.2.6.4', 'cae', '2.1.2.6.4', 'Mettre en évidence et communiquer sur les économies d''énergie et réduction de GES engendrées par les actions effectuées', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3', 'cae', '2.1.3', 'Etre exemplaire sur les bâtiments publics neufs et rénovés', 'La collectivité définit des niveaux de performance pour la construction et la rénovation de bâtiments publics concernant :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.1', 'cae', '2.1.3.1', 'Délibérer et inscrire les niveaux de performance dans un document cadre', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.1.1', 'cae', '2.1.3.1.1', 'Décider de la réalisation', 'Avoir une délibération mentionnant la volonté d''intégrer des prescriptions énergétiques dans la construction et la rénovation des bâtiments publics.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.1.2', 'cae', '2.1.3.1.2', 'Elaborer un document "cadre" pour la qualité environnementale de ses bâtiments', 'Le document cadre inclut: référentiel, charte, cahier des charges modèles intégrant des obligations concernant les aspects climat-air-énergie des projets de construction et de rénovation, analyse du cycle de vie via la méthodologie E+C-. L’intercommunalité invite les communes à l’élaboration de ce document « cadre ». Pour les projets de rénovation de bâtiments concernés par le décret tertiaire, le document « cadre » est cohérent avec les objectifs fixés par le décret.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.2', 'cae', '2.1.3.2', 'Définir des règles d’usages des bâtiments publics', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.2.1', 'cae', '2.1.3.2.1', 'Définir des règles/référentiels d''usage des bâtiments, à destination des usagers et du personnel œuvrant dans les locaux, visant à améliorer ou atteindre les performances climat-air-énergie attendues et les maintenir sur le long terme', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3', 'cae', '2.1.3.3', 'Définir des niveaux de performances ambitieux', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.1', 'cae', '2.1.3.3.1', 'Les constructions neuves sont passives ou à énergie positive et à haute performance environnementale', 'Tel que défini dans la règlementation sur l’exemplarité pour les constructions publiques.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.2', 'cae', '2.1.3.3.2', 'Les rénovations visent un niveau de consommation à atteindre', 'BBC rénovation a minima en métropole et inférieur à 300 kWhEP/m².an dans les DOM (Classe A DPE pour les bâtiments tertiaires sauf santé et hôtels). Les niveaux de performances visés sont plus ambitieux que les objectifs fixés par le décret tertiaire.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.3', 'cae', '2.1.3.3.3', 'Les niveaux de performances et les pratiques recommandées font référence à des labels ou référentiels de qualité reconnus au niveau national ou régional', 'DOM : label ECODOM, ECODOM +, PERENE (Réunion), QEA (Guyane), exigences du crédit d’impôt pour la transition énergétique…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.4', 'cae', '2.1.3.3.4', 'Le confort d’été est pris en compte mais le recours à la climatisation est limité en été, notamment par l’architecture bioclimatique et l’optimisation de la ventilation naturelle (brasseur d’air).', 'Si le recours à la climatisation est néanmoins nécessaire, les splits sont interdits, les appareils seront de classe A, avec un EER (coefficient d’efficacité frigorifique) de 4 minimum.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.5', 'cae', '2.1.3.3.5', 'Les risques amplifiés par le changement climatique (risques inondations ou retrait-gonflement des argiles par exemple) sont pris en compte de manière forte dans la conception des bâtiments pour limiter leur vulnérabilité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.6', 'cae', '2.1.3.3.6', 'Les matériaux utilisés ont une faible énergie grise, sont locaux, recyclés ou recyclables facilement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.7', 'cae', '2.1.3.3.7', 'Les chantiers sont respectueux de l’environnement (limitation des nuisances environnementales engendrées par les différentes activités du chantier, principalement en matière de gestion des déchets, limitation des pollutions – eau, air-, respect de la biodiversité et des milieux, et limitation du bruit)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.8', 'cae', '2.1.3.3.8', 'Des mesures en faveur de la qualité de l’air intérieur sont prises', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.9', 'cae', '2.1.3.3.9', 'Des mesures sur les économies d’eau et la gestion des eaux pluviales ont été prises', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.10', 'cae', '2.1.3.3.10', 'Des mesures sur l’éclairage sont prises', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.11', 'cae', '2.1.3.3.11', 'La végétalisation des abords, voire des murs ou des toitures, est privilégiée (dans une approche multicritère dans le choix des essences : risques d’allergies, biodiversité, production alimentaire, entretien…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.3.12', 'cae', '2.1.3.3.12', 'Si existant et si nécessaire en fonction des besoins du bâtiment, le raccordement à un réseau de chaleur/froid utilisant des énergies renouvelables a été pris en compte', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.4', 'cae', '2.1.3.4', 'Construire ou rénover un ou des bâtiments exemplaires', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.4.1', 'cae', '2.1.3.4.1', 'Le(s) bâtiment(s) concerné(s) respecte(nt) les niveaux de performance fixées dans le document cadre, voire au-delà', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.4.2', 'cae', '2.1.3.4.2', 'L’opération a été l’occasion de former des professionnels et de sensibiliser les habitants', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.5', 'cae', '2.1.3.5', 'Intégrer ces niveaux de performance dans les appels d’offres', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.5.1', 'cae', '2.1.3.5.1', 'Intégrer dans les critères de choix des offres, l''analyse du cycle de vie comme outil d''aide à la décision pour les constructions ou les rénovations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.5.2', 'cae', '2.1.3.5.2', 'Utiliser des spécifications techniques pour fixer des objectifs de performances environnementales, de manière prescriptive', 'Exemples: HQE, label de performance énergétique, label « bâtiment biosourcé », etc
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.5.3', 'cae', '2.1.3.5.3', 'Intégrer dans les critères de jugement des offres les références environnementales et les pratiques internes de l''entreprise candidate', 'Expérience requise en HQE, label de performance énergétique, normes ISO 14001, démarche d''éco-responsabilité...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.5.4', 'cae', '2.1.3.5.4', 'Le niveau de performance doit être supérieur au niveau réglementaire en vigueur', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.5.5', 'cae', '2.1.3.5.5', 'L''intégration de critères environnementaux dans les consultations a déjà abouti réellement à faire basculer le choix en faveur du candidat le plus exemplaire sur ces aspects', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.6', 'cae', '2.1.3.6', 'Généraliser les opérations exemplaires et diffuser les bonnes pratiques', 'Les éléments concernant le financement des projets sont abordés dans la mesure 2.1.2 sur la stratégie patrimoniale, et dans une approche plus globale sur le financement de la politique climat-air-énergie, dans la mesure 5.2.1
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.6.1', 'cae', '2.1.3.6.1', 'Mettre en place un système de management pour le suivi des projets de construction et de rénovation (responsabilités, pilotage, tableau de bord, suivi...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.6.2', 'cae', '2.1.3.6.2', 'Diffuser réellement les documents cadres et référentiels créés dans les services, appropriés par les acteurs concernés, et appliqués systématiquement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.6.3', 'cae', '2.1.3.6.3', 'Recourir à un AMO chargé de veiller à la qualité environnementale du/des projets (pour les collectivités ne disposant pas des ressources/compétences en interne)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.1.3.6.4', 'cae', '2.1.3.6.4', 'Inciter et encourager les communes de l''intercommunalité à mettre en place des projets exemplaires (diffusion documents cadres, guides d’usages, objectifs, etc).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2', 'cae', '2.2', 'Valeurs cibles énergétiques et climatiques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.3', 'cae', '2.2.3', 'Augmenter la part de consommation en énergies renouvelables et de récupération pour la chaleur et le rafraîchissement des bâtiments publics', 'La collectivité augmente et mesure la part de chaleur et de rafraîchissement d''origine renouvelable et de récupération dans les consommations de ses bâtiments et équipements, avec une attention à la qualité de l''air extérieur. Il s’agit de prioriser les sources locales, peu émissives et la distribution via des réseaux de chaleur/froid vertueux lorsque cela est possible.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.3.1', 'cae', '2.2.3.1', 'Utiliser les énergies renouvelables et de récupération locales en priorité (selon gisement)', 'Les études de faisabilité pour la création, l’extension ou le raccordement des bâtiments publics aux réseaux de chaleur vertueux sont évaluées dans la mesure 1.2.1
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.3.1.1', 'cae', '2.2.3.1.1', 'Mobiliser le potentiel de récupération de chaleur fatale dans et à proximité du patrimoine de la collectivité (cf. mesure 3.2.1)', '', '', '', '', null, null);
