insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.3.2', 'cae', '2.2.3.2', 'Si la collectivité dispose de chaufferies bois pour ses bâtiments et équipements, privilégier le bois certifié', '', '', '', '', null, 5.0);
