insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.3.3.1', 'cae', '2.2.3.3.1', 'Privilégier lorsque cela est possible la chaleur sans combustion comme la récupération de chaleur fatale (cf. mesure 3.2.1), la géothermie, le solaire...', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.3.3.2', 'cae', '2.2.3.3.2', 'Porter une attention particulière au traitement des fumées des installations biomasse collective, allant au-delà de la réglementation', 'Exemples: mise en place des meilleures techniques disponibles – électrofiltre, filtres à manches, régulation, foyer bas-NOx ou systèmes de dé-NOx- notamment en zones sensibles en matière de qualité de l’air, à proximité des écoles, etc.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.3.4', 'cae', '2.2.3.4', 'Suivre et atteindre une part d’énergie renouvelable et de récupération conséquente pour la chaleur et le rafraîchissement des bâtiments publics', 'La proportion d''énergies renouvelables et de récupération dans le domaine thermique* atteint plus de 38% des besoins thermiques du patrimoine de la collectivité en Métropole, 75% dans les DOM (valorisation progressive à partir de 0% ; pour rappel, l''autonomie énergétique est l''objectif pour 2030).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.2', 'cae', '2.2.2', 'Augmenter l''efficacité énergétique pour l''électricité des bâtiments publics', 'La collectivité développe l''efficacité énergétique pour les usages de l''électricité dans ses bâtiments et l''évalue au moyen d''indicateurs énergétiques pour différentes catégories de bâtiments.
