insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.2.2', 'cae', '2.2.2.2', 'Mettre en place des indicateurs en kWh/m2/an d''énergie primaire par catégories de bâtiments pour les usages électriques et les comparer avec des valeurs de références locales.', '', '', '', '', null, 5.0);
