insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.2.5.2', 'cae', '2.2.2.5.2', 'Inscrire cette diminution dans le rythme imposé par le décret tertiaire', 'Pour les bâtiments soumis au décret tertiaire, le rythme imposé est de: -40 % en 2030, -50 % en 2040 et -60 % en 2050 par rapport à 2010 (ou par référence aux valeurs absolues fixées réglementairement)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.1', 'cae', '2.2.1', 'Augmenter l''efficacité énergétique pour la chaleur et le rafraîchissement des bâtiments publics', 'La collectivité met en place une démarche de sobriété énergétique, systématise l''efficacité énergétique, pour le chauffage, l''eau chaude et le rafraîchissement de ses bâtiments et l''évalue au moyen d''indicateurs énergétiques pour différentes catégories de bâtiments.
