insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.4', 'cae', '2.2.4', 'Augmenter la part de consommation en énergies renouvelables pour l''électricité des bâtiments publics', 'La collectivité augmente et mesure la part d''électricité renouvelable dans les consommations de ses bâtiments et équipements, en priorité via la mise en service d’installations de production d’énergie éolienne, photovoltaïque, hydraulique, etc. mais également via l’achat d’électricité renouvelable.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.4.1', 'cae', '2.2.4.1', 'Délibérer et monter en compétences sur le sujet', 'Pour les DOM, l’évaluation est adaptée au contexte, et se concentre sur le premier item.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.4.1.1', 'cae', '2.2.4.1.1', 'Avoir la délibération sur la décision de construire des installations de production d''électricité, en précisant le financement prévu', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.4.1.2', 'cae', '2.2.4.1.2', 'Prendre connaissance des différentes offres existantes des fournisseurs d''électricité (offres EnR, garanties d''origine, critères de qualité environnementale...) et s''organiser pour consulter le marché (connaissance fine de ses consommations par point de livraison)', '', '', '', '', null, null);
