insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.4.2.2', 'cae', '2.2.4.2.2', 'Vendre prioritairement à des acteurs (acheteurs, fournisseurs) proposant une offre renouvelable de haute qualité', 'Seulement si la production d’électricité verte est commercialisée en dehors de l''obligation d''achat. Offre de haute qualité: 100% EnR, qualité environnementale, transparence, critère d''additionnalité, fond de développement des EnR...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.4.2.3', 'cae', '2.2.4.2.3', 'Etudier l’auto-consommation et la mettre en œuvre sur les installations où elle est pertinente', '', '', '', '', null, null);
