insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.2.4.4', 'cae', '2.2.4.4', 'Acheter de l’électricité renouvelable', 'La part des achats d''électricité renouvelable* de la collectivité dans ses achats totaux d’électricité se rapproche de 100% (garantie d’origine).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3', 'cae', '2.3', 'Eclairage public et économie d''eau dans les bâtiments', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2', 'cae', '2.3.2', 'Economiser l''eau dans les bâtiments publics', 'Dans une logique de réduction de sa facture d''eau et de préservation de la ressource en eau, la collectivité augmente la maîtrise de la consommation d''eau de ses bâtiments publics. Elle l’évalue grâce à des indicateurs par habitant et par la consommation d''eau annuelle de différentes catégories de bâtiments. La collectivité met en place une politique rationnelle de gestion de l''eau (besoins et utilisation) favorisant une utilisation économe de l''eau.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.1', 'cae', '2.3.2.1', 'Réaliser un diagnostic précis des consommations d’eau et des conduites d’eau de l’ensemble des bâtiments publics et des grands consommateurs', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.1.1', 'cae', '2.3.2.1.1', 'Réaliser un diagnostic des consommations d''eau de chaque bâtiment public et des grands consommateurs (fontaines, piscines, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.1.2', 'cae', '2.3.2.1.2', 'Equiper les compteurs des bâtiments publics de télé-relève', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.2', 'cae', '2.3.2.2', 'Formaliser l’engagement de la collectivité à réduire ses consommations', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.2.1', 'cae', '2.3.2.2.1', 'Décider de l''installation des appareils et robinets économes en eau (budget alloué)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.2.2', 'cae', '2.3.2.2.2', 'S''engager pour un objectif chiffré de réduction des consommations d''eau', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.3', 'cae', '2.3.2.3', 'Sensibiliser et informer les utilisateurs des bâtiments publics', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.3.1', 'cae', '2.3.2.3.1', 'Tenir informer les utilisateurs des bâtiments publics et les employés sur une utilisation scrupuleuse de l''eau', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.4', 'cae', '2.3.2.4', 'Mettre en œuvre les actions et suivre les consommations', '', '', '', '', null, 40.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.4.1', 'cae', '2.3.2.4.1', 'Relever des valeurs de consommations à une fréquence qui dépend de la consommation', 'Relevé mensuelle pour les gros consommateurs, au minimum tous les 6 mois pour les autres.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.4.2', 'cae', '2.3.2.4.2', 'Installer les appareils et robinets économes dans les bâtiments de la collectivité les plus consommateurs d''eau', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.4.3', 'cae', '2.3.2.4.3', 'Identifier un service technique pour effectuer les réparations en cas de fuite', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.4.4', 'cae', '2.3.2.4.4', 'Porter une attention particulière sur les piscines', 'Le taux de renouvellement de l’eau de baignade (en l/j/baigneur) est bas, les piscines sont équipées de dispositifs de destruction des chloramines et/ou de récupération d’eau et des calories.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.4.5', 'cae', '2.3.2.4.5', 'Régler les poussoirs sur des durées courtes dans les bâtiments scolaires et éduquer les enfants à économiser l’eau (et ne pas jouer avec)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.4.6', 'cae', '2.3.2.4.6', 'Récupérer les eaux pluviales en toiture pour l''utilisation dans le bâtiment ou les espaces verts', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.5', 'cae', '2.3.2.5', 'Etre exemplaire sur les consommations d’eau', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.5.1', 'cae', '2.3.2.5.1', 'Diminuer les consommations/m2 par rapport aux années précédentes et communiquer officiellement (ex : rapport de développement durable, bilan énergie/GES...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.2.5.2', 'cae', '2.3.2.5.2', 'Pour les piscines, attester de leur performance par un suivi précis mensuel avec un indicateur adapté', 'Exemple : indicateur de taux de renouvellement, litres par baigneur...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3', 'cae', '2.3.3', 'Gérer sa voirie durablement', 'La collectivité gère sa voirie durablement : elle est attentive à limiter l''impact énergétique et les émissions de gaz à effet de serre de sa politique de création et d''entretien de voirie, de l''amont à l''aval (choix des matériaux, technique de mise en oeuvre, action préventive, priorisation des voiries à rénover...). Elle anticipe les risques liés au changement climatique et contribue à l''adaptation du territoire.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.1', 'cae', '2.3.3.1', 'Connaître son patrimoine (voirie)', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.1.1', 'cae', '2.3.3.1.1', 'Mettre en place et tenir à jour un SIG répertoriant les voiries', 'Il identifie les délaissés et parties de voirie non utilisées pouvant être déclassées pour être désartificialisées, recevoir un revêtement limitant le stockage du rayonnement solaire (choix de l’albédo de la surface), le ruissellement, favorisant la biodiversité et la perméabilité des sols.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.1.2', 'cae', '2.3.3.1.2', 'Connaître la longueur totale de voirie gérée par la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.1.3', 'cae', '2.3.3.1.3', 'Collecter des informations qualitatives sur l''état de chaque voirie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.2', 'cae', '2.3.3.2', 'Former/sensibiliser les agents en charge de la voirie', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.2.1', 'cae', '2.3.3.2.1', 'Former les agents pour limiter les impacts environnementaux des projets de voirie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.2.2', 'cae', '2.3.3.2.2', 'Créer des échanges entre les agents du service voirie avec le service "mobilité/transport" (sur des projets particuliers et de manière plus transversale) pour une politique cohérente visant le développement cyclable/TC et la juste part de la voiture', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.2.3', 'cae', '2.3.3.2.3', 'Inscrire la baisse de l''impact environnemental des projets voirie comme un objectif du service', 'Doit être attesté par des prises de position/directives de l''élu en charge de ce sujet ou du chef de service (référentiel qualité de la voirie, compte-rendu de réunion, délibération, note de service, fiche de poste...).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.3', 'cae', '2.3.3.3', 'Adopter une politique préventive limitant les interventions lourdes', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.3.1', 'cae', '2.3.3.3.1', 'Privilégier le préventif plutôt que le curatif dans la programmation des travaux, afin de limiter les interventions lourdes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.3.2', 'cae', '2.3.3.3.2', 'Prendre en compte les impacts du changement climatique dans les critères de durabilité des voiries et techniques de mise en oeuvre', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.3.3', 'cae', '2.3.3.3.3', 'Coordonner des travaux entre les différents services et partenaires (réseaux énergétiques, télécom, assainissement, eau, espaces verts...) pour limiter les interventions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.4', 'cae', '2.3.3.4', 'Limiter l''impact environnemental des matériaux de voirie', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.4.1', 'cae', '2.3.3.4.1', 'Renforcer l''usage des matériaux suivants dans ces chantiers : matériaux perméables, recyclés, à faible albédo, à basse température de mise en oeuvre (enrobés à froid ou tiède...), liants végétaux', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.4.2', 'cae', '2.3.3.4.2', 'Utiliser des outils d''aides à la décision et à la gestion de projet :', 'Exemples: éco-comparateurs dans les activités de construction et d''entretien, label HQE infrastructures...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.4.3', 'cae', '2.3.3.4.3', 'Prévoir et pratiquer la valorisation des déchets issus des voiries en fin de vie (tri à la source, recyclage...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.4.4', 'cae', '2.3.3.4.4', 'Inscrire des critères environnementaux dans les appels d''offres des chantiers voirie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.5', 'cae', '2.3.3.5', 'Anticiper et encourager les changements de comportement et le report modal via la politique voirie', 'Les éléments concernant le stationnement sont traitées dans la mesure 4.2.1
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.5.1', 'cae', '2.3.3.5.1', 'Dans les projets neufs, adapter les gabarits aux usages, limiter l''emprise de la voirie réservée aux voitures (normes minimales).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.5.2', 'cae', '2.3.3.5.2', 'Limiter la création de nouvelles voiries au strict nécessaire', 'Exemple : pas de projets voirie rendant l''usage de la voiture plus confortable/rapide sans gain GES attesté par une étude.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.6', 'cae', '2.3.3.6', 'Témoigner de la baisse de l''impact environnemental de sa politique voirie', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.6.1', 'cae', '2.3.3.6.1', 'Suivre de manière chiffrée l''avancement de sa politique et se fixer des objectifs', 'Exemples : % de matériaux recyclés incorporés (en tonne), % de chantiers utilisant des enrobés tièdes ou à froid, % de surfaces utilisant des matériaux clairs sur les nouveaux projets d''espaces publics, % de chantiers utilisant des matériaux perméables (béton drainant, dalle à engazonner...).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.3.6.2', 'cae', '2.3.3.6.2', 'Suivre un indicateur visant la sobriété', 'Cet indicateur doit être "contenu", par exemple un ratio en m² de voirie/hab, ou en tonne de matériaux acheté/hab...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1', 'cae', '2.3.1', 'Optimiser l''éclairage public', 'La collectivité systématise la sobriété et l''efficacité énergétique de son éclairage public et l''évalue sur la base d''indicateurs. Elle met en place des technologies et pratiques économes en énergie et contribue à la prévention, à la réduction et à la limitation des nuisances lumineuses (suppression des lampadaires inutiles et redondants, horaires d’allumage et d’extinction dans les zones propices, éclairages à détection de présence, lampadaires économes, proportion de lumière, limitation des températures de couleur etc.).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.1', 'cae', '2.3.1.1', 'Réaliser un diagnostic de l’éclairage public', 'La collectivité désigne 2 référents énergie (1 élu et 1 technicien) qui aident le prestataire/ou le service (si réalisé en interne) à récolter les données nécessaires à l’étude.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.2', 'cae', '2.3.1.2', 'Mettre en place des actions de sobriété sur l’éclairage public en priorité', 'La collectivité met en place des actions de sobriété, attestées par la progression d''un indicateur : diminution du nb de point lumineux/hab ou /km, baisse du nombre d''heures d''éclairement, % de communes pratiquant l''extinction (pour les EPCI compétents), pas seulement lampadaires mais aussi mobilier urbain, enseignes.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3', 'cae', '2.3.1.3', 'Délibérer et mettre en œuvre un plan d’optimisation de l’éclairage public', '', '', '', '', null, 35.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.1', 'cae', '2.3.1.3.1', 'Mettre en œuvre de mesures d''amélioration des performances issues du diagnostic', 'Exemples de mesures: Optimisation tarifaire, maintenance préventive, modernisation des équipements, modification/optimisation du service rendu – extinction ou variateur de tension la nuit, choix judicieux des points lumineux, généralisation de l’éclairage à détection de présence ou encore la modération de l’éclairage des bâtiments, statues et façades...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.2', 'cae', '2.3.1.3.2', 'Formaliser des exigences/objectifs en matière d''entretien/maintenance dans un document dédié (contrat de maintenance, régie...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.3', 'cae', '2.3.1.3.3', 'Inscrire des mesures de limitation des consommations énergétiques dans un document formalisé, un plan pluriannuel d’optimisation (Schéma directeur d''aménagement lumière / Plan lumière, Plan d’aménagement Lumière ou autre document...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.4', 'cae', '2.3.1.3.4', 'Porter une attention à la concertation avec la population et à la valorisation de la démarche', 'Réunion publique, observation des étoiles, information sur le découplage sécurité des riverains/éclairage, bénéfices sur la biodiversité, labellisation « villes et villages étoilés »...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.5', 'cae', '2.3.1.3.5', 'Identifier les enseignes lumineuses illégales et prendre des mesures d’accompagnement pour y remédier (prise en compte de l’énergie dans le règlement local de publicité quand il existe)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.6', 'cae', '2.3.1.3.6', 'Intégrer une opération de portage collectif (EPCI, syndicat d’énergie, Agence locale de l’énergie) sur l’optimisation de l’éclairage public', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.7', 'cae', '2.3.1.3.7', 'Porter une attention au dimensionnement et à la performance des décorations de Noël', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.3.8', 'cae', '2.3.1.3.8', 'Respecter les prescriptions techniques de l’arrêté du 27 décembre 2018 relatif à la prévention, à la réduction et à la limitation des nuisances lumineuses', 'Prescriptions techniques: températures de couleur strictement inférieures à 3000K (idéal = 2700K) ; ratio ULR=0 (rapport du flux sortant des luminaires qui est émis dans l’hémisphère supérieur au flux total sortant des luminaires) ; densité surfacique de flux lumineux installé (lumen/m²) limitée à 35 lm/m2 en agglomération et 25lm/m2 hors agglomération.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.4', 'cae', '2.3.1.4', 'Suivre et atteindre les objectifs fixés en matière d’éclairage public', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.4.1', 'cae', '2.3.1.4.1', 'Comparer l’indicateur de consommation/habitant avec les valeurs de références (10% des points):', '*Valeur limite : 90 kWh/hab (énergie finale)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_2.3.1.4.2', 'cae', '2.3.1.4.2', 'Suivre le coût total et le coût rapporté à l’habitant de l’éclairage public (dépenses énergétiques)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6', 'cae', '6', 'Coopération, communication', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1', 'cae', '6.1', 'Stratégie de coopération et de communication', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1', 'cae', '6.1.1', 'Développer une stratégie partenariale multi-niveaux', 'La collectivité définit une stratégie de coopération multi-niveaux (locale, régionale, nationale, européenne et internationale) et à destination de tous les acteurs travaillant sur les thématiques climat air énergie. Elle recommande et défend sa politique climat-air-énergie par exemple par des prises de position sur l''élaboration des lois et règlements. Par ailleurs, elle développe des actions de coopération décentralisée.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.1', 'cae', '6.1.1.1', 'Identifier les acteurs clés à associer', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.1.1', 'cae', '6.1.1.1.1', 'Identifier et cartographier les acteurs clés (privés et publics, interne et externe) à associer', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.1.2', 'cae', '6.1.1.1.2', 'Inscrire la coopération dans les fiches de poste des agents concernés (missions identifiées et temps associé)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.2', 'cae', '6.1.1.2', 'Echanger et s’impliquer dans des projets à l’échelle extra-locale', 'Les projets de coopération spécifiques/restreints entre l’EPCI et ses communes membres sont étudiés dans la mesure 6.1.2. «Renforcer la coopération au sein de l’intercommunalité».
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.2.1', 'cae', '6.1.1.2.1', 'Coopérer avec les territoires voisins (communes ou EPCI limitrophes) sur l''inter-territoriale ou à une autre échelle (Parcs naturels régionaux, Pays…)', 'Notamment dans le cas de frontières entre territoire urbain et territoire rural (actions communes, mutualisation d''ingénierie et d''autres moyens, rencontres régionales d''échanges d’expérience, par exemple).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.2.2', 'cae', '6.1.1.2.2', 'Echanger et coopérer avec les institutions nationales et régionales impliquées dans les PCAET, SRCAE/SRADDET et PPA (DREAL, DDT, Ministère de l''écologie...)', 'Notamment sur la thématique de la vulnérabilité au changement climatique ou la qualité de l''air.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.2.3', 'cae', '6.1.1.2.3', 'Echanger et coopérer avec les chambres consulaires et plateformes d''information spécifiques aux différents acteurs du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.2.4', 'cae', '6.1.1.2.4', 'Participer aux groupes de travail dans les services régionaux spécialisés en matière d’énergie climat air (ASQUA, EIE, AASQA, etc)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.2.5', 'cae', '6.1.1.2.5', 'Participer aux commissions de suivi des sites (pour veiller à la question de la qualité de l''air, par exemple)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.3', 'cae', '6.1.1.3', 'Formaliser la stratégie partenariale', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.3.1', 'cae', '6.1.1.3.1', 'Planifier le dialogue et la collaboration à travers des accords de partenariat formalisés', 'Adhésion des partenaires à une charte partenariale, réseaux d’acteurs avec rencontres régulières, cycle de conférences, groupe de travail thématiques...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.3.2', 'cae', '6.1.1.3.2', 'Renforcer les engagements demandés aux partenaires et les impliquer dans le suivi et l’évaluation de la politique climat air énergie en fixant des objectifs chiffrés et datés par exemple en matière de réduction de GES et de consommation d’énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.3.3', 'cae', '6.1.1.3.3', 'Proposer un accompagnement technique vis-à-vis des partenaires (aide à la définition des objectifs, à l’évaluation des actions engagées, etc)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.3.4', 'cae', '6.1.1.3.4', 'Voter et budgéter le plan de coopération (valables sur plusieurs années)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.3.5', 'cae', '6.1.1.3.5', 'Mettre en œuvre au moins 2 partenariats significatifs et multi-acteurs dans le domaine climat-air-énergie durant les 4 dernières années', 'Valorisation à partir de 1 projet pour collectivités<3000 habitants.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.4', 'cae', '6.1.1.4', 'S’impliquer dans l’évolution du cadre réglementaire et législatif climat air énergie', 'Nota : ces actions, notamment pour les petites collectivités, peuvent avoir lieu dans le cadre de l’appartenance de la collectivité (techniciens et élus) à des réseaux d’acteurs comme AMORCE, AITF, FNCCR, CLER, AMF, France Urbaine, APVF ou en partenariat avec des organisations/associations environnementales d’envergure nationale ou régionale.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.4.1', 'cae', '6.1.1.4.1', 'Interpeller les acteurs sur les blocages législatifs et réglementaires rencontrés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.4.2', 'cae', '6.1.1.4.2', 'Interpeller officiellement le niveau national voire européen sur les blocages rencontrés (députés, sénateurs, commission européenne)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.4.3', 'cae', '6.1.1.4.3', 'Proposer les évolutions législatives, réglementaires et autres initiatives', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.4.4', 'cae', '6.1.1.4.4', 'Prendre position et/ou mener des actions lors de procédures de consultation ordinaires (lois, ordonnances, planifications, infrastructures de transport, etc.) attestées par des documents écrits', 'Par exemple: courriers au députés-sénateurs, questions au gouvernement, communication à la presse, lettres des lecteurs, récoltes de signatures, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.5', 'cae', '6.1.1.5', 'Mettre en place des actions au niveau international', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.5.1', 'cae', '6.1.1.5.1', 'S’engager dans des projets européens (LIFE, Interreg…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.5.2', 'cae', '6.1.1.5.2', 'Sensibiliser les élus, agents et acteurs du territoire aux enjeux de la coopération décentralisée (via des expositions par exemple)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.5.3', 'cae', '6.1.1.5.3', 'Intégrer les questions climat air énergie dans les accords existants de coopération décentralisée', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.5.4', 'cae', '6.1.1.5.4', 'Monter un ou plusieurs projets de coopération décentralisée sur les thématiques climat air énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.1.5.5', 'cae', '6.1.1.5.5', 'Budgéter l''aide au développement et les projets de coopération décentralisée (% du budget consacré à ce sujet)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3', 'cae', '6.1.3', 'Développer un plan de communication pour chaque cible du territoire', 'La collectivité établit chaque année un plan de communication spécifique climat-air-énergie et le met à jour régulièrement. Il fixe les objectifs, distribue les tâches et responsabilités et planifie les différentes actions de communication qui seront conduites en continu (par groupes cibles, avec tous les médias). L’identité climat air énergie de la collectivité est déclinée dans ce plan de communication et toutes les actions menées sont exemplaires en matière d’éco-responsabilité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.1', 'cae', '6.1.3.1', 'Connaître les besoins, identifier les cibles prioritaires et budgéter le plan de communication', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.1.1', 'cae', '6.1.3.1.1', 'Connaître les besoins en information et identifier des différents publics cibles (habitants, entreprises, locataires de logements sociaux, promoteurs...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.1.2', 'cae', '6.1.3.1.2', 'Définir le budget, les ressources humaines et le calendrier (dates ou périodicité) prévus pour les manifestations et actions de communication', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.2', 'cae', '6.1.3.2', 'Décliner l’identité climat air énergie de la collectivité dans les différents supports de communication', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.2.1', 'cae', '6.1.3.2.1', 'Prendre en compte la thématique climat air énergie dans la charte graphique de la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.2.2', 'cae', '6.1.3.2.2', 'Mettre en avant des déclarations claires relatives aux questions énergétiques et climatiques sur le site internet de la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.2.3', 'cae', '6.1.3.2.3', 'Mentionner régulièrement les démarches et actions climat air énergie dans les différents supports de communication établis et/ou utilisés par la collectivité', 'A minima 2 mentions/an et idéalement chaque mois notamment pour les Communautés d''Agglomération, Communautés Urbaine et Métropoles.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.2.4', 'cae', '6.1.3.2.4', 'Exploiter les moyens de communication existants (journal de la collectivité, réunions publiques, médias locaux, affichage, etc) pour diffuser les informations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.2.5', 'cae', '6.1.3.2.5', 'Mettre en avant des installations et projets exemplaires dans le secteur des énergies renouvelables et de la lutte contre le changement climatique (par exemple, chaudière bois collective, maisons passives...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.2.6', 'cae', '6.1.3.2.6', 'Etre cohérent dans le travail quotidien et les décisions prises', 'Par exemple: pas de salon de l''automobile, pas de vaisselle à usage unique lors des manifestations de la collectivité, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.3', 'cae', '6.1.3.3', 'Donner du poids et crédibiliser les actions de la collectivité dans sa communication', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.3.1', 'cae', '6.1.3.3.1', 'Inscrire ses démarches et actions climat-air-énergie dans des démarches de reconnaissance locales, nationales ou européennes pour crédibiliser et renforcer sa communication', 'Par exemple: ligue EnR, Cit’ergie, Rubans du développement durable, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.4', 'cae', '6.1.3.4', 'Décliner le plan de communication pour chaque cible du territoire', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.4.1', 'cae', '6.1.3.4.1', 'Mettre en place des supports de communication différenciés selon les cibles (contenu, médias et lieux de diffusion...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.4.2', 'cae', '6.1.3.4.2', 'Pour les collectivités comptant moins de 15 000 hab, mutualiser l’élaboration de supports de communication avec d''autres collectivités.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.4.3', 'cae', '6.1.3.4.3', 'Mettre en place des actions (évènements) nombreuses et variées (lieux, formes, horaires) sur les thèmes climat-air-énergie.', 'La fréquence de diffusion des informations est adaptée à la taille de la collectivité : 2 actions /an (< 3 000 hab), 5 actions /an (>3 000 hab), 10 actions /an (>50 000 hab).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.5', 'cae', '6.1.3.5', 'Contrôler et évaluer l’efficacité du plan de communication', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.5.1', 'cae', '6.1.3.5.1', 'Evaluer et contrôler de l''efficacité des actions par des enquêtes auprès des différentes cibles', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.5.2', 'cae', '6.1.3.5.2', 'Etre identifié depuis l''extérieur et depuis l''intérieur comme étant une "collectivité engagée et/ou novatrice dans les secteurs de l''énergie, de l''environnement et du climat"', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.3.5.3', 'cae', '6.1.3.5.3', 'Communiquer aux acteurs du territoire des démarches et actions climat air énergie menées sur le territoire (Cit’ergie, TEPOS, PCAET…) et leur mettre à disposition le suivi et l’évaluation de ces démarches (transparence de la collectivité)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2', 'cae', '6.1.2', 'Renforcer la coopération au sein de l''intercommunalité', 'La collectivité met en place des actions de coopération renforcée avec les autres collectivités de son territoire (dans le périmètre administratif de l’intercommunalité), dans une approche solidaire et efficace en matière de politique climat-air-énergie entre intercommunalité et communes membres. Elle s''implique en tant qu''animateur ou moteur du territoire. Elle accompagne les communes ou sert d’exemple dans la mise en place de leur politique climat air énergie et encourage la mise en place d’actions mutualisées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.1', 'cae', '6.1.2.1', 'Partager une stratégie climat-air-énergie commune', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.1.1', 'cae', '6.1.2.1.1', 'Echanger/relayer/diffuser des données, des documents et des bonnes pratiques entre EPCI et communes membres, concernant leur politique climat air énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.1.2', 'cae', '6.1.2.1.2', 'Etre à l’initiative ou participer à des rencontres ponctuelles bilatérales commune/intercommunalité en lien avec le climat, l’air et l’énergie', 'Par exemple: conférences, groupes de travail thématique, rencontres à l’occasion de l’élaboration des documents de planification et d’urbanisme. Pour les EPCI : les rencontres sont fréquemment délocalisées sur le territoire pour les territoires étendus pour faciliter la participation d’un maximum de communes membres.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.2', 'cae', '6.1.2.2', 'Connaître les rôles de chacun en matière de climat, d’air et d’énergie', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.2.1', 'cae', '6.1.2.2.1', 'Sur les thématiques en lien avec le climat, l’air et l’énergie, les rôles et compétences de chacun (intercommunalité/commune) sont clairs', 'Le cas échéant des précisions sont apportées et formalisées (délibération précisant les compétences, convention EPCI-commune membre)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.2.2', 'cae', '6.1.2.2.2', 'Les interlocuteurs climat-air-énergie principaux dans chaque collectivité sont connus et ont été rencontrés', 'Par exemple : le chef de projet « énergie » de l’intercommunalité a un interlocuteur privilégié au sein de chaque commune membre, les homologues sur le sujet de l’énergie entre communes membres se connaissent
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.3', 'cae', '6.1.2.3', 'Initier et/ou participer à des actions mutualisées sur le territoire', 'Exemples de projets collectifs concernés (initiation ou participation a minima 2 actions collectives durant les 4 dernières années) :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.3.1', 'cae', '6.1.2.3.1', 'EPCI : la collectivité est animatrice sur le sujet climat-air-énergie sur son territoire.', 'Elle a mis en place des actions mutualisées avec ses communes, et les soutient dans leurs projets climat-air-énergie.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.3.2', 'cae', '6.1.2.3.2', 'Commune : la collectivité est force de proposition auprès de l’intercommunalité pour la mise en place d’actions mutualisées et participe régulièrement aux actions collectives proposées par son EPCI.', 'En cas de faiblesse de l’EPCI sur le sujet, elle initie des actions collectives entre communes-membres.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.4', 'cae', '6.1.2.4', 'Systématiser la coopération intercommunale et donner l’exemple sur le territoire', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.4.1', 'cae', '6.1.2.4.1', 'EPCI: Mettre en place un outil global pour accompagner les communes membres sur les thématique climat-air-énergie', 'Par exemple: kit pédagogique, plateforme internet, cellule d’appui technique…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.4.2', 'cae', '6.1.2.4.2', 'EPCI: Réflechir sur de nouveaux outils juridico-financiers pour faciliter le passage à l’acte des communes', 'Par exemple: faisabilité d’un fonds local issu des ressources générées par le développement des énergies renouvelables à l’échelle de l’EPCI, faisabilité d’une société publique locale dédiée à la rénovation pour accompagner les communes dans leurs projets…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.4.3', 'cae', '6.1.2.4.3', 'EPCI: Institutionnaliser les réunions thématiques avec les communes membres', 'Composition et fréquence de réunions déterminées à l’avance, notion de « Club climat-air-énergie » ou équivalent réunissant régulièrement les collectivités volontaires sur le territoire...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.4.4', 'cae', '6.1.2.4.4', 'Commune: Elle donne l’exemple au sein de l’EPCI', 'Expérimentation d’actions pilotes sur le territoire communal (ex : lauréat d’appel à projets de l’EPCI, volontariat pour tester des outils…), faire figure de référence auprès des autres communes membres (témoignage fréquent…)...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.1.2.4.5', 'cae', '6.1.2.4.5', 'Commune: Participation quasi-systématique aux actions proposées par l’EPCI', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2', 'cae', '6.2', 'Bâtiments résidentiels et tertiaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2', 'cae', '6.2.2', 'Prévenir et lutter contre la précarité énergétique', 'La collectivité engage une politique de prévention et de lutte contre la précarité énergétique (liée au logement et à la mobilité) et l''inscrit dans ses documents stratégiques.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.1', 'cae', '6.2.2.1', 'Réaliser un état des lieux de la précarité énergétique liée au logement et à la mobilité sur le territoire', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.1.1', 'cae', '6.2.2.1.1', 'Disposer de la liste des acteurs intervenants sur la thématique de précarité, susceptibles d’intégrer la question de la précarité énergétique liée au logement et à la mobilité sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.1.2', 'cae', '6.2.2.1.2', 'Réaliser un diagnostic (données INSEE, ANAH, CCAS/CIAS, CAF, FSL…) afin d''identifier et caractériser les ménages en situation de précarité énergétique', 'Diagnostic: quantification, localisation, caractéristiques sociodémographiques des ménages et de leurs logements, type de chauffage principal et d''appoint ainsi que le type de système de renouvellement d''air, nombre d''habitants au m2...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.1.3', 'cae', '6.2.2.1.3', 'Cartographier les zones d’intervention (OPAH, PIG, programme de rénovation urbaine...) et les comparer aux zones où statistiquement il peut y avoir de la précarité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.2', 'cae', '6.2.2.2', 'Adopter une stratégie de lutte contre la précarité énergétique liée au logement et à la mobilité', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.2.1', 'cae', '6.2.2.2.1', 'Introduire la lutte contre la précarité énergétique comme un enjeu prioritaire de la politique du territoire dans les différents documents directeurs (PLH, PDU…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.2.2', 'cae', '6.2.2.2.2', 'Fixer et formaliser dans les documents du territoire des objectifs et des orientations en matière de lutte contre la précarité énergétique et de recherche de baisse des charges pour les usagers', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.3', 'cae', '6.2.2.3', 'Fédérer les acteurs autour de la problématique de la précarité énergétique afin d’initier la prévention contre la précarité énergétique', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.3.1', 'cae', '6.2.2.3.1', 'Coordonner et fédérer les acteurs locaux gravitant autour de la précarité énergétique pour le logement (bailleurs sociaux, CCAS, ADIL...en lien avec l’ANAH) et pour la mobilité : rencontres régulières, mise en réseau, outils partagés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.3.2', 'cae', '6.2.2.3.2', 'Sensibiliser les élus et les services à la lutte contre la précarité énergétique (le CCAS ou CIAS -Centre Communal ou Intercommunal d''Action Sociale- notamment)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.3.3', 'cae', '6.2.2.3.3', 'Informer et sensibiliser les ménages sur la précarité énergétique liée au logement, à la mobilité (dispositif “Familles à Energie Positive” par exemple)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.3.4', 'cae', '6.2.2.3.4', 'Sensibiliser et communiquer sur les outils existants', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.3.5', 'cae', '6.2.2.3.5', 'Sensibiliser les acteurs et les habitants sur les enjeux sanitaires liés à une mauvaise qualité de l''air intérieur', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.4', 'cae', '6.2.2.4', 'Formaliser une offre d''outils à destination des ménages en situation de précarité', '', '', '', '', null, 40.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.4.1', 'cae', '6.2.2.4.1', 'Développer et proposer une aide financière locale à destination des ménages en situation de précarité énergétique (en particulier, aides à la rénovation pour les propriétaires occupants)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.4.2', 'cae', '6.2.2.4.2', 'Mettre en place des actions de suivi et d''accompagnement des ménages précaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.4.3', 'cae', '6.2.2.4.3', 'Mettre en place des actions conjointes avec les acteurs de l''hébergement pour diminuer la facture énergétique des logements et les inciter à des constructions et rénovations performantes, à recourir aux solutions énergétiques les moins onéreuses', 'Exemple d''actions conjointes : convention/charte d''engagement/accords, contrat local d’engagement –CLE- convention ANRU...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.4.4', 'cae', '6.2.2.4.4', 'Utiliser les outils existants et en créer des nouveaux', 'Par exemple: PDALPD, baux à réhabilitation, outil préventif pour l''amélioration thermique des logements, outil curatif d''aide au paiement des dettes d''énergie, Fonds de Solidarité pour le logement, Fonds Sociaux d''Aide aux Travaux de Maîtrise de l''Energie (FSATME), opération de distribution de "kits eau/énergie"...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.2.4.5', 'cae', '6.2.2.4.5', 'Monter des partenariats pour proposer/élaborer une offre de solutions alternatives afin d''éviter les déplacements contraints en véhicule individuel pour les publics précaires', 'Par exemple: gratuité/tarif réduit des transports en commun, transports à la demande ou locations de vélo pour les demandeurs d’emplois ou seniors, desserte TC ou transport à la demande des agences Pôle emploi…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3', 'cae', '6.2.3', 'Accompagner les particuliers pour la rénovation et la construction durable de leur logement (habitat individuel et collectif)', 'La collectivité gère ou soutient un ou des centre(s) de conseil (local ou régional) sur le climat, l''air et l''énergie à l''intention des particuliers. Elle soutient également financièrement les travaux de rénovation du secteur résidentiel. Ces missions sont assurées dans l''idéal par un guichet unique type espace conseil FAIRE .
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.1', 'cae', '6.2.3.1', 'Disposer de services d’information climat-air-énergie visibles et accessibles pour le particulier', 'Exemple pour une commune non compétente : il s''agit d''accueillir une permanence, de diffuser de la documentation sur ce service, de faciliter les déplacements vers ce service...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.1.1', 'cae', '6.2.3.1.1', 'Promouvoir activement l''offre d''information et de conseils', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.1.2', 'cae', '6.2.3.1.2', 'Disposer de structures d''accueil centrales, ouvertes au moins une fois par semaine', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.1.3', 'cae', '6.2.3.1.3', 'Disposer d’un service d''information propre à la collectivité ou participer activement à un service d''information local/départemental/régional neutre pour les questions concernant le climat, l''air et l''énergie dans le bâtiment pour les particuliers (suffisant pour les collectivités inférieures à 10 000 habitants)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.1.4', 'cae', '6.2.3.1.4', 'Simplifier l’accès à ces services d''information (lieu, prix) pour le grand public', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.2', 'cae', '6.2.3.2', 'Adresser des conseils pour la rénovation BBC notamment, et la construction, à des cibles variées', 'Exemple pour une commune non compétente : elle repère les différentes cibles et fait une communication différenciée, elle facilite le repérage des bénéficiaires en relayant les opérations portées par l’espace conseil FAIRE...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.3', 'cae', '6.2.3.3', 'Accompagner le particulier jusqu’aux travaux de rénovation énergétique', 'Les ECF sont abordés également dans les mesures 1.2.4 (volet planification) et 6.2.1 (volet mobilisation des professionnels du bâtiment).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.3.1', 'cae', '6.2.3.3.1', 'S''appuyer a minima sur les missions de son espace conseil FAIRE pour guider le particulier dans son parcours de rénovation de son logement.', 'L''accompagnement le plus complet possible est recherché : aide à la réalisation d''un audit énergétique, fléchage artisan RGE, analyse des devis, aides au montage des dossiers de subvention)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.3.2', 'cae', '6.2.3.3.2', 'Disposer d’un espace conseil FAIRE actif sur son territoire pour accompagner le particulier jusqu’aux travaux, qui inclut des prestations avancées', 'Par exemple : analyse de devis, visite-conseils à domicile, récupération de CEE…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.4', 'cae', '6.2.3.4', 'Soutenir financièrement le particulier', 'Exemple pour une commune non compétente : elle abonde les aides mises en place à un échelon supérieur.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.4.1', 'cae', '6.2.3.4.1', 'Des aides financières ponctuelles existent à la rénovation niveau BBC (notamment) et la construction durable', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.4.2', 'cae', '6.2.3.4.2', 'Mettre en place des programmes de financement globaux, cohérents et partenariaux, ou pour certains publics ANRU, OPAH, PIG, CLE ou équivalents pour une massification des actions de rénovation de l''habitat', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.4.3', 'cae', '6.2.3.4.3', 'Couvrir par les aides financières des publics ANAH et hors ANAH', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.5', 'cae', '6.2.3.5', 'Mesurer les résultats de l’accompagnement', 'Le suivi de l’efficacité de la politique de rénovation énergétique dans son ensemble est mesuré dans la mesure 1.2.4.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.5.1', 'cae', '6.2.3.5.1', 'Contrôler les résultats des consultations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.5.2', 'cae', '6.2.3.5.2', 'Nombre de consultations et temps accordé pour le conseil', 'Valeur limite: 10 min/100 hab; valeur cible: 60 min/100 hab
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.5.3', 'cae', '6.2.3.5.3', 'Communiquer et transmettre les indicateurs à ceux qui en font la demande, et en particulier les communes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.3.6', 'cae', '6.2.3.6', 'Mettre en place un dispositif de suivi des projets subventionnés pour analyser l''efficacité des moyens investis', 'Pour une commune non compétente : la commune demande à son EPCI les indicateurs à l’échelle de sa commune (10%) et les obtient (10%).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4', 'cae', '6.2.4', 'Accompagner les acteurs du secteur tertiaire pour la rénovation et la construction durable de leurs locaux', 'La collectivité a un rôle d’animateur territorial à jouer pour impulser une dynamique sur le territoire afin d’agir indirectement sur les déperditions énergétiques des bâtiments tertiaires.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.1', 'cae', '6.2.4.1', 'Disposer de services d’information climat-air-énergie visibles et accessibles pour les acteurs tertiaires', 'Nota : le service de conseil peut être commun avec celui de la mesure 6.2.3
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.1.1', 'cae', '6.2.4.1.1', 'Promouvoir activement l''offre d''information et de conseils en partenariat avec les acteurs spécialisés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.1.2', 'cae', '6.2.4.1.2', 'Disposer de structures d''accueil centrales, ouvertes au moins une fois par semaine', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.1.3', 'cae', '6.2.4.1.3', 'Disposer d’un service d''information propre à la collectivité ou participer activement à un service d''information local/départemental/régional neutre pour les questions concernant le climat, l''air et l''énergie dans le bâtiment pour le secteur tertiaire privé (suffisant pour les collectivités inférieures à 10 000 habitants)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.1.4', 'cae', '6.2.4.1.4', 'Simplifier l’accès à ces services d''information (lieu, prix) pour les acteurs tertiaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.2', 'cae', '6.2.4.2', 'Aller au-delà du conseil ponctuel pour la rénovation durable du secteur tertitaire privé', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.2.1', 'cae', '6.2.4.2.1', 'Accompagner concrètement les entreprises du secteur tertiaire (TPE/PME et commerçants notamment) et les artisans pour améliorer la performance climat-air-énergie de leurs locaux en partenariat avec les acteurs spécialisés (chambres consulaires...)', 'Soutien méthodologique ou financier, par exemple : appels à projets pour inciter aux initiatives innovantes ou exemplaires, fonds de soutien aux projets lauréats, opération collective de soutien à l’élaboration de plan d’actions de réduction de gaz à effet de serre, désignation/mise à disposition d’un animateur référent tertiaire, signature de charte d’engagement climat-air-énergie (objectifs qualitatifs et/ou quantitatif sur les consommations d’énergies, les émissions de gaz à effet de serre, le confort d''été et de polluants atmosphériques)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.2.2', 'cae', '6.2.4.2.2', 'Soutenir spécifiquement les entreprises tertiaires > 1000 m² soumises aux obligations du "Décret Tertiaire"', 'Decret Tertiaire: obligation de mise en œuvre d’actions de réduction de la consommation d’énergie finale dans les bâtiments existants à usage tertiaire afin de parvenir à une réduction de la consommation d’énergie finale pour l’ensemble des bâtiments soumis à l’obligation d’au moins 40 % en 2030, 50 % en 2040 et 60 % en 2050 par rapport à 2010. Exemple de soutien: lettre d''information, club d''échanges, visites de site, etc.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.3', 'cae', '6.2.4.3', 'Rationaliser et mutualiser la construction de nouveaux locaux tertiaires', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.3.1', 'cae', '6.2.4.3.1', 'Mettre en relation les disponibilités (recensement de bureaux délaissés/inoccupés) et les besoins en bureaux ou salles de réunion des principales TPE/PME et collectivités, afin d’optimiser l’utilisation des locaux existants.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.3.2', 'cae', '6.2.4.3.2', 'Favoriser l’implantation d’activité de co-working sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.4', 'cae', '6.2.4.4', 'Inciter à la limitation des consommations d''électricité spécifique propres au secteur tertiaire', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.4.1', 'cae', '6.2.4.4.1', 'Aider à la mise en place d’un système de management énergétique et inciter à la certification des entreprises (ISO 50 001, ISO 26 000…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.4.2', 'cae', '6.2.4.4.2', 'Sensibiliser et diffuser les bonnes pratiques en matière d''utilisation sobre des technologiques numériques, de l''éclairage, des équipements de réfrigération dans les commerces...', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.5', 'cae', '6.2.4.5', 'Coopérer avec les autres acteurs du tertiaire public sur le territoire (enseignement, hôpitaux, services administratifs...)', 'Les actions de coopération entre EPCI et communes membres concernant leurs bâtiments publics sont étudiées dans la mesure 6.1.2. Renforcer la coopération au sein de l''intercommunalité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.6', 'cae', '6.2.4.6', 'Mesurer les résultats de l’activité d''accompagnement', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.6.1', 'cae', '6.2.4.6.1', 'Le nombre de consultations émanant des acteurs tertiaires privés est en augmentation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.6.2', 'cae', '6.2.4.6.2', 'Des locaux tertiaires rénovés (BBC rénovation) ou neufs exemplaires (E+C-, certification HQE…), ayant fait l''objet du soutien de la collectivité, sont présents sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.4.6.3', 'cae', '6.2.4.6.3', 'Suivre le respect des obligations de performance du décret tertiaire sur son territoire', 'Par exemple: mise en place de partenariats pour obtenir les données au niveau local, baisse constatée des consommations moyennes par type de bâtiment etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1', 'cae', '6.2.1', 'Coopérer avec les acteurs professionnels du bâtiment et de l''immobilier', 'La collectivité informe et incite les acteurs professionnels du bâtiment (bailleurs sociaux, promoteurs, investisseurs, artisans, bureau d''études, administrateurs de biens, notaires, agents immobiliers...) à intégrer les enjeux climat-air-énergie à leurs activités. Ces actions sont réalisées via  des espaces conseils FAIRE ou des conventions volontaires, contenant des standards de construction et de rénovation élevés, des objectifs d''efficacité énergétique, la sensibilisation des occupants.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.1', 'cae', '6.2.1.1', 'Disposer d''une connaissance fine des acteurs du bâtiment et de l''immobilier privé et les sensibiliser aux enjeux climat-air-énergie', 'Via les centres de ressources BEEP (Bâti Environnement Espace Pro) et les acteurs de la rénovation énergétique qu’elle soutient (espaces conseils FAIRE …), la collectivité doit mettre en place les actions suivantes.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.1.1', 'cae', '6.2.1.1.1', 'Se doter d’une bonne connaissance des acteurs du bâtiment et de l''immobilier privé de son territoire', 'Investisseurs, promoteurs immobiliers, entreprises du bâtiment et artisans, constructeurs de maisons individuelles, bureaux d''études, administrateurs de biens (dont les syndicats de copropriété sur son territoire), notaires, agents immobiliers, ...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.1.2', 'cae', '6.2.1.1.2', 'Informer les acteurs du bâtiment et de l’immobilier sur la politique énergétique et climatique de la collectivité et sur les objectifs à atteindre dans le secteur du bâtiment et de la mobilité pour les différentes cibles (propriétaires occupants ou bailleurs, logements individuels ou collectifs…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.1.3', 'cae', '6.2.1.1.3', 'Informer les acteurs du bâtiment et de l’immobilier sur la vulnérabilité du territoire au changement climatique', 'Vigilance quant au choix des implantations de bâtiments et d''équipements.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.1.4', 'cae', '6.2.1.1.4', 'Informer les acteurs du bâtiment et de l’immobilier des dispositifs mis en place au niveau régional et local ainsi que des formations disponibles, en s’appuyant sur les acteurs de la formation professionnelle de son territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.1.5', 'cae', '6.2.1.1.5', 'Informer les aménageurs sur les dispositifs permettant de développer les usages cyclables', 'Par exemple: niveau de qualité des aménagements, du stationnement, systématisation d’une réflexion intégrant le vélo dans un projet…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.2', 'cae', '6.2.1.2', 'Animer et inciter les acteurs à qualifier leurs pratiques', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.2.1', 'cae', '6.2.1.2.1', 'Favoriser l’animation des acteurs avec mise en place de dispositifs variés (ateliers, visites, rencontres, formation) afin de créer une culture commune sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.2.2', 'cae', '6.2.1.2.2', 'Favoriser la mise en réseau des professionnels du bâtiment et leur visibilité au travers du label RGE', 'Mise en valeur des données du répertoire disponible sur renovation-info-service.gouv.fr
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.2.3', 'cae', '6.2.1.2.3', 'Favoriser les exonérations fiscales (taxe foncière) et bonification des droits à construire pour inciter les acteurs privés à la construction (promoteurs/investisseurs) et à la rénovation durable', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.3', 'cae', '6.2.1.3', 'Les bailleurs sociaux sont engagés aux côtés de la collectivité dans des actions particulièrement ambitieuses', 'Par exemple : création d''un référentiel de performance énergie-climat pour les opérations de logements sociaux, signature d''accords/de convention/d''une charte d''engagement entre la collectivité et les bailleurs, par exemple dans le cadre d''un CLE ou de programmes ANRU, éco-conditionnement des garanties d''empunts accordés aux bailleurs sociaux, soutien à la réalisation de diagnostic de performance énergétique dans les logements ou aux investissements écologiques (soutien technique, conseil, groupement d''achats, montage commun de dossier de certificats d''économie d''énergie...).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.2.1.4', 'cae', '6.2.1.4', 'Disposer d’une stratégie de mobilisation des professionnels du bâtiment et de l’immobilier formalisée', 'La collectivité dispose d’une stratégie de mobilisation des professionnels formalisée, articulée avec les actions des autres échelons de gouvernance (département, région, Etat), et dotée de moyens dédiés (par exemple via une plateforme territoriale de la rénovation, un centre de ressources BEEP qu’elle soutient, etc.)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3', 'cae', '6.3', 'Activités économiques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3', 'cae', '6.3.3', 'Contribuer à l''innovation et l''excellence sur la thématique climat-air-énergie', 'La collectivité contribue à l’innovation et l’excellence sur la thématique climat-air-énergie. Elle peut collaborer avec les centres de formation et le milieu de la recherche ou soutenir un projet phare exemplaire et ambitieux privé, dans ces mêmes domaines, en apportant son expertise et/ou ses conseils pour pousser le projet plus loin dans l’excellence. Elle peut en outre favoriser l’émergence de projets en fédérant les acteurs (organisation de rencontres entre collectivité, entreprises, associations et chercheurs) ou en facilitant le dépôt de projets (recherche des projets, soutien méthodologique).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.1', 'cae', '6.3.3.1', 'Mener une veille pro-active sur les acteurs et projets phares', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.1.1', 'cae', '6.3.3.1.1', 'Recenser les acteurs privés et ceux de la recherche, de l''enseignement supérieur, de la formation professionnelle (GRETA, lycée agricole, CFA, MFR…), qui sont susceptibles de mener des projets sur les thématiques climat-air-énergie (situés sur le territoire ou à proximité de celui-ci pour le milieu rural)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.1.2', 'cae', '6.3.3.1.2', 'Rencontrer des acteurs pour étudier des partenariats sur des projets concrets et/ou des axes de recherches et de formations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.1.3', 'cae', '6.3.3.1.3', 'Recenser les principaux appels à projets, notamment européens, et informer les acteurs du territoire susceptibles d’y répondre ont été informés', 'Par exemple: lettre d’information, journée de rencontres, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.2', 'cae', '6.3.3.2', 'Formaliser l’engagement de la collectivité dans le soutien des projets des acteurs privés', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.2.1', 'cae', '6.3.3.2.1', 'Le partenariat ou l''implication de la collectivité avec les acteurs décrits précédemment a fait l''objet d''une discussion', 'Discussion inscrite dans un compte-rendu, voire d’une délibération si nécessaire au niveau de l''organe délibérant
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.3', 'cae', '6.3.3.3', 'Participer à des études et des recherches pour faire progresser les connaissances et techniques', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.3.1', 'cae', '6.3.3.3.1', 'Mener des études et recherches avec les organismes de formation (stagiaires, apprentis), les établissements d''enseignements supérieurs ou de recherche', 'Par exemple sur: éco-matériaux, efficacité énergétique dans les bâtiments ou les transports, agriculture durable (notamment les liens air et agriculture), surveillance des pollens, qualité de l''air intérieur, gestion des risques dans le cadre de l''adaptation au changement climatique, études des liens entre les conditions locales de circulation et les concentrations de polluants atmosphériques ...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.3.2', 'cae', '6.3.3.3.2', 'Se proposer comme territoire test/pilote pour des projets de recherche/expérimentation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4', 'cae', '6.3.3.4', 'S’investir et se mobiliser concrètement sur des réalisations ambitieuses', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4.1', 'cae', '6.3.3.4.1', 'Les projets soutenus vont au-delà de la réglementation et intègrent des performances environnementales et climatiques chiffrées, dans une approche transversale. Ils sont menés dans une approche participative.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4.2', 'cae', '6.3.3.4.2', 'Organiser des réunions régulières entre la maîtrise d''ouvrage privée, la maîtrise d''œuvre et la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4.3', 'cae', '6.3.3.4.3', 'La collectivité a tiré vers le haut les objectifs fixés pour le projet et a aidé à mettre en place un dispositif d''évaluation des performances du projet', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4.4', 'cae', '6.3.3.4.4', 'Formuler des avis techniques ou organisationnels sur le projet', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4.5', 'cae', '6.3.3.4.5', 'Mobiliser des outils ou du personnel de la collectivité pour suivre le projet et fournir des conseils', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4.6', 'cae', '6.3.3.4.6', 'Communiquer sur le projet', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.4.7', 'cae', '6.3.3.4.7', 'Permettre la formation de partenariats entre les acteurs décrits précédemment (privés, enseignement, recherche, …), en les mettant en relation grâce à son rôle d’animateur, médiateur', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.5', 'cae', '6.3.3.5', 'Etre un territoire d’innovation et d’excellence sur certaines thématiques', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.5.1', 'cae', '6.3.3.5.1', 'Etre membre d’un pôle de compétitivité, pôle d''excellence rural, cluster et/ou etre lauréat d’un appel à projet sur le développement durable, notamment européen (LIFE, H2020, etc...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.5.2', 'cae', '6.3.3.5.2', 'Existance d''une offre de formation locale et diversifiée (thématiques et niveaux de qualification) aux métiers utiles à la transition énergétique et climatique', 'Pour les collectivités supérieures à 100 000 habitants.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.3.5.3', 'cae', '6.3.3.5.3', 'Se positionner clairement contre les grands projets nuisibles en matière d’impacts sur le climat, la qualité de l’air ou la consommation d’énergie ou l’utilisation des terres agricoles sous l’influence de son territoire', 'Par exemple : nouvelles capacités de productions d’énergies fossiles, grands projets de construction sur espaces naturels et agricoles ou à urbaniser (ex : centres commerciaux décentralisés, constructions à faible densité, création d’activités polluantes).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2', 'cae', '6.3.2', 'Développer un  tourisme durable', 'La collectivité élabore une stratégie touristique durable prenant en compte de manière intégrée les impacts du tourisme sur les ressources naturelles et les écosystèmes (la pression sur la ressource locale en eau, …), en matière d''émissions GES (notamment liées au transport) et l''accès aux activités touristiques pour tous. Elle prend des mesures pour réduire ces impacts.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.1', 'cae', '6.3.2.1', 'Identifier les enjeux et les bonnes pratiques en matière de tourisme durable sur le territoire', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.1.1', 'cae', '6.3.2.1.1', 'Identifier les pratiques de tourisme durable dans l''offre existante notamment les établissements labellisés (ex : Ecolabel européen).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.1.2', 'cae', '6.3.2.1.2', 'Identifier les acteurs touristiques clés sur le territoire, les professionnels volontaires et les axes de progression', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.1.3', 'cae', '6.3.2.1.3', 'Connaître les impacts environnementaux des activités touristiques locales et leur vulnérabilité au changement climatique et formaliser les enjeux prioritaires au regard de ce diagnostic, en concertation avec les acteurs concernés', 'En lien avec l’action 1.1.3
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.2', 'cae', '6.3.2.2', 'Mobiliser les acteurs du tourisme', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.2.1', 'cae', '6.3.2.2.1', 'Engager l’Office de Tourisme dans une démarche éco-responsable', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.2.2', 'cae', '6.3.2.2.2', 'Valoriser par l’office de tourisme des acteurs du tourisme engagés dans une démarche de tourisme durable', 'Par exemple: mise en exergue sur le site web, mise en exergue par un macaron sur la vitrine...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.2.3', 'cae', '6.3.2.2.3', 'Sensibiliser aux enjeux climat-air-énergie, l’ensemble des acteurs professionnels et associatifs', 'Les prestataires, les professionnels de l''hôtellerie et de la restauration, les élus et les agents des collectivités…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.2.4', 'cae', '6.3.2.2.4', 'Accueillir de façon responsable, sensibiliser et impliquer les travailleurs saisonniers', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.2.5', 'cae', '6.3.2.2.5', 'Développer un programme de formation à la réduction des émissions de gaz à effet de serre, des consommations énergétiques et la protection de l’environnement pour ces acteurs et favoriser le partage des bonnes pratiques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.2.6', 'cae', '6.3.2.2.6', 'Sensibiliser les touristes aux enjeux du tourisme durable et inciter aux bonnes pratiques (publicité pour des infrastructures touristiques durables, incitation à l''utilisation de transports collectifs, tri des déchets, etc.) via des campagnes de communication, l''organisation d''événements sur les sites touristiques, ...', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.3', 'cae', '6.3.2.3', 'Développer une offre touristique de haute qualité environnementale (hors mobilité)', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.3.1', 'cae', '6.3.2.3.1', 'Développer des activités touristiques au contenu pédagogique permettant l''éducation à l''environnement et aux enjeux climat air énergie, en particulier à destination des jeunes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.3.2', 'cae', '6.3.2.3.2', 'Créer des partenariats visant l’information et la sensibilisation aux enjeux énergie climat sur le territoire ou tout autre particularité environnementale du territoire ainsi que les produits locaux, la promotion de l''artisanat local, etc. entre professionnels de l''hébergement et de la restauration et les autres acteurs du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.3.3', 'cae', '6.3.2.3.3', 'Augmenter le nombre de structures touristiques labellisées (Ecolabel européen notamment), par exemple en soutenant une opération collective d''éco labellisation de professionnels de la restauration et de l''hébergement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.3.4', 'cae', '6.3.2.3.4', 'Soutenir la création d''entreprises œuvrant pour le tourisme durable par le biais d''appels à projets, de valorisation médiatique, de soutiens financiers', 'Par exemple appel à projets Ecolabel européen...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.3.5', 'cae', '6.3.2.3.5', 'Créer des événements touristiques sur le territoire éco-responsables au travers un engagement via une charte et visant la certification ISO 20121:2012 (évènements intégrant le développement durable)', 'Par exemple: festival, manifestation culturelle, manifestation sportive, congrès, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.4', 'cae', '6.3.2.4', 'Mettre en place une offre globale de mobilité durable en lien avec le tourisme', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.4.1', 'cae', '6.3.2.4.1', 'Faciliter l’accès pour se rendre sur le territoire par les transports en commun. Promouvoir l’usage des transports en commun sur tous les supports de communication', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.4.2', 'cae', '6.3.2.4.2', 'Faciliter l’accès à l’intérieur du territoire aux activités/hébergements par les transports en commun. Promouvoir l’usage des transports en commun sur tous les supports de communication', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.4.3', 'cae', '6.3.2.4.3', 'Identifier et remettre en état / développer les circuits de randonnée pédestres et cyclables touristiques sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.4.4', 'cae', '6.3.2.4.4', 'Interdire/contraindre fortement l''accès aux véhicules motorisés sur une ou des parties particulièrement sensibles et fréquentées du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.4.5', 'cae', '6.3.2.4.5', 'Création de circuits touristiques sans voiture, de services de mobilité en particulier pour le “dernier kilomètre” en lien avec les hébergements', 'Par exemple: navettes, partenariats hébergeur/services de location de vélo…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.4.6', 'cae', '6.3.2.4.6', 'Prendre en compte le déplacement des saisonniers dans l’offre de transports et de logements', 'Exemple: navette, co-voiturage, subventionner des logements au coeur des stations pour rendre les loyers accessibles
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.5', 'cae', '6.3.2.5', 'Intégrer et formaliser de manière ambitieuse l’enjeu du tourisme durable dans la vision climat air énergie du territoire', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.5.1', 'cae', '6.3.2.5.1', 'Porter une offre touristique durable et adaptée au changement climatique dans le discours politique et les documents de planification', 'Par exemple: diversification des activités, tourisme 4 saisons, suppression progressive des activités constituant une mal-adaptation, c’est-à-dire un changement opéré dans les systèmes naturels ou humains et qui conduit de manière non intentionnelle à augmenter la vulnérabilité au lieu de la réduire…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.5.2', 'cae', '6.3.2.5.2', 'Adhérer collectivement à des chartes à l’échelle du territoire', 'Exemples: Charte de l’Organisation Mondiale du Tourisme, Charte européenne du tourisme durable, charte des professionnels…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.5.3', 'cae', '6.3.2.5.3', 'Présence d''orientations stratégiques fortes en matière de qualité environnementale des zones d''activités touristiques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.5.4', 'cae', '6.3.2.5.4', 'Créer une taxe locale sur les prestations touristiques et de loisirs pour financer des projets d''adaptation sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.2.5.5', 'cae', '6.3.2.5.5', 'Gérer de façon durable les zones de baignade et/ou les stations de ski et les distinguer par une labellisation', 'Par exemple : flocon vert pour les stations de ski, pavillon bleu pour les plages
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1', 'cae', '6.3.1', 'Favoriser les activités économiques durables', 'La collectivité initie, soutient ou participe à des projets de coopération avec les acteurs de l''économie locale ou régionale sur le climat, l''air et l''énergie, afin d''encourager la mutation des activités économiques vers un modèle bas-carbon. Elle participe notamment au développement des projets d’écologie industrielle et plus généralement d''économie circulaire sur le territoire. Elle contribue à l''amélioration de la qualité environnementale des zones d’activités .
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.1', 'cae', '6.3.1.1', 'Partager la stratégie, les connaissances et les enjeux liés aux entreprises au sein de la collectivité', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.1.1', 'cae', '6.3.1.1.1', 'Travailler en transversalité entre le service/chef de projet développement économique (ou équivalent) et d’autres services, sur des projets communs', 'Par exemple avec le service déchets sur le sujet de l’écologie industrielle et territoriale.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.1.2', 'cae', '6.3.1.1.2', 'Définir précisément le champ d’exercice de la compétence « développement économique »', 'Exemple de champ d''excercice: rôle, moyens, périmètre, priorités…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.1.3', 'cae', '6.3.1.1.3', 'Disposer d’un argumentaire clair pour inciter les entreprises à mettre en place des actions en faveur de la transition énergétique et climatique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.2', 'cae', '6.3.1.2', 'Capitaliser et travailler en partenariat avec les acteurs spécialisés', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.2.1', 'cae', '6.3.1.2.1', 'Identifier les acteurs spécialisés au contact des entreprises, connaître leurs actions et leur présenter la politique climat-air-énergie de la collectivité', 'Chambre de commerces et d’industrie, chambre des métiers et de l’artisanat, pôle emploi, agence d’intérim, fédérations ou associations professionnelles locales…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.2.2', 'cae', '6.3.1.2.2', 'Recenser les bonnes pratiques des entreprises du territoire, les entreprises « locomotives » pouvant servir de relais et d’exemples', 'Pour le volet industriel (bâtiment tertiaire traité dans la mesure 6.2.4), en partenariat avec les acteurs spécialisés.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.2.3', 'cae', '6.3.1.2.3', 'Formations des développeurs d’entreprises sur les enjeux et bonnes pratiques climat-air-énergie en entreprise ; travailler avec eux pour identifier et hiérarchiser les enjeux les plus importants à porter auprès des entreprises, et intégrer ensuite ces thématiques dans les documents d''accueil ou services d''accompagnement proposés par la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.3', 'cae', '6.3.1.3', 'Améliorer la qualité et la mixité des zones d''activités économiques', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.3.1', 'cae', '6.3.1.3.1', 'Mettre en place et qualifier les missions des gestionnaires de zones d''activités pour un aménagement adapté et inclusif (services/commerces de la vie quotidienne de type crèche/boulangerie dans les pôles d''emplois)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.3.2', 'cae', '6.3.1.3.2', 'Décliner des orientations stratégiques fortes en matière de localisation et de qualité environnementale des zones d''activités dans les documents d’urbanisme', 'Accès en TC, limitation de l’imperméabilisation, implantation sur des friches et en reconquête urbaine plutôt qu’en extension urbaine…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.3.3', 'cae', '6.3.1.3.3', 'Faire certifier les zones d’activités (norme ISO 14001 / règlement EMAS)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.4', 'cae', '6.3.1.4', 'Impliquer les entreprises dans des projets d’écologie industrielle structurés et collectifs', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.4.1', 'cae', '6.3.1.4.1', 'Identifier les périmètres pertinents pour développer le concept d’écologie industrielle et inviter les entreprises à identifier les synergies possibles', 'Soutien à des études de faisabilité, poste d''animateur EIT (écologie industrielle et territoriale), analyse des flux et des matières, récupération de chaleur fatale – mesure 3.2.1  …
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.4.2', 'cae', '6.3.1.4.2', 'Elaborer un plan d’action, une charte d’engagement, un cahier des charges ou un règlement d’appel à projets pour formaliser les engagements des entreprises dans un projet d''écologie industrielle/économie circulaire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.4.3', 'cae', '6.3.1.4.3', 'S''impliquer ou soutenir l''utilisation de la démarche territoriale/label "économie circulaire" à l''échelle pertinente', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.4.4', 'cae', '6.3.1.4.4', 'Dans les DOM, Réaliser des actions spécifiques pour la filière "bateau" (recommandations du Grenelle de la Mer sur l''éco-conception et la gestion du recyclage/démantèlement des navires)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.5', 'cae', '6.3.1.5', 'Maintenir une dynamique économique, en adéquation avec les besoins d’une société bas carbone', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.5.1', 'cae', '6.3.1.5.1', 'Renforcer le rôle de relais des commerçants et artisans auprès de leurs clients, pour modifier les habitudes de consommation', 'Orienter vers des produits à plus faible impact environnemental, de fabrication locale, affichage, mise à disposition de document de sensibilisation pour les consommateurs, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.5.2', 'cae', '6.3.1.5.2', 'Soutenir l’artisanat et les commerces de proximité par des exonérations de charge locale (foncière par exemple), par des mesures particulières dans le PLU, par une mise en valeur des produits/savoirs faires locaux ou à faible impact environnemental (offres touristiques, partenariat dans le cadre des comités d’entreprises, création d’un label local etc.), par des opérations FISAC…', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.3.1.5.3', 'cae', '6.3.1.5.3', 'Maintenir ou développer l’attractivité économique de son territoire, en adéquation avec les besoins d’une société bas carbone', 'Par exemple : éco-filières, low tech, économie sociale et solidaire, économie de la fonctionnalité...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4', 'cae', '6.4', 'Agriculture, forêt', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2', 'cae', '6.4.2', 'Soutenir l''utilisation durable des forêts et des espaces boisés', 'La collectivité prend en compte la forêt et les espaces boisés dans ses documents d’urbanisme, dans un objectif d’identification, de préservation, d’utilisation rationnelle ou de développement. Elle incite aux pratiques sylvicoles durables et au développement des pratiques de gestion raisonnée des forêts et espaces boisés, en interne et auprès des particuliers et des professionnels. Elle contribue à structurer et développer des filières bois-énergie et bois-construction.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.1', 'cae', '6.4.2.1', 'Sensibiliser élus, services et acteurs du territoire aux enjeux liés à la forêt et aux espaces boisés', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.1.1', 'cae', '6.4.2.1.1', 'Sensibiliser les élus et services de la collectivité et les particuliers à la gestion durable de la forêt (sensibilisation notamment aux leviers forestiers d’atténuation du changement climatique : séquestration carbone en forêt et production durable de bois pour les usages bois matériau et bois énergie) et à l’utilisation du bois énergie sur le territoire, de façon complémentaire aux usages matériaux', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.1.2', 'cae', '6.4.2.1.2', 'Sensibiliser les élus à l’utilisation du bois matériau, notamment à l’utilisation du bois dans la construction', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.1.3', 'cae', '6.4.2.1.3', 'Sensibilises élus à l’utilisation optimale du bois', 'Par exemple: complémentarité des filières, principe d’utilisation de bois en cascade, utilisation du bois local filières courtes (ex : marques type bois des Alpes, bois des Territoires du Massif Central, ...)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.1.4', 'cae', '6.4.2.1.4', 'Sensibiliser les élus à la lutte contre la déforestation importée', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.2', 'cae', '6.4.2.2', 'Lutter contre la déforestation importée', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.2.1', 'cae', '6.4.2.2.1', 'Etre attentif à la provenance du bois pour ses fournitures (par exemple son mobilier), il doit provenir de forêt locales et gérées durablement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.2.2', 'cae', '6.4.2.2.2', 'Dresser une liste des produits alimentaires dont la production est cause de déforestation (par exemple : soja, huile de palme, bœuf et se coproduits, cacao, hévéa). Contrôler leur provenance et les méthodes de production lorsqu’ils sont utilisés pour la restauration publique', 'S’ils ne proviennent pas de cultures durables ils sont limités voire interdits.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.3', 'cae', '6.4.2.3', 'Réaliser des diagnostics, connaître les enjeux liés à la forêt et aux espaces boisés sur son territoire', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.3.1', 'cae', '6.4.2.3.1', 'Réaliser un diagnostic pour identifier les attentes, les besoins, les pratiques et la nature des activités forestières, y compris pour leur adaptation au changement climatique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.3.2', 'cae', '6.4.2.3.2', 'Réaliser un diagnostic de la forêt incluant les surfaces par typologie des forêts, les stocks de carbone et la séquestration de carbone par la forêt, le taux des prélèvements, la répartition de la récolte entre les différents usages du bois, de ses usages et de ses acteurs sur le territoire.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.3.3', 'cae', '6.4.2.3.3', 'Etudier les différents potentiels de la forêt et des espaces boisés dans le cadre d’une démarche territoriale concertée (ex : élaboration d’une charte concertée de territoire)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.4', 'cae', '6.4.2.4', 'Formaliser sa stratégie dans des documents de planification dédiés à la forêt et dans les documents d’urbanisme', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.4.1', 'cae', '6.4.2.4.1', 'La collectivité met en œuvre avec les acteurs du territoire un plan de préservation et de valorisation de la forêt', 'Doit porter sur:
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.4.2', 'cae', '6.4.2.4.2', 'Prendre en compte les zones boisées dans les documents d’urbanisme (zonage approprié, espaces boisés classés, éléments remarquables à protéger et repérés dans les documents graphiques)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.4.3', 'cae', '6.4.2.4.3', 'Articuler sa stratégie avec les politiques locales de développement (Chartes de Pays, etc.) et les autres dispositifs de gestion forestière (schémas régionaux de gestion forestière, plan de développement de massif) en co-construction avec les acteurs locaux concernés (ex : les Chartes forestières)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.4.4', 'cae', '6.4.2.4.4', 'Pérenniser un pilotage multi-partenarial de la politique de préservation et de valorisation de la forêt', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.5', 'cae', '6.4.2.5', 'Mettre en place les actions définies dans le plan de préservation et valorisation de la forêt permettant une mobilisation durable de bois et un renforcement durable des puits de carbone dans les forêts (biomasse et sols)', '(Si peu de ressource bois expoitable sur le territoire, les 30% concernant le développement des filières bois-contruction et bois-énergie sont à reporter vers les actions de préservation et lutte contre la déforestation importée)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.5.1', 'cae', '6.4.2.5.1', 'Protéger les forêts de l’artificialisation des sols et favorisef la restauration des forêts impactées par des évènements extrêmes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.5.2', 'cae', '6.4.2.5.2', 'Favoriser le regroupement forestier', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.5.3', 'cae', '6.4.2.5.3', 'Mettre en place des actions pour favoriser l’élaboration des documents de gestion ciblant une production de bois de qualité et encourageant des pratiques sylvicoles comme la conversion des taillis en futaies ; l’amélioration sylvicole des accrus forestiers et en raisonnant les pratiques pouvant avoir des impacts négatifs sur les stocks de carbone du sol', 'Par exemple : coupes rases associées à un travail du sol, récolte accrue des rémanents, et tout particulièrement les souches...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.5.4', 'cae', '6.4.2.5.4', 'Mettre en place des mesures pour favoriser la séquestration de carbone dans les forêts et les espaces boisés (ex : îlots de sénescence, arbres morts...).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.5.5', 'cae', '6.4.2.5.5', 'Mettre en place des actions favorisant l’utilisation optimale du bois (tri du bois, chantiers groupés).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.6', 'cae', '6.4.2.6', 'Prendre en charge, promouvoir et soutenir le développement d’une filière bois-construction locale et à haute performance environnementale', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.6.1', 'cae', '6.4.2.6.1', 'Soutenir la structuration et l’optimisation de la filière bois', 'Par exemple: création de plateforme de séchage et de stockage destinées en priorité à un usage local, espaces/groupes de discussion entre acteurs, création d''association, de SEM ou de SCIC, label pour le bois local...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.6.2', 'cae', '6.4.2.6.2', 'Engager une opération de construction / rénovation bois en adaptant les spécifications techniques aux caractéristiques du bois local', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.6.3', 'cae', '6.4.2.6.3', 'Accompagner les acteurs de la filière bois du territoire pour pénétrer le marché de la construction bois sur le territoire et au-delà', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.6.4', 'cae', '6.4.2.6.4', 'Porter des actions d’augmentation de la demande, notamment des actions de communication (et de formation) vers les constructeurs de bâtiment, les architectes et maîtres d’œuvre, les particuliers qui font construire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.7', 'cae', '6.4.2.7', 'Prendre en charge, promouvoir et soutenir le développement d’une filière bois-énergie à haute performance environnementale', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.7.1', 'cae', '6.4.2.7.1', 'Accompagner le développement d’une offre et d’une demande collective en bois-énergie sur le territoire adapté aux ressources disponibles dans le territoire (réflexion globale et complémentaire avec la filière bois-construction, utilisation de la ressource bocagère…) ; puis développer et sécuriser la filière d’approvisionnement bois-énergie locale en s’appuyant sur la complémentarité des filières et le principe d’utilisation de bois en cascade.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.7.2', 'cae', '6.4.2.7.2', 'Participer à une filière bois énergie sur un bassin plus vaste (selon la ressource en bois disponible sur son territoire)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.8', 'cae', '6.4.2.8', 'Suivre et atteindre ses objectifs en matière de forêt et d’espaces boisés', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.8.1', 'cae', '6.4.2.8.1', 'Connaître le % de surfaces forestières certifiées', 'Objectif >70% pour les forêts communales et >20% pour les forêts privées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.2.8.2', 'cae', '6.4.2.8.2', 'Suivre la séquestration carbone de la forêt et d’autres indicateurs', 'Par exemple taux de récolte commercialisé...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1', 'cae', '6.4.1', 'Soutenir une agriculture et une alimentation plus durable', 'Au travers de ses compétences, la collectivité soutient, promeut et encourage des pratiques agricoles et alimentaires durables sur son territoire et sur ses territoires d’approvisionnement :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.1', 'cae', '6.4.1.1', 'Réaliser un diagnostic agricole et alimentaire de son territoire', 'Ce diagnostic agricole et alimentaire peut s’inscrire dans le cadre d’un projet alimentaire territorial.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.2', 'cae', '6.4.1.2', 'Animer son territoire sur le sujet de l’agriculture et de l’alimentation durable', 'Cette animation peut prendre la forme de l’initiation d’un projet alimentaire territorial.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.2.1', 'cae', '6.4.1.2.1', 'Initier une concertation entre les acteurs (réunions professionnelles, réunions publiques…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.2.2', 'cae', '6.4.1.2.2', 'Mener des actions de sensibilisation (réunions publiques, films, intervenants extérieurs, théâtre, expos, défis Famille à alimentation positive…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.2.3', 'cae', '6.4.1.2.3', 'Mettre en relation les acteurs', 'Par exemple: producteurs-consommateurs-restaurateurs, commissions locales à l’installation – café installation-, limitation du gaspillage alimentaire, facilitation et accompagnement d’associations d’aide alimentaire, don, acteurs de l’alimentation, de la santé et du social autour des questions d’accès de tous à une alimentation saine et durable …
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.3', 'cae', '6.4.1.3', 'Améliorer la performance environnementale de la production agricole', 'Les améliorations visent les champs suivants :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.3.1', 'cae', '6.4.1.3.1', 'Favoriser le développement de pratiques agroécologiques, l’installation de producteurs bios/ avec des pratiques agro-écologiques, favoriser les conversions en bio, soutenir les groupements locaux d’agriculture biologique (GAB)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.3.2', 'cae', '6.4.1.3.2', 'Encourager les filières locales à rentrer dans des logiques de performance environnementale et d’écoconception : intégration d’exigences et suivi d’indicateurs environnementaux dans les cahiers des charges, promotion des démarches HVE, formation à l’agro-écologie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.3.3', 'cae', '6.4.1.3.3', 'Travailler avec les AOC, IGP, marques régionales etc. pour qualifier les productions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4', 'cae', '6.4.1.4', 'Reterritorialiser l’alimentation de la collectivité et contribuer à sa résilience alimentaire', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.1', 'cae', '6.4.1.4.1', 'Accompagner la diversification des productions agricoles locales pour répondre aux besoins du territoire (légumes secs, fruits et légumes frais de saison, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.2', 'cae', '6.4.1.4.2', 'Intégrer les questions d’approvisionnement alimentaire (durable) dans les réflexions urbanistiques : types de commerces, marchés de détails et marché de gros (MIN), accès aux modes doux…', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.3', 'cae', '6.4.1.4.3', 'Favoriser l’accès à la terre et les regroupements de producteurs locaux pour optimiser la logistique du dernier kilomètre et l’installation de boutiques paysans', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.4', 'cae', '6.4.1.4.4', 'Valoriser les produits locaux / filières locales de qualité (création de marché de producteurs locaux, guide, cartographie…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.5', 'cae', '6.4.1.4.5', 'En milieu urbain, accompagner l’installation de fermes urbaines durables ; de jardins partagés ou pédagogiques, des ruches (dans le respect de la biodiversité locale de pollinisateurs), les plantations comestibles en milieu urbain, encourager la multiplication des potagers privés (sensibiliser aux pratiques agro-écologiques, enjeux environnementaux et sanitaires)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.6', 'cae', '6.4.1.4.6', 'Favoriser / soutenir les initiatives d’accès à tous à une alimentation saine et durable. Faire se rencontrer les acteurs de l’alimentation, de la santé et du social', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.7', 'cae', '6.4.1.4.7', 'Mettre en place ou soutenir les actions de sensibilisation du grand public à l’alimentation durable', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.4.8', 'cae', '6.4.1.4.8', 'Préserver les terres agricoles via les documents d’urbanisme', 'Evalué dans la mesure 1.3.1.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5', 'cae', '6.4.1.5', 'Modifier les habitudes alimentaires, notamment dans la restauration collective, en respectant les obligations de la loi EGalim et en s’engageant plus loin', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.1', 'cae', '6.4.1.5.1', 'Revoir les approvisionnements en restauration collective publique', 'A minima 50% de produits durables et de qualité, dont au moins 20% issus de l’agriculture biologique (critères Egalim) ; privilégier les produits locaux ou ancrés dans les territoires (PAT); servir uniquement des fruits et légumes frais de saison, favoriser les produits issus du commerce équitable.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.2', 'cae', '6.4.1.5.2', 'Informer les convives sur l’origine, la saisonnalité et la qualité des produits servis (Egalim et autres critères de qualité / durabilité); mettre en place un affichage environnemental et nutritionnel des menus', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.3', 'cae', '6.4.1.5.3', 'Diversifier les apports alimentaires, en cohérence avec les nouvelles recommandations de Santé Publique France', 'Notamment : mettre en place un plan de diversification protéique, introduisant notamment les légumineuses (obligatoire à partir de 200 couverts/j, Egalim) ; proposer au moins un menu végétarien hebdomadaire (obligatoire en restauration scolaire, Egalim) et/ou une alternative végétarienne (choix multiples).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.4', 'cae', '6.4.1.5.4', 'Lutter contre le gaspillage alimentaire (ajuster les quantités aux besoins, sensibiliser les convives, organiser des pesées annuelles sur une semaine, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.5', 'cae', '6.4.1.5.5', 'Lors des achats de matériels/rénovations importantes, réfléchir sur la globalité du repas (besoin en légumerie, cuisson basse température, contenants de différentes tailles pour les petites/grandes faims, salade bar, …)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.6', 'cae', '6.4.1.5.6', 'Former les cuisinier·es et le personnel de cantine à l''introduction de produits bio locaux et à la diversification des protéines, et plus globalement aux enjeux environnementaux, et leur fournir un corpus de recettes adaptées à la restauration collective. Les former également aux démarches de réduction du gaspillage alimentaire.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.7', 'cae', '6.4.1.5.7', 'Former les acheteur·ses et les gestionnaires impliqué·es dans la restauration collective aux questions environnementales, sociales, sanitaires de l’alimentation, aux achats bios/locaux et aux démarches de réduction du gaspillage alimentaire.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.5.8', 'cae', '6.4.1.5.8', 'En restauration scolaire en particulier, organiser des ateliers sur la provenance des aliments consommés par les enfants (dont les protéines animales), sur l''impact environnemental de leur assiette, pour les produits locaux ou d’importation faisant partie de nos habitudes alimentaires, organiser des visites de fermes d’où proviennent les aliments cuisinés ou des interventions des producteurs. Organiser avec les enfants des pesées d’assiette, et le tri sélectif des déchets, pour agir sur le gaspillage alimentaire. Co-construire les menus avec eux·elles (et le·a diététicien·ne de la ville)', 'En lien avec la mesure 6.5.4
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.6', 'cae', '6.4.1.6', 'Participer concrètement à des installations et au maintien des pratiques agricoles durables', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.6.1', 'cae', '6.4.1.6.1', 'Créer une régie agricole et/ou utiliser de baux environnementaux', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.6.2', 'cae', '6.4.1.6.2', 'Acquérir, mettre en réserve (convention de portage foncier EPF ou SAFER, stockage en direct par la collectivité) ou mettre à disposition du foncier (terres ou bâtiment, fermes communales ou intercommunales, fermes relais, régies communales) en partenariats avec des associations (exemple Foncière Terre de Liens)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.6.3', 'cae', '6.4.1.6.3', 'Installer des porteurs de projets exemplaires et créer des espaces-test', 'Exemple d''espaces test: mise à disposition de terres, portage ou financement de bâtiments, aide à démarrage et au fonctionnement de la structure d''accueil, portage politique et technique du projet...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.6.4', 'cae', '6.4.1.6.4', 'dans les DOM, les mesures spécifiques DOM plan Ecophyto sont déclinées/soutenues localement', 'Par exemple : plateforme d’expérimentation « systèmes de culture innovants » adaptées au climat tropical, recherche sur les méthodes de lutte biologique…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.7', 'cae', '6.4.1.7', 'Adopter une vision globale grâce à la formalisation d’un Projet Alimentaire Territorial (PAT)', 'Formaliser son PAT (par exemple via la reconnaissance au niveau national par le Ministère de l’agriculture et de l’alimentation) avec des critères écologiques et sociaux, compatible avec le PCAET, prenant en compte la résilience alimentaire et appuyant la structuration de filières paysannes, biologiques et locales, rémunératrices pour les agriculteurs.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.8', 'cae', '6.4.1.8', 'Mesurer et atteindre des objectifs ambitieux en matière d’agriculture et d’alimentation durable', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.8.1', 'cae', '6.4.1.8.1', 'Mettre en place et suivre des indicateurs en matière d''intrants (fréquence, quantité, produits...) selon les cultures, soutien et suivi technique des rendements/coûts/impacts environnementaux d’espaces test agricoles…', 'En lien avec les chambres d''agriculture, les coopératives agricoles, les ONVAR (CIVAM, GAB, CUMA, etc.) et les agriculteurs
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.8.2', 'cae', '6.4.1.8.2', 'En restauration collective, sur le gaspillage alimentaire, atteindre l’objectif de 50% de réduction par rapport à la référence nationale du secteur', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.8.3', 'cae', '6.4.1.8.3', 'S’engager à au moins 75% de l’alimentation en restauration collective qui soit de qualité ou durable, dont au moins 40% issus de l’agriculture biologique et 50% issue de produits locaux ; poursuivre la diversification alimentaire.', 'Dans les DOM, suivi uniquement de l’évolution.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.8.4', 'cae', '6.4.1.8.4', 'mettre en place un programme pour l’accès à tous à une alimentation saine et durable, incluant : sensibilisation et accompagnement de la population, soutien à des structures ESS type épiceries solidaires, AMAP, etc.), programme de type chèque alimentaire ou Sécurité sociale alimentaire locale', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.4.1.8.5', 'cae', '6.4.1.8.5', 'Avoir un pourcentage élevé de Surface Agricole Utile en agricultue biologique.', 'En métropole, objectif 20%, valorisé progressivement à partir de 6% (dans les DOM, suivi uniquement de l’évolution).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5', 'cae', '6.5', 'Société civile', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3', 'cae', '6.5.3', 'Développer des actions d''éducation et de sensibilisation dans les établissements scolaires et centres de petite enfance', 'La collectivité développe des actions d’éducation et de sensibilisation à la problématique climat-air-énergie dans les établissements scolaires, crèches et garderies. Ces actions sont variées (forme et contenu), et impliquent les élèves, le corps enseignant et le personnel. Autant que possible, elles s’intègrent dans un projet pédagogique global et sont planifiées sur l’année voire sur l’ensemble du cycle. Certaines actions visent directement la réduction des consommations d’énergie, d’eau et les émissions de polluants liées à l’utilisation des locaux et aux déplacements scolaires.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.1', 'cae', '6.5.3.1', 'Rencontrer les acteurs locaux de l’éducation et la petite enfance et s’organiser pour mener des actions communes', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.1.1', 'cae', '6.5.3.1.1', 'Définir le budget et les ressources humaines prévus pour les actions de sensibilisation climat-air-énergie dans les écoles et structures d’accueil de jeunes enfants', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.1.2', 'cae', '6.5.3.1.2', 'Présenter les enjeux et la politique climat-air-énergie de la collectivité aux enseignants et professionnels de la petite enfance', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.1.3', 'cae', '6.5.3.1.3', 'Identifier les bonnes pratiques, ressources et projets déjà mis en place dans les structures et les besoins d’accompagnement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.1.4', 'cae', '6.5.3.1.4', 'Faire travailler les acteurs de l’éducation et de la petite enfance en réseau sur ces sujets (groupe de travail, formation collective…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2', 'cae', '6.5.3.2', 'Sensibiliser les enfants aux enjeux climat-air-énergie et à l’éco-citoyenneté', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.1', 'cae', '6.5.3.2.1', 'Participer et soutenir la journée ou semaine-évènement sur le climat, l’air ou l’énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.2', 'cae', '6.5.3.2.2', 'Participer et soutenir le défi inter-écoles ou inter-classes sur le thème de l’énergie, la mobilité, le tri des déchets…', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.3', 'cae', '6.5.3.2.3', 'Participer et soutenir la visite d’une vélo-station, d’un centre de tri des déchets, d’un jardin partagé…', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.4', 'cae', '6.5.3.2.4', 'Soutenir l''implication des élèves et des enseignants dans le diagnostic, le suivi et les mesures prises pour améliorer la qualité de l''air intérieur', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.5', 'cae', '6.5.3.2.5', 'Soutenir l''implication des élèves dans les projets climat-air-énergie de la collectivité (trouver le nom ou dessiner le logo d’un projet ENR citoyen ou des panneaux de sensibilisation…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.6', 'cae', '6.5.3.2.6', 'Participer à la réalisation d’une vidéo participative, réalisation d’une exposition en lien avec l’énergie, l’air ou le climat', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.7', 'cae', '6.5.3.2.7', 'Soutenir les appels à projets en direction des établissements pour inciter à la mise en place d’actions pédagogiques climat-air-énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.2.8', 'cae', '6.5.3.2.8', 'Soutenir la labellisation « éco-école »', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.3', 'cae', '6.5.3.3', 'Mettre en place des lignes de bus pédestre ou cycliste dans le cadre d’un Plan de déplacement établissement scolaire (PDES)', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.3.1', 'cae', '6.5.3.3.1', 'Identifier et localiser des parents et élèves volontaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.3.2', 'cae', '6.5.3.3.2', 'Organiser une journée test (choix d’une date festive pour faire participer le maximum d’élèves)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.3.3', 'cae', '6.5.3.3.3', 'Matérialiser des arrêts et itinéraires des pédibus/vélobus', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.3.4', 'cae', '6.5.3.3.4', 'Soutenir une offre d’équipements à toutes les écoles participantes (mobilier urbain, gilets réfléchissants, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.3.5', 'cae', '6.5.3.3.5', 'Participer à l''analyse et à la valorisation des retours d’expériences', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.4', 'cae', '6.5.3.4', 'Pérenniser et massifier la sensibilisation', '- large public visé (toutes les classes d''âges, corps enseignant, personnel, parents d’élèves)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.5', 'cae', '6.5.3.5', 'Suivre les résultats et atteindre les objectifs fixés', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.5.1', 'cae', '6.5.3.5.1', 'Avoir un % élevé (> 50%) d’élèves concernés par une démarche de sensibilisation/action', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.5.2', 'cae', '6.5.3.5.2', 'Avoir une part d’établissements scolaires couverts par un PDES ou un pédibus/vélobus en augmentation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.5.3', 'cae', '6.5.3.5.3', 'Contrôler l’efficacité des actions mises en place, par exemple : baisse constatée des consommations d’économies d''énergie ou d’eau réalisées, baisse de la quantité de déchets résiduels…', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.3.5.4', 'cae', '6.5.3.5.4', 'Avoir une reconnaissance via un dispositif de labellisation ou des retombées presse (éco-école, écolo crèche, article de presse…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2', 'cae', '6.5.2', 'Inciter les citoyens et les relais d''opinions à une consommation responsable, limiter l''emprise de la publicité', 'La collectivité incite les citoyens aux comportements et à la consommation éco-responsables et soutient, sensibilise les relais d''opinion (partis politiques, associations, ONG) pour qu''ils deviennent des modèles d''exemplarité et exercent une influence sur les habitants pour qu''ils agissent en conformité avec la politique climat-air-énergie de la collectivité. Des actions sont en particulier menées pour limiter l’emprise de la publicité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.1', 'cae', '6.5.2.1', 'S''organiser en interne et localement pour sensibiliser les habitants', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.1.1', 'cae', '6.5.2.1.1', 'une personne au sein de la collectivité est spécifiquement chargée des actions de sensibilisation auprès des habitants et est formée aux questions de changement de comportement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.1.2', 'cae', '6.5.2.1.2', 'Définir un budget pour les actions de promotion des comportements éco-responsables des citoyens (dont les achats, l''usage et la fin de vie de produits plus respectueux de l''environnement comme ceux porteurs de l''Ecolabel Européen)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.1.3', 'cae', '6.5.2.1.3', 'Dédier un lieu à la sensibilisation climat-air-énergie (il est visible et accessible)', 'Plus large que le volet "rénovation" étudié dans la mesure 6.2.3 et 6.2.4
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.2', 'cae', '6.5.2.2', 'Informer et mobiliser les relais d’opinions (partis politiques, associations, ONG)', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.2.1', 'cae', '6.5.2.2.1', 'Les relais d''opinion connaissent les objectifs et les axes de la politique climat-air-énergie de la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.2.2', 'cae', '6.5.2.2.2', 'Convier les relais d''opinions aux groupes de travail', 'Ils sont identifiés comme soutiens voire porteurs potentiels de certaines actions de la collectivité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.2.3', 'cae', '6.5.2.2.3', 'Le nombre d''associations environnementales est important, elles font des propositions constructives, régulières et sont soutenues par la collectivité (convention de partenariat, mise en place de forum des associations, etc.). En fonction du tissu associatif local, les relais d’opinions sont variés d’un territoire à l’autre.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.3', 'cae', '6.5.2.3', 'Sensibiliser les habitants sur l’éco-consommation et les éco-gestes', '- promotion de la consommation modérée de ressources énergétiques et matérielles
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.4', 'cae', '6.5.2.4', 'Montrer l’exemple lors des évènements', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.4.1', 'cae', '6.5.2.4.1', 'A l''occasion de manifestations ou à l''occasion de récompenses à des jeux ou objets "promotionnels", des objets incitant aux comportements éco-responsables sont privilégiés', 'Par exemple: réducteurs de pression, lampes basses consommations, gilet fluo pour faire du vélo, produits (biens ou services) porteurs de l''Ecolabel Européen...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.4.2', 'cae', '6.5.2.4.2', 'Mettre en place des “ponts” entre les différents postes de consommation', 'Lors des manifestations pour une alimentation biologique et locale la collectivité promeut (ou invite les organisateurs à promouvoir) aussi les initiatives sur les économies d’énergie, etc. afin de favoriser une approche globale de la consommation responsable.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.4.3', 'cae', '6.5.2.4.3', 'Réaliser de manière éco-responsable la tenue des évènements des relais d''opinions', 'Par exemple: élaboration d''un guide par la collectivité, autorisation ou caution liées aux éco-gestes, relevé des consommations d''énergie et d''eau avant et après l''utilisation des locaux par les associations...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.5', 'cae', '6.5.2.5', 'Limiter l’emprise de la publicité', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.5.1', 'cae', '6.5.2.5.1', 'Sensibiliser les habitants à travers des actions rapides et efficaces (par exemple distribution généralisée d’autocollants « Stop Pub » avec le bulletin municipal)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.5.2', 'cae', '6.5.2.5.2', 'Faire respecter les obligations concernant les enseignes lumineuses et l''affichage par exemple en s’appuyant sur les associations locales pour vous accompagner dans le travail de recensement et de sensibilisation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.5.3', 'cae', '6.5.2.5.3', 'Mettre en place une taxe locale sur les enseignes et publicités extérieures (TPLE)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.5.4', 'cae', '6.5.2.5.4', 'Devenir une ville sans publicité en instaurant une Zone de Publicité Restreinte', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.5.5', 'cae', '6.5.2.5.5', 'Mettre en place un règlement local de publicité (RLP)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.6', 'cae', '6.5.2.6', 'Soutenir l’éco-consommation locale, notamment via un système d''échange local ou une monnaie locale', 'Les circuits de proximité alimentaires sont traités plus en détail dans la mesure 6.4.1
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.6.1', 'cae', '6.5.2.6.1', 'Initier la création d’une monnaie locale ou soutenir les associations à l’initiative de ces projets (Charte d’engagement environnemental adossé à la monnaie locale par exemple)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.6.2', 'cae', '6.5.2.6.2', 'Informer les habitants de l’intérêt d’une monnaie locale', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.2.6.3', 'cae', '6.5.2.6.3', 'Développer des circuits de proximité et favoriser les initiatives de consommation alternative (mise à disposition de lieux par exemple)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1', 'cae', '6.5.1', 'Mobiliser la société civile en développant la concertation', 'La collectivité mobilise les citoyens dans le processus de décision et organise, en collaboration avec le service public concerné, des groupes de travail et des dispositifs participatifs variés relatifs aux projets climat-air-énergie. La concertation peut être aussi bien menée en phase de diagnostic que lors de la construction du programme d’actions ou de sa mise œuvre.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.1', 'cae', '6.5.1.1', 'Définir une organisation et identifier des axes de travail', '', '', '', '', null, 35.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.1.1', 'cae', '6.5.1.1.1', 'Entretenir la culture de la collaboration sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.1.2', 'cae', '6.5.1.1.2', 'Connaître les différents niveaux d’implication des citoyens : l’information, la consultation, la concertation, la co-production, la codécision, l’auto-gestion', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.1.3', 'cae', '6.5.1.1.3', 'Formaliser les « règles du jeu » de la concertation : les objectifs et le pouvoir d’influence des instances sont définis clairement, les droits et les devoirs de chacun (collectivité / citoyens) sont annoncés, par exemple dans une charte', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.1.4', 'cae', '6.5.1.1.4', 'Définir un/des groupes de travail sur des thèmes variés : climat, air, énergie, déchets, eau, assainissement, mobilité…', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.1.5', 'cae', '6.5.1.1.5', 'Institutionnaliser la collaboration entre les habitants et la collectivité sur les thèmes relevant de l''énergie et du climat en définissant un organe participatif et/de concertation ou en se basant sur les groupes de travail déjà existants (qualifier les Conseils de quartier, Conseils de développement, …)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2', 'cae', '6.5.1.2', 'Conduire une concertation régulière et pérenne', '', '', '', '', null, 45.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.1', 'cae', '6.5.1.2.1', 'Animer et mobiliser régulièrement (au moins trois fois par an) les habitants lors d’ateliers thématiques ou autour de projets particuliers (plan vélo, rénovation d’une école, etc.).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.2', 'cae', '6.5.1.2.2', 'S''organiser pour faire participer des publics variés en mobilisant des canaux de communication et des formats de concertation variés et adaptés au différents publics', 'Par exemple: ateliers avec les centres sociaux, avec les Maisons des Jeunes et de la Culture (MJC), mise en place de garderies pour favoriser la participation des jeunes actifs, ...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.3', 'cae', '6.5.1.2.3', 'Adapter les processus de concertation des habitants au travail collaboratif et à l’expression de la parole de chacun.', 'Un travail est conduit pour favoriser l’appropriation des problématiques air-énergie-climat afin que chacun se sente en capacité de s’exprimer (pédagogie des intervenants, production de documents synthétiques et pédagogiques...). Les modes d’animation mises en place favorisent l’écoute des participants.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.4', 'cae', '6.5.1.2.4', 'Mettre en place des formats participatifs favorisant les initiatives avec un budget et des moyens dédiés : budget participatifs, appels à projets, etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.5', 'cae', '6.5.1.2.5', 'Mobiliser les instances de concertation régulières (conseil de quartier, conseil de développement, ...) pour participer à la construction du PCAET et les tenir informés au moins une fois par an de l’avancée du plan d’actions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.6', 'cae', '6.5.1.2.6', 'Impliquer les citoyens sur des questions concrètes qui les concernent directement (à l’échelle du bâtiment, du quartier par exemple), mais également sur des éléments plus stratégiques (contribution à la « vision » globale du territoire dans une approche de transition énergétique)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.7', 'cae', '6.5.1.2.7', 'Allouer des délais et des moyens suffisants aux mécanismes participatifs pour une implication de qualité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.2.8', 'cae', '6.5.1.2.8', 'Prévoir si nécessaire une expertise externe afin d’animer les séances, de produire des comptes rendus rapidement, de disposer de lieux conviviaux et adaptés, de matériels informatiques et audiovisuels de qualité.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.3', 'cae', '6.5.1.3', 'Utiliser les contributions des instances de concertation', 'Evalué dans la 5.1.4 : Une instance de concertation dédiée à la politique air-énergie-climat ou développement durable est chargée du suivi-évaluation de la politique.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.3.1', 'cae', '6.5.1.3.1', 'Le travail des habitants est reconnu et rendu public (plaquette de synthèse, article de presse...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.3.2', 'cae', '6.5.1.3.2', 'Faire systématiquement un retour aux instances participatives pour rendre compte des arbitrages faits au regard des contributions et présenter l’avancée des projets.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.3.3', 'cae', '6.5.1.3.3', 'Evaluer de façon régulière les dispositifs de concertation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_6.5.1.3.4', 'cae', '6.5.1.3.4', 'Voter au moins une décision par an par l''organe délibérant de la collectivité issue du travail des groupes de travail', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3', 'cae', '3', 'Approvisionnement énergie, eau, assainissement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1', 'cae', '3.1', 'Organisation de la distribution d''énergie et services associés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2', 'cae', '3.1.2', 'Inciter à la réduction des consommations et à l’achat d’électricité verte avec les fournisseurs et syndicats d’énergie', 'La collectivité œuvre, en partenariat avec les fournisseurs d’énergie actifs sur le territoire et le syndicat d’énergie, à inciter les usagers à baisser leur consommation et à augmenter la part d’électricité renouvelable achetée. Les clients et adhérents sont sensibilisés et des prestations de services dans ce domaine sont proposées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.1', 'cae', '3.1.2.1', 'Inciter à l’achat d’électricité renouvelable', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.1.1', 'cae', '3.1.2.1.1', 'S''engager à mener une politique active de promotion des achats d''électricité verte auprès de ses habitants et acteurs socio-économiques pour augmenter la demande', 'Doit se faire à côté d''un engagement en faveur de la production d''électricité verte ou de l’auto-consommation sur le territoire.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.1.2', 'cae', '3.1.2.1.2', 'Faire la promotion et informer les habitants et les entreprises de la possibilité d''acheter de l''électricité verte et sur les critères de qualité à respecter', 'Exemples de critères de qualité: existence de labels au niveau européen, 100% ENR, qualité environnementale, transparence, critère d''additionalité, fond de développement des ENR...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.1.3', 'cae', '3.1.2.1.3', 'Encourager et soutenir les démarches de groupement d''achats d''électricité verte des acteurs publics et privés (réunion d''information/formation/cahier des charges...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.2', 'cae', '3.1.2.2', 'Utiliser la facturation de l’énergie pour encourager les comportements économes (selon marge d’actions)', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.2.1', 'cae', '3.1.2.2.1', 'Faire apparaitre de manière évidente dans les objectifs politiques des fournisseurs locaux (factures) des principes de tarification des agents énergétiques favorisant un comportement économe en énergie', 'Faire apparaître ces principes ou a minima la limitation des pointes de consommation: prix différenciés selon les saisons (été/hiver), différenciation heure pleine/heure creuse, pas de réduction liée à la quantité, modèles tarifaires novateurs (par ex. tarif d''efficacité)…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.2.2', 'cae', '3.1.2.2.2', 'Indiquer une valeur de référence (moyenne nationale ou régionale d''un ménage-type), l''évolution des consommations par rapport aux années précédentes, des recommandations ou des sites-ressources sur les factures d''électricité, de gaz ou chauffage urbain', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.3', 'cae', '3.1.2.3', 'Développer les prestations en matière de maîtrise de l’énergie et d’énergie renouvelable proposées par les acteurs de l’énergie', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.3.1', 'cae', '3.1.2.3.1', 'Bénéficier et/ou solliciter son syndicat pour mettre en place des services en matière de MDE et d''ENR', 'Le syndicat d’énergie auquel adhère la collectivité propose des services en matière de MDE et d’ENR aux collectivités (groupement de commande d’achats d’énergie renouvelable, récupération de CEE, études d’opportunité ENR, CEP…) ;
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.3.2', 'cae', '3.1.2.3.2', 'Les fournisseurs d’énergie (gaz, chaleur, électricité, en lien avec la collectivité) proposent aux clients des prestations de services dans le domaine de la MDE et des ENR', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.3.3', 'cae', '3.1.2.3.3', 'Se fixer des objectifs de performances et d’amélioration de ces services', 'Par exemple: certification ISO, labellisation Clair’énergie ou référence à des critères de qualité inspirés de démarche de labellisation de l’électricité européennes comme OK Power en Allemagne ou Naturmade Star en Suisse –respect de l’environnement des installations de productions, additionnalité...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.3.4', 'cae', '3.1.2.3.4', 'Accompagner des projets pilotes d’auto-consommation d’électricité renouvelable', 'Aussi évalué dans la mesure 3.1.1
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.4', 'cae', '3.1.2.4', 'Suivre l’efficacité des actions menées', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.4.1', 'cae', '3.1.2.4.1', 'Se rapprocher des fournisseurs actifs de son territoire pour connaître et orienter les offres proposées sur son territoire et suivre le volume d''électricité verte acheté à l''échelle du territoire (habitants et acteurs socio-professionnels publics et privés)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.4.2', 'cae', '3.1.2.4.2', 'Possèder une offre 100% renouvelable et augmenter la proportion de clients sur ce produit', 'Pour une régie d''électricité ou une SEM qui est présente sur le territoire.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.2.4.3', 'cae', '3.1.2.4.3', 'Augmenter le volume des prestations fournies', 'Doit être constatée par l’augmentation du chiffre d’affaire associé à ces prestations, l’augmentation des adhérents, des opérations financées…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1', 'cae', '3.1.1', 'Optimiser le service public de la distribution d''énergie', 'La collectivité, en lien avec les gestionnaires/concessionnaires de réseaux d’électricité, de gaz et de chaleur, veille à la qualité du service rendu et à la contribution de cette mission à sa stratégie d''efficacité énergétique, de développement des énergies renouvelables et de lutte contre le changement climatique. Des réflexions et des actions pour une optimisation du réseau, notamment via la flexibilité de la demande, de la production et/ou du stockage (SmartGrid) sont menées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.1', 'cae', '3.1.1.1', 'Connaître les enjeux de la distribution d’énergie sur le territoire', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.1.1', 'cae', '3.1.1.1.1', 'Connaître les enjeux de la distribution d’énergie sur son territoire', 'La collectivité a une bonne connaissance de son rôle (et de celui du syndicat compétent le cas échéant), des contrats de concession, du compte-rendu d’activité du concessionnaire (rapport CRAC : Compte-rendu Annuel de l’Activité du Concessionnaire).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.1.2', 'cae', '3.1.1.1.2', 'Demander au syndicat d’énergie ou au concessionnaire/gestionnaire du réseau les informations utiles à sa politique énergie', 'Informations utiles: consommations totales par commune, le nb de points de livraison (en soutirage et en injection), le nb de producteurs et la puissance installée et raccordée par énergie, nb et identité des fournisseurs actifs sur le territoire et volume vendu par ces fournisseurs, les dates d''échéance des contrats de concession...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.1.3', 'cae', '3.1.1.1.3', 'Suivre les concessions par des personnes dédiées et spécialisées', 'Ces personnes sont formées notamment aux Smart Grids - au sens large, tous réseaux confondus) : syndicat d’énergie en cas de délégation de la compétence, ou désignation d’une personne en interne, ou mission d’un AMO pour renforcer les compétences.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.1.4', 'cae', '3.1.1.1.4', 'Connaître les provisions pour renouvellement le cas échéant', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.2', 'cae', '3.1.1.2', 'Impliquer les acteurs de la distribution d’énergie de manière transversale', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.2.1', 'cae', '3.1.1.2.1', 'Les concessionnaires prennent ou ont pris une part active aux travaux du PCAET ou équivalent (signature de charte, participation aux ateliers…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.2.2', 'cae', '3.1.1.2.2', 'Mettre en place un comité consultatif spécifique au réseau de chaleur/froid', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.2.3', 'cae', '3.1.1.2.3', 'S’assurer de la bonne représentativité́ de l’ensemble des acteurs et notamment de la présence des représentants des usagers et des abonnés.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.3', 'cae', '3.1.1.3', 'Intégrer des critères d’efficacité énergétique et de développement des énergies renouvelable dans le service public de distribution d’énergie', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.3.1', 'cae', '3.1.1.3.1', 'Pour les réseaux de chauffage/froid urbain, être attentif à la performance environnementale dans le choix de son concessionnaire et inscrire des exigences dans le contrat et les avenants qui les lient', 'Exemple d’exigences : réalisation des travaux et conseils en MDE auprès de la collectivité et des usagers, études de potentiel et promotion des énergies renouvelables, mise en place d’un fond dédié à l’amélioration de performance énergétique du réseau, système de management environnemental type ISO, suivi d’un indicateur en gCO2/kWh... La réalisation effective des actions est vérifiée via le rapport annuel du concessionnaire. Des indicateurs de performance énergétique, environnementale et économique du réseau de chaleur sont mis en évidence à travers un suivi mensuel ou a minima un bilan annuel.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.3.2', 'cae', '3.1.1.3.2', 'Demander aux concessionnaires de préciser dans leurs rapports annuels les mesures d''amélioration des performances énergétiques réalisées et l''état des réflexions et actions sur les smart grid ou l’injection du bio-méthane dans le réseau gaz', 'Exemples de mesures d''amélioration des performances énergétiques: transformateurs performants, calorifugeage des réseaux de chaleur, mesures de rendement...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.3.3', 'cae', '3.1.1.3.3', 'En régie, afficher et formaliser la performance énergétique du service et sa contribution à la politique MDE et EnR de la collectivité', '(règlement de service, délibération, service dédié). Les données sont capitalisées et partagées en interne pour suivre les effets de la politique énergie-climat.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.4', 'cae', '3.1.1.4', 'Définir et piloter une stratégie en matière d’optimisation des réseaux pour dialoguer plus efficacement avec les acteurs spécialisés', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.4.1', 'cae', '3.1.1.4.1', 'Définir un plan stratégique / une feuille de route propre à la collectivité', 'Il doit être alimenté par les rapports annuels des concessionnaires, suivi dans le temps par des agents formés. Les solutions organisationnelles et techniques permettant le soutien au développement des énergies renouvelables électriques sur le territoire en minimisant les couts de raccordement pour les producteurs sont par exemple étudiées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.4.2', 'cae', '3.1.1.4.2', 'Suivre et mettre à jour le schéma de cohérence de développement des réseaux de distribution d’énergie du PCAET', 'Le suivi et la mise à jour doit être réalisé par la collectivité et ses gestionnaires. Il est actualisé avec les opérations d’aménagement de la collectivité et le développement des énergies renouvelables sur le territoire.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.4.3', 'cae', '3.1.1.4.3', 'Piloter et suivre de manière globale sa politique énergétique en exploitant les données des différents réseaux (électricité, gaz, chaleur/froid)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.4.4', 'cae', '3.1.1.4.4', 'Produire des documents synthétisés/appropriés sur ce sujet', 'Exemple : note courte sur les actions menées par la collectivité, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.5', 'cae', '3.1.1.5', 'Mettre en œuvre un programme localisé de maîtrise de l’énergie avec les gestionnaires/concessionnaires des réseaux', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.5.1', 'cae', '3.1.1.5.1', 'Oeuvrer (via l’autorité concédante le cas échéant) pour la mise en place d''un service de flexibilité local auprès du gestionnaire de réseau (étude en cours ou service en place), appelé également « boucle énergétique locale » ou équivalent', 'Montage d’une action pilote de MDE sur une zone particulière, en coordonnant tous les intervenants, en premier lieu le Syndicat d’Energie et le gestionnaire de réseau électrique.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.5.2', 'cae', '3.1.1.5.2', 'Accompagner des projet pilotes en auto-consommation d’électricité renouvelable pour augmenter les taux d’auto-consommation et d’auto-production (juste dimensionnement, synchronisation de la consommation avec la production, opération collective…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.1.1.5.3', 'cae', '3.1.1.5.3', 'Participer à des projets de smartgrid', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2', 'cae', '3.2', 'Production énergétique locale', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1', 'cae', '3.2.1', 'Récupérer la chaleur industrielle, pratiquer la cogénération, utiliser les réseaux de chaleur/froid comme vecteur', 'Les possibilités de récupération de chaleur des grandes entreprises industrielles et des centrales électriques, y compris à partir et pour la production de froid, sont exploitées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1', 'cae', '3.2.1.1', 'Evaluer le potentiel de récupération de chaleur et de cogénération', '* caractérise le gisement de chaleur fatale ;
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.1', 'cae', '3.2.1.1.1', 'Approfondir la connaissance du tissu industriel présent sur leur territoire', 'Recensement large puis pré-ciblage des industries à partir de la qualification de leurs activités (via les bases de données publiques disponibles, ex : base SIRENE).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.2', 'cae', '3.2.1.1.2', 'Se rapprocher auprès des industries a priori pertinentes et des gros consommateurs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.3', 'cae', '3.2.1.1.3', 'Identifier des projets existants et de sites ainsi que des zones de demande en chaleur/froid', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.4', 'cae', '3.2.1.1.4', 'Réaliser une évaluation qualitative des rejets thermiques des entreprises industrielles', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.5', 'cae', '3.2.1.1.5', 'Réaliser une évaluation quantitative du potentiel à travers une étude de potentiel préalable récente', 'L''étude :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.6', 'cae', '3.2.1.1.6', 'Faire une étude sur le potentiel en auto-consommation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.7', 'cae', '3.2.1.1.7', 'Réaliser une étude de potentiel de micro-cogénération', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.1.8', 'cae', '3.2.1.1.8', 'Réaliser une étude de potentiel d’injection vers un réseau de chaleur existant, potentiel pour réaliser des (micro) réseaux de chaleur/froid', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.2', 'cae', '3.2.1.2', 'Soutenir les installations de récupération de chaleur industrielle et de cogénération sur le territoire', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.2.1', 'cae', '3.2.1.2.1', 'Se fixer des dispositions contraignantes et des objectifs de développement dans la programmation énergétique de la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.2.2', 'cae', '3.2.1.2.2', 'Conseiller activement les porteurs de projets de territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.2.3', 'cae', '3.2.1.2.3', 'Permettre aux entreprises de se rencontrer et d’identifier des synergies potentielles relatives à la récupération de chaleur (dans une démarche globale d’écologie industrielle évaluée dans la mesure 6.3.1)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.2.4', 'cae', '3.2.1.2.4', 'Soutenir une campagne / un programme de promotion de la micro-cogénération', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.2.5', 'cae', '3.2.1.2.5', 'Participer financièrement aux installations lorsque la desserte concerne un réseau de chaleur urbain', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.3', 'cae', '3.2.1.3', 'Suivre le développement de la récupération de chaleur et de la cogénération et atteindre les objectifs fixés', '', '', '', '', null, 40.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.3.1', 'cae', '3.2.1.3.1', 'Des installations sont en fonctionnement sur le territoire', 'Avoir connaissance si possible des MWh récupérés/produits. S''en servir d''exemples.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.3.2', 'cae', '3.2.1.3.2', 'Epuiser le potentiel de récupération de la chaleur industrielle identifié', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.3.3', 'cae', '3.2.1.3.3', 'Epuiser le potentiel principal de cogénération (hors micro-cogénération)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.1.3.4', 'cae', '3.2.1.3.4', 'Réaliser l''injection vers un réseau de chaleur à proximité', 'Taux d’ENR&R du réseau de chaleur valorisé dans la mesure 3.2.2.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.3', 'cae', '3.2.3', 'Augmenter la production d''électricité issue d''énergies renouvelables sur le territoire', 'Il y a un pourcentage élevé de production d''électricité renouvelable sur le territoire, comptabilisé en MWh/an et en % de la consommation totale d’électricité sur le territoire (photovoltaïque, hydraulique, éolien, etc.). Le choix du type d’énergie renouvelable pour la production d’électricité est  en fonction des avantages écologiques.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.3.1', 'cae', '3.2.3.1', 'Augmenter progressivement la production d''électricité renouvelable', 'Le taux de production d’électricité renouvelable annuel (en %) est en hausse, avec a minima une augmentation de 20 kWh/hab par rapport au dernier bilan (datant de moins de 4 ans)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.3.2', 'cae', '3.2.3.2', 'Diversifier les sources de production d’électricité renouvelable', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.3.2.1', 'cae', '3.2.3.2.1', 'Susciter les projets citoyens et/ou encadrer l’intervention d’un développeur extérieur pour réaliser et exploiter un équipement dédié à la production d’électricité', 'en complément des installations publiques (mesure 2.2.3), lancer des appels d’offres dans lesquels les conditions précises du développement et de l’exploitation du site sont déterminées, mettre en place des partenariats publics-privés...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.3.2.2', 'cae', '3.2.3.2.2', 'La puissance photovoltaïque installée sur le territoire vise des seuils (dans la description)', '− pour les collectivités > 100 000 habitants : 20 Wc/hab (Métropole) ; 60 Wc/hab (DOM-ROM)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.3.2.3', 'cae', '3.2.3.2.3', 'Mettre en fonctionnement des installations éoliennes ou hydroélectriques sur le territoire', 'Si le potentiel existe, avec y compris l''installation de micro-éolien ou micro-hydroélectricité
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.3.3', 'cae', '3.2.3.3', 'Atteindre une part élevée d’électricité renouvelable', 'La production d''électricité renouvelable atteint:
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.2', 'cae', '3.2.2', 'Augmenter l''utilisation des énergies renouvelables pour la chaleur et le rafraîchissement', 'Le potentiel d''utilisation des énergies renouvelables pour le chauffage des bâtiments, l''eau chaude sanitaire et le rafraîchissement est épuisé, avec une attention sur la qualité de l''air. Lorsque cela est possible, des réseaux de chaleur renouvelable sont mis en place. L’atteinte des objectifs est mesurée avec le pourcentage d’énergie d''origine renouvelable dans la consommation de chaleur ou de  sur le territoire.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.2.1', 'cae', '3.2.2.1', 'Utiliser les réseaux de chaleur pour atteindre les objectifs fixés en matière de chaleur renouvelable', 'Le taux de production d’énergie renouvelable et de récupération des réseaux de chaleur (hors réseaux 100% fossiles) doit atteindre 75%.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.2.2.2', 'cae', '3.2.2.2', 'Atteindre les objectifs fixés en matière de couverture des besoins de chaleur et de rafraichissement du territoire par les énergies renouvelables', 'Le taux de production d''énergie renouvelable pour la chaleur et le rafraîchissement sur le territoire doit atteindre 38% d’énergies renouvelables en Métropole, et doit atteindre 75% de la couverture des besoins par les énergies renouvelables dans les DOM (rappel : l''autonomie énergétique est visée à l''horizon 2030). Valorisation proportionnelle à partir de 10%.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3', 'cae', '3.3', 'Gestion de l''eau, des espaces verts, des déchets du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4', 'cae', '3.3.4', 'Préserver la biodiversité du territoire et développer des espaces verts', 'A travers ses services techniques et ses partenariats, la collectivité contribue à l’amélioration de la connaissance de la biodiversité de son territoire et à la sensibilisation de tous les acteurs à ce sujet. La collectivité met en place des actions concrètes pour préserver et/ou renforcer la biodiversité sur son territoire et développer des espaces verts, notamment via une politique de végétalisation, de lutte contre la pollution lumineuse, de mobilisation des documents d’urbanisme ou d’outils de contractualisation spécifiques. L’approche menée est transversale et les co-bénéfices avec les orientations climat-air-énergie, notamment en  au changement climatique sont particulièrement recherchées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.1', 'cae', '3.3.4.1', 'Connaître l’état de la biodiversité et connaître les enjeux liés aux espaces verts sur son territoire (obligations réglementaires)', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.1.1', 'cae', '3.3.4.1.1', 'Identifier les réservoirs et corridors écologiques et assurer la mise en œuvre de la trame verte et bleue', 'En relation avec le Schéma Régional de cohérence écologique (obligation réglementaire SCoT et PLUi).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.1.2', 'cae', '3.3.4.1.2', 'Etudier les impacts du plan d’action du plan climat / des actions menées dans le cadre de la politique climat air énergie sur la biodiversité', 'Obligation de l’évaluation environnementale stratégique des PCAET.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.2', 'cae', '3.3.4.2', 'Sensibiliser à l’importance et à la fragilité de la biodiversité et aux enjeux liés aux espaces verts', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.2.1', 'cae', '3.3.4.2.1', 'Mettre en place des actions de sensibilisation aux différents publics à la biodiversité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.2.2', 'cae', '3.3.4.2.2', 'Mettre en valeur les informations et actions locales relatives à la biodiversité dans ses supports de communication', 'Par exemple: journal communal ou intercommunal, site web...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.2.3', 'cae', '3.3.4.2.3', 'Participer à la réalisation des pratiques de gestion des espaces verts.', 'Par exemple: initiative, financement, soutien, participation à des réunions, avis...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.2.4', 'cae', '3.3.4.2.4', 'Organiser de sessions de sensibilisation / formation en interne sur la gestion différenciée des espaces verts et les pratiques alternatives aux produits phytosanitaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.3', 'cae', '3.3.4.3', 'Réduire les impacts de l’éclairage sur la biodiversité nocturne', 'Les travaux sur l’éclairage public sont évalués dans la mesure 2.3.1.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.3.1', 'cae', '3.3.4.3.1', 'Etre en relation avec les associations environnementales locales, syndicat d’énergie ou autres partenaires pour faire le lien entre la politique d’éclairage public et le respect de la biodiversité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.3.2', 'cae', '3.3.4.3.2', 'Réaliser une trame sombre (ou trame noire)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.3.3', 'cae', '3.3.4.3.3', 'Organiser un événement contre la pollution lumineuse', 'Par exemple : « Jour de la nuit »
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.3.4', 'cae', '3.3.4.3.4', 'Obtenir le label « Villes et villages étoilés » de l’ANPCEN', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.3.5', 'cae', '3.3.4.3.5', 'Suivre la réglementation relative à l’éclairage des enseignes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.4', 'cae', '3.3.4.4', 'Mettre en place une gestion différenciée des espaces verts et des pratiques alternatives aux produits phytosanitaires sur le territoire', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.4.1', 'cae', '3.3.4.4.1', 'Pratiquer la gestion différenciée des espaces verts et systématiser les pratiques alternatives aux produits phytosanitaires', 'Cela limite au global les consommations d''énergie liées à l''entretien, les quantités d''engrais (notamment chimiques) et d''eau dans l''objectif "zéro phyto" obligatoire à partir de 2017.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.4.2', 'cae', '3.3.4.4.2', 'Sensibiliser les habitants à la gestion différenciée et aux pratiques alternatives aux produits phytosanitaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.4.3', 'cae', '3.3.4.4.3', 'Utiliser des espèces rustiques et adaptées au contexte local, y compris dans la perspective du changement climatique.', 'La collectivité limite les espèces allergènes.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5', 'cae', '3.3.4.5', 'Prendre en compte la biodiversité dans une approche transversale', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5.1', 'cae', '3.3.4.5.1', 'Travailler ensemble pour mener la politique de préservation de la biodiversité (réunion commune, relecture itérative des documents…)', 'Les services et les élus en lien avec l’urbanisme, la voirie, les milieux naturels et la transition énergétique doivent être impliqués.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5.2', 'cae', '3.3.4.5.2', 'Intègrer les enjeux et les objectifs de la démarche climat-air-énergie dans l’élaboration de la trame verte et bleue (et vice-versa)', 'Par exemple: travaux de restauration et entretien des haies bocagères augmentant la ressource bois, croisement des potentiels énergétiques et des cartes d’enjeux environnementaux, séquestration carbone…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5.3', 'cae', '3.3.4.5.3', 'Définir des critères environnementaux pour orienter les porteurs de projet', 'Cela permet de limiter l’impact sur la biodiversité des constructions et des ENR (fiche d’application de la trame verte bleue, zoom par secteur, etc.)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5.4', 'cae', '3.3.4.5.4', 'Concilier trame verte et bleue avec densification des espaces urbains', 'Par exemple: trame verte et bleue urbaine multifonctionnelle, en créant, préservant et/ou restaurant des milieux naturels - notamment berges et cours d''eau- et des corridors écologiques entre ces milieux.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5.5', 'cae', '3.3.4.5.5', 'Décider d’un seuil maximal d’imperméabilisation des sols pour tout projet d’aménagement de construction, de rénovation ou d’agrandissement sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5.6', 'cae', '3.3.4.5.6', 'Utiliser le coefficient de biotope par surface dans le PLU', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.5.7', 'cae', '3.3.4.5.7', 'Intégrer la vulnérabilité des espèces au changement climatique à la stratégie d’adaptation du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.6', 'cae', '3.3.4.6', 'Mettre en place des actions de préservation de la biodiversité contribuant à l’adaptation au changement climatique et à l’amélioration du cadre de vie', 'Les actions ci-dessous favorisent la biodiversité et permettent, dans une approche intégrée, de diminuer le risque inondation, de lutter contre le phénomène d’îlot de chaleur urbain et de développer la “Nature en Ville”.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.6.1', 'cae', '3.3.4.6.1', 'Restaurer des zones humides', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.6.2', 'cae', '3.3.4.6.2', 'Végétaliser des bâtiments (toitures, façades…) en prenant en compte la biodiversité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.6.3', 'cae', '3.3.4.6.3', 'Déminéraliser des espaces urbains non bâtis (cours d’école, d’immeubles, voiries)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.6.4', 'cae', '3.3.4.6.4', 'Mettre en place des actions de valorisation écologique des espaces publics', 'Par exemple: parcs, dents creuses, friches industrielles, coulées vertes, rives des plans et cours d’eau…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.7', 'cae', '3.3.4.7', 'Utiliser des outils de contractualisation opérationnels pour la mise en œuvre de la trame verte et bleue', 'La collectivité met en place/promeut des outils de contractualisation et de maîtrise foncière transversaux dans lesquels la "préservation de la biodiversité, la protection des milieux et des ressources" constitue un pilier majeur.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.7.1', 'cae', '3.3.4.7.1', 'Mettre en place un outil de contractualisation à l’échelle parcellaire', 'Par exemple: Bail rural à clauses environnementales, contrat Natura 2000, convention de gestion, cahier des charges de lotissement, mesure agro environnementale territorialisée, …
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.7.2', 'cae', '3.3.4.7.2', 'Mettre en place un outil à une échelle plus large', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.8', 'cae', '3.3.4.8', 'Suivre l’évolution de la biodiversité et la gestion des espaces verts', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.8.1', 'cae', '3.3.4.8.1', 'Faire contribuer les services de la collectivité en lien avec les espaces verts ou l’entretien du réseau routier au comptage de la biodiversité et au suivi de son évolution', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.8.2', 'cae', '3.3.4.8.2', 'Suivre l’évolution de la biodiversité à travers un observatoire ou un atlas dynamique de la biodiversité locale, en partenariat avec une association ou un autre organisme du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.4.8.3', 'cae', '3.3.4.8.3', 'Suivre les quantités annuelles d''engrais et d''eau pour les espaces verts et les faire diminuer', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1', 'cae', '3.3.1', 'Optimiser l’efficacité des installations d''eau potable (énergie et ressource)', 'La collectivité souhaite réduire ses consommations énergétiques et étend donc son action à l''amélioration et l''optimisation de l''efficacité énergétique de ses installations d''eau potable. Des mesures sont prises pour favoriser une utilisation économe de l''eau et la prise de conscience des consommateurs, par exemple :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.1', 'cae', '3.3.1.1', 'Définir la recherche de l''efficacité énergétique comme un objectif dans les contrats de DSP ou l''inscrire dans la stratégie de la régie', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.1.1', 'cae', '3.3.1.1.1', 'Demander la recherche de l’efficacité énergétique dans les contrats de DSP ou l''inscrite dans la stratégie de la régie', 'Une certification ISO 9001 et/ou 50001,  etc. du service ou des installations est un plus.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.1.2', 'cae', '3.3.1.1.2', 'Determiner les indices les plus importants dans le cadre d''une analyse et proposer des mesures d''amélioration de l''efficacité énergétique', 'Par exemple: consommation d''énergie, rendements, indices linéaires de pertes, …
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.2', 'cae', '3.3.1.2', 'Conseiller et former, de façon fréquente, les acteurs du secteur', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.2.1', 'cae', '3.3.1.2.1', 'Dispenser des formations à destination des acteurs du secteur', 'Par exemple séminaires de formation pour les installateurs sanitaires...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.2.2', 'cae', '3.3.1.2.2', 'Coopérer avec des associations spécialisées', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.3', 'cae', '3.3.1.3', 'Mettre en œuvre des mesures concrètes visant l''amélioration et l''optimisation de l''efficacité énergétique des installations d''eau potable', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.3.1', 'cae', '3.3.1.3.1', 'Exploiter complétement le potentiel des sources situées en hauteur', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.3.2', 'cae', '3.3.1.3.2', 'Exploiter totalement le potentiel de production d''électricité par turbinage', 'Dans la mesure où cela est économiquement raisonnable, par exemple en remplacement des réducteurs de pression.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.3.3', 'cae', '3.3.1.3.3', 'Avoir un rendement élévé sur les pompes à grande capacité dans des conditions hydrauliques adéquates', 'La régulation se fait en fonction de la demande (par vitesse variable si approprié), les moteurs sont choisis de classe IE3 ou IE2 avec variation de vitesse (cf. Directive Européenne CE 640/2009).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.3.4', 'cae', '3.3.1.3.4', 'Optimiser les stations de traitement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.3.5', 'cae', '3.3.1.3.5', 'Mettre à disposition un budget approprié pour les travaux courants de réparation de casses et programmer un budget de renouvellement pluriannuel', 'Une détection des fuites est effectuée (sectorisation, campagnes de détection, …).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.4', 'cae', '3.3.1.4', 'Mettre en place des mesures incitatives pour faire économiser l’eau aux usagers', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.4.1', 'cae', '3.3.1.4.1', 'Définir dans les objectifs politiques du distributeur d''eau l''utilisation économe de l''eau par les clients et les intégrer dans la stratégie commerciale', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.4.2', 'cae', '3.3.1.4.2', 'Utiliser de la facture comme moyen de sensibilisation', 'Avec sur la facture: détails, année précédente, valeurs moyennes françaises d''un ménage, ...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.4.3', 'cae', '3.3.1.4.3', 'Mettre en place des tarifs linéaires ou une tarification progressive (pas de rabais de quantité)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.4.4', 'cae', '3.3.1.4.4', 'Encourager via des actions les comportements et équipements économes en eau', 'Comportements et équipements économes en eau: robinets économes, douche plutôt que bain, usage rationnel du lave-vaisselle et de la machine à laver, ...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.4.5', 'cae', '3.3.1.4.5', 'Promouvoir l''utilisation des eaux pluviales avec une attention particulière accordée à leur qualité', 'cf. action 3.3.3
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.5', 'cae', '3.3.1.5', 'Anticiper le renouvellement des installations', 'Les tarifs appliqués couvrent les coûts à long terme. Ils permettent l''entretien ET le remplacement futur des installations et des réseaux selon leur état.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.1.6', 'cae', '3.3.1.6', 'Suivre les consommations d’énergie et d’eau suite à la mise en œuvre des mesures', 'L’effet des mesures est suivi par au moins 2 indicateurs, régulièrement utilisés pour appliquer des actions correctives et d’amélioration et témoignant d’une évolution positive (ex : consommation d’énergie spécifique, rendement de la distribution ou de l’ensemble du service, Indice linéaire de Pertes en réseau).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5', 'cae', '3.3.5', 'Valoriser les déchets résiduels et les biodéchets', 'La collectivité valorise les déchets résiduels (comprenant les ordures ménagères résiduelles, les refus des centres de tri, le tout-venant, les déchets non recyclables incinérables) et les bio-déchets du territoire :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.1', 'cae', '3.3.5.1', 'Effectuer un état des lieux du potentiel d’optimisation de la valorisation énergétique des déchets', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.1.1', 'cae', '3.3.5.1.1', 'Evaluer finement la quantité de biogaz émis dans les centres d’enfouissement techniques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.1.2', 'cae', '3.3.5.1.2', 'une analyse sur l''augmentation de l''efficacité énergétique de l''UIOM est disponible. Si la collectivité a délégué sa compétence, elle incite et encourage la collectivité compétente à effectuer cette analyse.', 'L’analyse porte par exemple sur :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.1.3', 'cae', '3.3.5.1.3', 'Etudier du potentiel méthanisation sur les biodéchets, intégrant des synergies avec les gros producteurs de biodéchets et les agriculteurs pour une vision globale des gisements', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.1.4', 'cae', '3.3.5.1.4', 'Etudier la faisabilité sur la mise en place de la collecte sélective des biodéchets des ménages (évalué dans l’action 1.2.3)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.1.5', 'cae', '3.3.5.1.5', 'Etudier les possibilités d''injection du biométhane valorisé dans le réseau ou dans des véhicules (budget ou moyens humains mobilisés)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.1.6', 'cae', '3.3.5.1.6', 'Etudier les possibilités de production et de valorisation de bio-carburants à partir des biodéchets locaux (budget ou moyens humains mobilisés)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2', 'cae', '3.3.5.2', 'Valoriser l’énergie issue de la gestion des déchets', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.1', 'cae', '3.3.5.2.1', 'Valoriser la chaleur de l''UIOM (utilisation complète, en interne et/ou vente à l''extérieur)et suivre le % réel de valorisation énergétique/déchets entrants.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.2', 'cae', '3.3.5.2.2', 'Valoriser la chaleur résiduelle (cogénération) si l''UIOM produit de l''électricité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.3', 'cae', '3.3.5.2.3', 'Valoriser la chaleur ou l''électricité dans l''unité de combustion de CSR (pour les DOM)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.4', 'cae', '3.3.5.2.4', 'Optimiser le captage du biogaz issu des centres d’enfouissement', 'Par exemple: diminution des émissions en phase d’exploitation avant mise en œuvre des dispositifs définitifs de captage (captage précoce, optimisation de la durée d’exploitation des alvéoles ou casiers) et limitation des fuites aux parois du réseau, notamment par l’optimisation des dimensionnements des réseaux
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.5', 'cae', '3.3.5.2.5', 'Valoriser le biogaz issu du centre d’enfouissement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.6', 'cae', '3.3.5.2.6', 'Mettre en place une gestion intégrée des différents biodéchets du territoire, avec une part de valorisation énergétique (méthanisation, combustion ou agro-carburants)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.7', 'cae', '3.3.5.2.7', 'Promouvoir les bonnes pratiques de compostage auprès des particuliers', 'Pour les collectivités pratiquant prioritairement le compostage, elles veillent : promouvoir les bonnes pratiques auprès des particuliers (formation de maîtres composteurs, distribution de fourche ou brasse-compost, aires de démonstration, rappel des paramètres clés : équilibre carbone/Azote, taux d''humidité et aération). Pour les plateformes de compostage centralisé, les meilleures pratiques sont réalisées et formalisées (courbes de suivi des températures et de l''humidité, aération...), l''impact des déplacements engendrés pour l''acheminement des déchets verts est pris en compte (choix de la localisation...) et le compost produit est valorisé localement et est conforme à la norme (analyses physico-chimiques). En milieu rural, du co-compostage à la ferme avec un ou plusieurs agriculteurs volontaires est réalisé.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.8', 'cae', '3.3.5.2.8', 'Contrôler l''interdiction du brûlage à l''air libre des déchets verts est contrôlée et faire l''objet de nombreuses actions de sensibilisation', 'Des solutions alternatives sont proposées aux particuliers (amélioration de la collecte des déchets verts, travail sur le compostage dans la restauration collective, filière courte de valorisation pour alimenter les chaufferies bois...)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.2.9', 'cae', '3.3.5.2.9', 'Trouver des débouchés pour l''ensemble de l''énergie produite et pour le compost (lien avec le monde agricole)', 'Par exemple: vente à un fournisseur de gaz, raccordement à un réseau de chaleur, favoriser l’installation d’utilisateurs d’énergie à proximité des sites de traitements des déchets producteurs d’énergie, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.3', 'cae', '3.3.5.3', 'Suivre les objectifs et atteindre un haut niveau de valorisation des déchets', '', '', '', '', null, 40.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.3.1', 'cae', '3.3.5.3.1', 'Avoir un rendement de l''UIOM élevé', 'Le rendement doit être supérieur ou égal au seuil de performance énergétique permettant la modulation de la TGAP selon la règlementation.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.3.2', 'cae', '3.3.5.3.2', 'Les centres de stockage des déchets ménagers et assimilés font l''objet d''une valorisation énergétique du biogaz >75%', 'Par exemple: production de chaleur, cogénération ou injection dans le réseau gaz. La valorisation est proportionnelle selon le pourcentage de biogaz valorisé, à partir de 75% et avec le maximum des points à 100%.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.3.3', 'cae', '3.3.5.3.3', 'Augmenter la production d’électricité et de chaleur en kWh produit à partir de biodéchets pour l''ensemble du territoire (ménages et activités économiques, agricoles...) ou à défaut la quantité en kg/hab.an de biodéchets ménagers et assimilés issus de collecte sélective valorisés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.5.3.4', 'cae', '3.3.5.3.4', 'Témoigner des performances par des indicateurs de suivi de la politique de compostage', 'Par exemple : nombre de composteurs individuels distribués, nb de sites collectifs en pied d''immeuble, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2', 'cae', '3.3.2', 'Optimiser le potentiel énergétique des systèmes d''assainissement', 'La collectivité optimise le potentiel énergétique des systèmes d''assainissement :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.1', 'cae', '3.3.2.1', 'Réaliser un état des lieux de l’efficacité des systèmes d’assainissement et formaliser les objectifs', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.1.1', 'cae', '3.3.2.1.1', 'Effectuer une analyse de l''efficacité énergétique des installations de collecte et d''épuration des eaux usées', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.1.2', 'cae', '3.3.2.1.2', 'Demander dans les contrats de DSP la recherche de l’efficacité énergétique des stations d''épuration ou l''inscrire dans la stratégie de la régie', 'Une certification ISO 14001 et/ou 50001, etc. du service ou des installations est un plus.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.2', 'cae', '3.3.2.2', 'Analyser le potentiel de récupération de chaleur sur les eaux usées', 'Le potentiel d''utilisation des calories  des stations d’épuration et des collecteurs d''eaux usées est déterminé, une étude existe. Elle doit croiser les tailles de collecteurs, les débits disponibles avec les possibilités d’utilisation (les sites consommateurs sont identifiés).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.3', 'cae', '3.3.2.3', 'Analyser le potentiel de valorisation des boues d’épuration', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.3.1', 'cae', '3.3.2.3.1', 'Réaliser une étude sur les possibilités de valorisation des boues', 'La valorisation matière et la valorisation énergétique doivent être étudiées de manière complémentaire selon les débouchés et les possibilités techniques. Le co-traitement avec les déchets ménagers et/ou d’origine agricole est à étudier pour de faibles volumes.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.3.2', 'cae', '3.3.2.3.2', 'Etre attentif à l''actualisation des données et conclusions de l''étude sur les possibilités de valorisation des boues', 'Les gisements et techniques pouvant évoluer au fil du temps, la collectivité doit être attentive (attention aux études trop anciennes).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.4', 'cae', '3.3.2.4', 'Réaliser les travaux d’amélioration de l’efficacité énergétique du système d’assainissement', 'Exemples d''actions possibles pour améliorer l’efficacité énergétique sur les réseaux :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.4.1', 'cae', '3.3.2.4.1', 'Appliquer toutes les mesures résultant des analyses de l’efficacité énergétique du système d’assainissement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.5', 'cae', '3.3.2.5', 'Réaliser les travaux de valorisation énergétique des systèmes d’assainissement', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.5.1', 'cae', '3.3.2.5.1', 'Avoir réalisé ou être en phase de construction des installations de récupération de chaleur sur les eaux usées ou épurées', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.5.2', 'cae', '3.3.2.5.2', 'Mettre en place un dispositif de valorisation des boues d’épuration et dépasser le simple respect de la règlementation dans sa mise en œuvre', 'Exemplees: méthanisation avec injection du biogaz dans le réseau ou cogénération si débouchés de la chaleur existantes à proximité (le chauffage des digesteurs et des locaux ne suffit pas, il est possible d’utiliser la chaleur de l’eau épurée pour cela), valorisation matière avec optimisation de la solution choisie ou caractère innovant montrant une réflexion poussée de la collectivité (séchage solaire des boues, traitements visant à améliorer l''intérêt agronomique des boues ou leur manutention, compostage… la réalisation du plan d’épandage règlementaire est apprécié mais ne suffit pas pour avoir l’ensemble des points).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.6', 'cae', '3.3.2.6', 'Suivre régulièrement les actions et évaluer leurs effets', 'Par défaut : ratio kWh/m3 épuré en diminution (comparaison sur plusieurs années).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.6.1', 'cae', '3.3.2.6.1', 'Suivre l''effet des mesures pour améliorer l’efficacité énergétique des systèmes d’assainissement par des indicateurs', 'Indicateurs prioritaires :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.6.2', 'cae', '3.3.2.6.2', 'Suivre un indicateur mesurant la production de chaleur de récupération sur les eaux usées en MWh/an.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.2.6.3', 'cae', '3.3.2.6.3', 'Suivre un indicateur de valorisation des boues d’épuration, montrant la progression de la collectivité', 'Par exemple le % de boues valorisées par méthanisation et/ou compostées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3', 'cae', '3.3.3', 'Optimiser la gestion des eaux pluviales', 'La collectivité pratique une gestion intégrée des eaux pluviales, permettant de maîtriser le ruissellement pluvial sur les zones aménagées (à la source), ainsi qu’à l’aval (ralentissement, stockage temporaire, infiltration), la dépollution et de s’adapter au site.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.1', 'cae', '3.3.3.1', 'Connaître les enjeux locaux de la gestion des eaux pluviales et des inondations sur son territoire', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.1.1', 'cae', '3.3.3.1.1', 'Sensibiliser élus et technicien à la gestion intégrée des eaux pluviales par des solutions compensatoires de rétention ou infiltration à la parcelle', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.1.2', 'cae', '3.3.3.1.2', 'Cartographier les zones inondables', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.1.3', 'cae', '3.3.3.1.3', 'Réaliser un schéma directeur d’assainissement et du pluvial', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.1.4', 'cae', '3.3.3.1.4', 'Modéliser les comportements des fleuves et des eaux pluviales', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.2', 'cae', '3.3.3.2', 'Informer la population des enjeux et des solutions permettant de limiter le ruissellement', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.2.1', 'cae', '3.3.3.2.1', 'Informer sur les zones à risques et sur les comportements à adopter en cas d’inondation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.2.2', 'cae', '3.3.3.2.2', 'Informer sur la gestion intégrée des eaux pluviales : documents et/ou réunions d’informations spécialement conçus pour les particuliers', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.2.3', 'cae', '3.3.3.2.3', 'Mettre en place des mesures d''incitations des particuliers pour la récupération des eaux pluviales', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.3', 'cae', '3.3.3.3', 'Réduire les émissions de polluants et le ruissellement à la source, au-delà des compétences du service en charge de la gestion des eaux pluviales', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.3.1', 'cae', '3.3.3.3.1', 'Adapter le choix des revêtements de chaussées et autres matériaux urbains (matériaux neutres)', 'Il convient de vérifier l’origine des matériaux et leur absence de contamination.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.3.2', 'cae', '3.3.3.3.2', 'Utiliser des peintures de sols et autres matériaux sans adjuvants toxiques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.3.3', 'cae', '3.3.3.3.3', 'Modifier les pratiques locales de nettoyage des rues (fréquence accrue du nettoyage) et sensibiliser sur la nécessité de ne pas rejeter de détritus sur la voie publique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.3.4', 'cae', '3.3.3.3.4', 'En métropole, utiliser de manière plus réfléchie les produits de déneigement et de déverglaçage', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.3.5', 'cae', '3.3.3.3.5', 'Améliorer l’efficacité des systèmes de dépollution des systèmes industriels producteurs de fumée et améliorer la gestion des aires de stockage industrielles', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.3.6', 'cae', '3.3.3.3.6', 'Faire participer les services urbanisme, espaces verts et transport à l’atteinte des objectifs en matière de gestion intégrée des eaux pluviales', 'Des actions dédiées sont présentes dans le référentiel.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.4', 'cae', '3.3.3.4', 'Utiliser le règlement d’assainissement pour gérer les eaux pluviales au plus près de leur point de chute', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.4.1', 'cae', '3.3.3.4.1', 'Intégrer au règlement d''assainissement des normes permettant de limiter le ruissellement des eaux pluviales et la surcharge des collecteurs d’assainissement', 'Par exemple: rétention à la parcelle, débit de fuite limité, conformité des branchements aux réseaux séparatifs...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.4.2', 'cae', '3.3.3.4.2', 'Appliquer systématiquement les mesures du règlement d''assainissement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.4.3', 'cae', '3.3.3.4.3', 'Mettre en place des mesures particulières sur certaines zones pour limiter les risques d''inondation par ruissellement qui peuvent être amplifiés par le changement climatique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.5', 'cae', '3.3.3.5', 'Mener une politique ferme de prévention des inondations et de protection du milieu récepteur', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.5.1', 'cae', '3.3.3.5.1', 'Participer activement à l''élaboration d''une politique de gestion des inondations', 'Seulement si la collectivité est concernée, notamment dans la perspective de l''adaptation au changement climatique (échelle communale ou supra-communale, par exemple via les programmes d''actions de prévention contre les inondations (PAPI), le plan submersions rapides...).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.5.2', 'cae', '3.3.3.5.2', 'Mettre en place un centre de contrôle des installations d’assainissement et pluvial', 'La collectivité doit assurer la dépollution des rejets urbains par temps de pluie (temps de retour au moins mensuel) avant rejet au milieu naturel par la gestion dynamique des bassins et réseaux, la concentration des polluants et le traitement sur stations d’épuration.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.5.3', 'cae', '3.3.3.5.3', 'Couvrir toutes les zones concernées par le risques inondations par un PPRI', 'Les PPRI anciens sont révisés pour intégrer l’évolution du contexte (imperméabilisation, changement climatique…).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.5.4', 'cae', '3.3.3.5.4', 'Interdire les constructions dans les zones à fort risques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.5.5', 'cae', '3.3.3.5.5', 'Mettre en place la taxe GEMAPI', 'Seulement si la compétence "gestion des milieux aquatiques et la prévention des inondations" est prise.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.6', 'cae', '3.3.3.6', 'Devenir exemplaire sur l’ensemble du territoire', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.6.1', 'cae', '3.3.3.6.1', 'Avoir mis en place sur le territoire de nombreux ouvrages de gestion alternative des eaux pluviales', 'Leur mise en œuvre est systématiquement étudiée dans les projets : noues, tranchées, jardins de pluie, bassins paysagers, espaces inondables intégrés à l''aménagement, ...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.6.2', 'cae', '3.3.3.6.2', 'Evaluer la proportion du territoire équipée d''un système séparatif ou de rétention/infiltration à la parcelle', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_3.3.3.6.3', 'cae', '3.3.3.6.3', 'Traiter les rejets urbains par temps de pluie (de temps de retour au moins mensuel) avant rejet au milieu naturel', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4', 'cae', '4', 'Mobilité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1', 'cae', '4.1', 'Promotion et suivi de la mobilité durable sur le territoire et en interne', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1', 'cae', '4.1.1', 'Promouvoir et suivre les pratiques multimodales sur le territoire', 'La collectivité conduit des actions régulières de promotion et d''information pour une mobilité efficace et active à l''échelle de l''ensemble de son territoire. Elle s’associe pour cela à l’ensemble des acteurs impliqués dans le domaine de la mobilité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.1', 'cae', '4.1.1.1', 'Connaître les parts modales sur son territoire', 'La connaissance du taux de motorisation et de multi-motorisation des ménages (données INSEE enquêtes logement) est évaluée dans la mesure 1.2.2.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.1.1', 'cae', '4.1.1.1.1', 'Réaliser une enquête destinée à évaluer la part modale de chaque mode de déplacement actif ou alternatif', 'Réalisation d''une Enquête Mobilité Certifiée CEREMA (EMC²) ou d''une méthodologie simplifiée ou partenariale pour les petites collectivités (niveau intercommunal avec déclinaison communale, étude réalisée dans le cadre de l''élaboration d''un PDM, d''un SCoT ou d''un PLU...). A minima, pour les territoires ruraux, les parts modales des trajets domicile-travail à partir des données INSEE sont identifiées.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.2', 'cae', '4.1.1.2', 'S’organiser pour mener une politique d’information et de sensibilisation sur la mobilité durable', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.2.1', 'cae', '4.1.1.2.1', 'Formaliser une politique d''information et de sensibilisation des habitants, intégrant notamment des liens avec la qualité de l’air', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.2.2', 'cae', '4.1.1.2.2', 'Disposer d''un budget et de moyens humains dédiés à l’information sur la mobilité durable', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.2.3', 'cae', '4.1.1.2.3', 'Travailler a minima avec les autres collectivités et les Autorités Organisatrices des Mobilités afin de développer l’intermodalité (promotion des autres modes de transports disponibles au départ ou à destination, etc.)', 'Les AOM sont compétentes en matière d’organisation des déplacements sur leur territoire.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.3', 'cae', '4.1.1.3', 'Promouvoir l’intermodalité et les modes de transports alternatifs à la voiture individuelle sous des formes et vers des cibles variées', 'La collectivité utilise pour cela des supports et des formats variés :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.3.1', 'cae', '4.1.1.3.1', 'Promouvoir et soutenir les modes alternatifs à la voiture individuelle et l''intermodalité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.3.2', 'cae', '4.1.1.3.2', 'Promouvoir et soutenir le télétravail, les vidéo-conférences, l’aménagement des horaires de travail pour limiter les déplacements et faciliter l''usage des transports en commun, ou modes actifs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.3.3', 'cae', '4.1.1.3.3', 'Promouvoir et soutenir les Plans de mobilité dans les entreprises (PDME - Plans de mobilité employeur) et les plans de mobilité employeur communs pour les entreprises situées sur un même site.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.3.4', 'cae', '4.1.1.3.4', 'Promouvoir et soutenir l''usage des véhicules électriques, au biogaz ou à hydrogène de manière raisonnée et complémentaire aux modes alternatifs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.4', 'cae', '4.1.1.4', 'Créer un lieu/service dédié à l’information sur l’éco-mobilité', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.4.1', 'cae', '4.1.1.4.1', 'Centraliser l’information disponible dans un lieu dédié à la mobilité ou diffusée par un service unique', 'Ce lieu/service doit être facilement identifiable par les usagers (numéro d’appel dédié, horaires d’ouverture larges, localisation centrale…). Ce lieu/service, qui peut être mutualisé à l’échelle intercommunale, dispense des conseils gratuits et vise à réduire l’utilisation systématique de la voiture individuelle. Il s''adresse aux entreprises, aux administrations, aux commerces, aux établissements d''enseignements, de soins ou de loisirs, mais aussi aux particuliers (salariés, élèves, parents, touristes, clients...).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.4.2', 'cae', '4.1.1.4.2', 'Développer des services d’information individualisée de mobilité auprès des usagers', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.5', 'cae', '4.1.1.5', 'Travailler avec les acteurs de la mobilité à l’échelle du bassin de vie', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.5.1', 'cae', '4.1.1.5.1', 'Créer/participer à un réseau local dédié à la mobilité durable', 'Ce réseau est élargi et regroupe les différents acteurs locaux concernés par la mobilité durable (collectivités, services de l''État, AOT-AOM, entreprises de transport, entreprises, organismes consulaires, associations d''usagers ou de salariés, etc.). Le réseau est destiné à capitaliser et mutualiser les bonnes pratiques ainsi que les moyens (plans de mobilité , plateforme de covoiturage, formations à l''éco-conduite, etc.). Il contribue également à la finalisation et l''adaptation de la stratégie en matière de transport et de mobilité, notamment pour l''élaboration d''un plan de mobilité ou d’un plan de mobilité simplifié  (cf. action 1.2.2).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.6', 'cae', '4.1.1.6', 'Vérifier la progression des parts modales des modes de transports alternatifs à la voiture, atteindre des valeurs exemplaires et les communiquer', '', '', '', '', null, 40.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.6.1', 'cae', '4.1.1.6.1', 'Analyser la progression des parts modales dans le temps et les comparer avec les parts modales moyennes à l''échelle nationale (ou régionale pour les DOM) ou encore de collectivités exemplaires', 'Par exemple: part modale des piétons, des cyclistes, des TC, du co-voiturage ou autre système alternatif à la voiture individuelle, adapté au milieu rural notamment (cf. indicateurs). Pour les collectivités rurales ou de petite taille, ce suivi peut-être mutualisé avec d’autres collectivités, et effectué à une échelle supérieure (avec appropriation et déclinaison par la collectivité infra).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.6.2', 'cae', '4.1.1.6.2', 'Avoir de bonnes performances', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.1.6.3', 'cae', '4.1.1.6.3', 'Communiquer et valoriser l''exemplarité de la politique de mobilité durable menée (via une campagne d''affichage des chiffres clés, dans les journaux locaux, …)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2', 'cae', '4.1.2', 'Promouvoir la mobilité durable en interne au sein de la collectivité', 'La collectivité veille à la gestion, l''utilisation efficace et à la faible consommation de carburant de sa flotte de véhicules. Cette action implique des mesures portant sur la maintenance, l''entretien, la conduite ou encore l''évolution du parc de véhicules. Il s''agit également d''optimiser, rationaliser et mutualiser l''ensemble des déplacements des agents de la collectivité :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.1', 'cae', '4.1.2.1', 'Réaliser un diagnostic de ses besoins et de sa flotte de véhicules', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.1.1', 'cae', '4.1.2.1.1', 'Evaluer ses besoins en matière de véhicules motorisés (kilomètres parcourus, usages, temps et fréquence d''utilisation, location, communication, …).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.1.2', 'cae', '4.1.2.1.2', 'Réaliser un bilan économique de son poste « déplacements »', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.1.3', 'cae', '4.1.2.1.3', 'La collectivité réalise un diagnostic de sa flotte de véhicules.', 'A ce titre, elle élabore :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.2', 'cae', '4.1.2.2', 'Engager une politique de mobilité durable au sein de la collectivité', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.2.1', 'cae', '4.1.2.2.1', 'S’engager politiquement en faveur d’un Plan de Mobilité', 'Avoir une délibération sur des objectifs chiffrés, discussion sur la mise en place du Forfait Mobilités durables.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.2.2', 'cae', '4.1.2.2.2', 'Encourager le personnel à l''éco-mobilité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.2.3', 'cae', '4.1.2.2.3', 'Prendre des directives concernant la priorité aux trajets en transports publics, à vélo, à pied pour les déplacements domicile-travail et/ou professionnels seulement (note de service)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.2.4', 'cae', '4.1.2.2.4', 'Justifier les déplacements professionnels en avion et voiture (soumis à une autorisation préalable), et les déplacements des élus municipaux, dont les frais font l’objet d’une prise en charge par la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.2.5', 'cae', '4.1.2.2.5', 'Intégrer dans les directives d’achats des critères d’efficacité énergétique et des carburants faiblement émetteurs en CO2 et polluants atmosphériques (attention portée à l’étiquette énergie au poids des véhicules ainsi qu’à leur motorisation : électrique, biogaz ou hydrogène).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.3', 'cae', '4.1.2.3', 'Former à l''éco-conduite et renouveler sa flotte de véhicules', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.3.1', 'cae', '4.1.2.3.1', 'Proposer une offre de formation de type "Eco-drive" aux agents.', 'Ces formations sont proposées à tous les agents, et le suivi de ces formations se fait notamment pour les collaborateurs parcourant beaucoup de kilomètres, via des cours réguliers de remise à niveau (l''administration paie les frais de formation et les heures d''absence).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.3.2', 'cae', '4.1.2.3.2', 'Renouveler la flotte de véhicules (en priorité les véhicules les plus polluants et énergivores) et acheter de nouveaux véhicules en s''appuyant sur les critères fixés en matière d''énergie, d''émissions de carbone et de polluants ainsi qu''en prenant en compte les besoins et usages de la collectivité (taille, poids des véhicules...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.4', 'cae', '4.1.2.4', 'Optimiser, rationaliser les déplacements au sein de la collectivité', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.4.1', 'cae', '4.1.2.4.1', 'Optimiser ou rationaliser les trajets et les circuits en installant des systèmes de géolocalisation et des logiciels d''optimisation des déplacements', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.4.2', 'cae', '4.1.2.4.2', 'Mettre en place en interne des dispositifs de covoiturage ou de mutualisation des véhicules et des trajets entre les services, et limiter les véhicules de fonction au sein de la collectivité (réduction du nombre de véhicules sur la base des besoins réels et substitution de certains véhicules par des modes alternatifs)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.4.3', 'cae', '4.1.2.4.3', 'Réfléchir à une réorganisation des activités moins génératrices de flux', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.5', 'cae', '4.1.2.5', 'Mettre en place des mesures incitatives', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.5.1', 'cae', '4.1.2.5.1', 'Réglementer les places de stationnement devant les bâtiments communaux (indiquer le règlement et le tarif)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.5.2', 'cae', '4.1.2.5.2', 'Faire la promotion du co-voiturage pour les employés et faciliter le stationnement pour les covoitureurs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.5.3', 'cae', '4.1.2.5.3', 'Encourager le télétravail et les vidéo-conférences (si débit suffisant en zone rurale)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.5.4', 'cae', '4.1.2.5.4', 'Aménager les horaires pour faciliter l’usage des modes actifs ou TC.', 'En l’absence de TC, la collectivité mène une réflexion pour mettre en place des systèmes de mobilités alternatives aux TC par exemple ligne de bus scolaire ou vélo-bus scolaire, transport à la demande, réflexion autour des pistes cyclables touristiques, etc.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.5.5', 'cae', '4.1.2.5.5', 'Déployer d’autres mesures incitatives par exemple installation de douches, places de parking vélo répondant aux besoins des agents etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.6', 'cae', '4.1.2.6', 'Mettre à disposition des moyens physiques dans la majorité des lieux de travail', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.6.1', 'cae', '4.1.2.6.1', 'Proposer des places de stationnement attrayantes et sécurisées pour les vélos, situées à un endroit optimal près de l''entrée', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.6.2', 'cae', '4.1.2.6.2', 'Acheter et mettre à disposition des agents des vélos de service ou de vélos de fonction', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.6.3', 'cae', '4.1.2.6.3', 'Donner la possibilité de suspendre/sécher l''équipement cycliste (casque, pèlerine), douches/vestiaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.6.4', 'cae', '4.1.2.6.4', 'Déployer une offre de voiture en auto-partage (pool de véhicules de service partagé)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.6.5', 'cae', '4.1.2.6.5', 'Disposer de matériels de visioconférence et d’audioconférence performants (si débit suffisant en zone rurale)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.7', 'cae', '4.1.2.7', 'Inciter financièrement les agents à l’usage des modes actifs / TC', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.7.1', 'cae', '4.1.2.7.1', 'Mettre à disposition et utiliser des titres de transports publics non nominatifs pour les déplacements professionnels', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.7.2', 'cae', '4.1.2.7.2', 'Mettre à disposition des employés des contributions pour des abonnements transports publics ou location vélo allant au-delà des obligations réglementaires.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.7.3', 'cae', '4.1.2.7.3', 'Mettre en place  le forfait mobilité durable ou le remboursement des frais d''alimentation d''un véhicule électrique personnel (voiture ou vélo à assistance électrique) utilisé dans le cadre du travail.', 'Le règlement du remboursement des frais est complété par des conditions liées à l''énergie (déplacements professionnels en vélo ou en train, abonnements transmissibles, cartes de réduction, etc.).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8', 'cae', '4.1.2.8', 'Atteindre les objectifs et communiquer sur les actions engagées', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.1', 'cae', '4.1.2.8.1', 'Faire diminuer les consommations annuelles (kW/an/agent)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.2', 'cae', '4.1.2.8.2', 'Faire diminuer les émissions de polluants atmosphériques associés à l’usage de la flotte de véhicules sont en diminution', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.3', 'cae', '4.1.2.8.3', 'Faire augmenter le pourcentage d’agents formés à l’éco-conduite', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.4', 'cae', '4.1.2.8.4', 'Réaliser des enquêtes périodiques sur les critères du Plan de mobilité et sur la répartition modale', 'Par exemple sur l''utilisation des places de stationnement, sur l''utilisation des voitures partagées, sur les vélos de service (suivi du nb de km parcourus) et sur d''autres offres de mobilité auprès du personnel de la collectivité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.5', 'cae', '4.1.2.8.5', 'Réduire les déplacements liés à son activité (attesté par des chiffres)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.6', 'cae', '4.1.2.8.6', 'Faire augmenter la part des déplacements professionnels sans voiture (attesté par des chiffres)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.7', 'cae', '4.1.2.8.7', 'Pérenniser les actions écomobiles', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.8', 'cae', '4.1.2.8.8', 'Intégrer et utiliser les résultats des enquêtes dans les processus d’amélioration', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.1.2.8.9', 'cae', '4.1.2.8.9', 'Communiquer sur son exemplarité, via notamment un affichage sur les véhicules de la collectivité (ex : "je roule au gaz naturel")', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2', 'cae', '4.2', 'Rationalisation des déplacements motorisés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3', 'cae', '4.2.3', 'Optimiser la logistique et la gestion des flux de marchandises sur le territoire', 'Pour réduire ses consommations d’énergie et réduire les émissions de gaz à effet de serre, la collectivité définit des règles et organise l''approvisionnement et la distribution des marchandises de son territoire. Elle favorise également une activité de circuits de proximité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1', 'cae', '4.2.3.1', 'Engager une concertation avec les acteurs locaux pour identifier les flux de marchandises et les besoins', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1.1', 'cae', '4.2.3.1.1', 'Identifier, contacter et mettre en relation les différents acteurs de la logistique sur le territoire (collectivités, transporteurs, clients, chargeurs, commerçants et citoyens) y compris les opérateurs portuaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1.2', 'cae', '4.2.3.1.2', 'Dresser une cartographie des flux (intégrant aussi le trafic engendré par le transit des poids lourds)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1.3', 'cae', '4.2.3.1.3', 'Réaliser un état des lieux sur la livraison de marchandises sur le territoire, (flux, nature, en intégrant notamment les opérateurs portuaires et la problématique des livraisons liées au e-commerce, notamment celles assurées par des véhicules utilitaires légers) et les besoins de stationnement associés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1.4', 'cae', '4.2.3.1.4', 'Connaître et sensibiliser les entreprises aux enjeux du « dernier kilomètre » dans la distribution de marchandises et de l’impact sur la qualité de l’air des véhicules utilitaires et des poids lourds anciens (notamment pour s''adapter à la mise en place éventuelle de zone à circulation restreinte)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1.5', 'cae', '4.2.3.1.5', 'Identifier un interlocuteur au sein de la collectivité pour traiter du sujet', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1.6', 'cae', '4.2.3.1.6', 'Identifier les circuits-courts de proximité pertinents et favoriser leur déploiement autour des bassins de vie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.1.7', 'cae', '4.2.3.1.7', 'Réaliser des études sur l’optimisation énergétique des équipements du port, sur les besoins de formation des grutiers, des chauffeurs, etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.2', 'cae', '4.2.3.2', 'Intégrer des orientations en faveur de l''optimisation du transport de marchandises à l''échelle du territoire dans les documents de planification', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.2.1', 'cae', '4.2.3.2.1', 'Intégrer des actions précises sur le transport de marchandises en ville dans le PDU', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.2.2', 'cae', '4.2.3.2.2', 'Orienter les projets d’équipements en faveur de l’optimisation du transport de marchandises à l’échelle du territoire dans le SCOT par exemple le report modal du routier vers le rail ou le fluvial (ou maritime et cabotage)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.2.3', 'cae', '4.2.3.2.3', 'Optimiser les schémas logistiques urbains par rapport à leur impact sur la qualité de l''air', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.3', 'cae', '4.2.3.3', 'Se doter de la compétence "gestion des marchandises"', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4', 'cae', '4.2.3.4', 'Réglementer, encadrer, soutenir et faire appliquer l''optimisation de la gestion des flux de marchandises sur le territoire', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4.1', 'cae', '4.2.3.4.1', 'Réglementer afin d''organiser l''accès au centre-ville (plage horaire, différenciation selon les performances environnementales des véhicules, etc), voire mise en place d''une zone à circulation restreinte en zone PPA', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4.2', 'cae', '4.2.3.4.2', 'Proposer des solutions alternatives de transport de marchandises', 'Par exemple: création d''un port fluvial dédié au transport de marchandises, création de centres de distribution ou de plateformes urbaines pour centraliser les apports et réacheminer les marchandises sur les derniers kilomètres, promotion des modes actifs de livraison (vélo, triporteur...)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4.3', 'cae', '4.2.3.4.3', 'Mettre en place des centres de distribution urbaine (pour les collectivités > 50 000 habitants) et des points relais (dans des commerces, des entreprises ou des équipements spécialisés)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4.4', 'cae', '4.2.3.4.4', 'Mettre en place des services mutualisés de livraison (acteurs économiques, établissements publics et des particuliers)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4.5', 'cae', '4.2.3.4.5', 'Encourager la mutualisation des services', 'Par exemple mutualiser les activités de commerces/services ambulants en milieu rural (épicerie, postes, etc) lorsque la pertinence climat-air-énergie est avérée (bon fonctionnement du service, circuit étudié, réponse à un besoin réel...)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4.6', 'cae', '4.2.3.4.6', 'En milieu rural, cibler les acteurs clés par exemple les agriculteurs pour réaliser des actions pour optimiser le transport de marchandises', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.4.7', 'cae', '4.2.3.4.7', 'En présence d’un port, Réaliser des actions spécifiques', 'Par exemple: formation des grutiers, optimisation des véhicules de transport d''un point de vue énergétique, …
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.5', 'cae', '4.2.3.5', 'Repenser la stratégie de stationnement des véhicules de marchandises', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.5.1', 'cae', '4.2.3.5.1', 'Proposer un réseau cohérent et suffisant d''aires de livraison', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.5.2', 'cae', '4.2.3.5.2', 'Réglementer sur la durée et la plage horaire autorisée pour éviter les heures de pointe et limiter les conflits d''usage', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.5.3', 'cae', '4.2.3.5.3', 'Contrôler le bon fonctionnement des aires de livraisons (respect, juste dimensionnement et localisation)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.6', 'cae', '4.2.3.6', 'Inciter et soutenir les acteurs du territoire dans un cadre formalisé', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.6.1', 'cae', '4.2.3.6.1', 'Élaborer une charte locale "logistique urbaine" reprenant les principes du cadre national, avec plan d''actions sur les GES et les polluants atmosphériques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.6.2', 'cae', '4.2.3.6.2', 'S''engager avec l''ADEME dans une démarche "engagement volontaire collectivités", qui cible la logistique urbaine durable', 'Par exemple le programme InTerLUD : Innovations Territoriales et Logistique Urbaine Durable, 2020-2022
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.6.3', 'cae', '4.2.3.6.3', 'Engager une démarche environnementale de type charte « Objectif CO2 » pour inciter les transporteurs à optimiser leur activité en termes d''émissions de GES', 'Programme EVE, dont le porteur pilote est l’ADEME www.eve-transport-logistique.fr
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.3.6.4', 'cae', '4.2.3.6.4', 'Inciter les chargeurs à s’engager dans une démarche volontaire FRET 21ou EVCOM pour leur permettre de réduire l’impact environnemental de leurs transports', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2', 'cae', '4.2.2', 'Réguler le trafic, réduire la vitesse et valoriser l''espace public', 'La collectivité met en œuvre des actions destinées à limiter et réduire le trafic sur les axes principaux, réduire les vitesses dans certaines zones et améliorer la qualité des espaces publics. Il s''agit notamment, à travers l’acquisition d’outil de gestion du trafic d’une part, de la signalétique, la restructuration et le réaménagement de la voirie et des espaces publics d’autre part, de privilégier les usagers les plus vulnérables tels que les piétons et cyclistes ainsi que de redynamiser les commerces de proximité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.1', 'cae', '4.2.2.1', 'Identifier les zones génératrices de déplacements et traversées par des flux importants', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.1.1', 'cae', '4.2.2.1.1', 'Identifier les points noirs (accidentologie)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.1.2', 'cae', '4.2.2.1.2', 'Identifier les axes où la congestion est importante et de risque de bouchons', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.1.3', 'cae', '4.2.2.1.3', 'Identifier où la collectivité à des voies réservées au transport public', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.1.4', 'cae', '4.2.2.1.4', 'Repérer les zones génératrices de déplacements et les axes de circulation importants', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.1.5', 'cae', '4.2.2.1.5', 'Identifier les zones où il est nécessaire de mettre en place des zones apaisées (zone résidentielles, proximité des écoles, cœur de bourg, …)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.1.6', 'cae', '4.2.2.1.6', 'Réaliser des actions de concertation en amont avec la population pour l’identification des besoins et des ressentis', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.2', 'cae', '4.2.2.2', 'Mettre en place des outils de gestion du trafic', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.2.1', 'cae', '4.2.2.2.1', 'Formaliser une stratégie de gestion du trafic différenciée selon les zones (centre, périphérie, axes principaux, zones résidentielles, rues commerciales…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.2.2', 'cae', '4.2.2.2.2', 'Mettre en place un outil de suivi du trafic (en temps réel dans l’idéal) et utiliser des données pour l’amélioration de la politique de mobilité et notamment sur les évolutions des tracées des voies bus (pour les collectivités traversées par des flux importants et/ou celles dotées de transport en commun)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.2.3', 'cae', '4.2.2.2.3', 'Valoriser les données pour mieux anticiper les difficultés à venir et notamment favoriser le transport public, réduire les bouchons et réduire les pollutions et risques d’accidents', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.3', 'cae', '4.2.2.3', 'Réaménager et restructurer la voirie et les espaces publics afin de créer des zones de circulation apaisée', '', '', '', '', null, 35.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.3.1', 'cae', '4.2.2.3.1', 'Réaménager et restructurer la voirie et les espaces publics (trame viaire, organisation et signalétique) dans les zones présentant des problématiques de conflits (zones hostiles) piétons/cyclistes/automobilistes', 'Avec pour objectif d''améliorer la qualité des espaces publics et la redynamisation du commerce de proximité :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.3.2', 'cae', '4.2.2.3.2', 'Installer une signalisation favorisant la réduction des vitesses et la valorisation des commerces de proximité', 'Par exemple: radars pédagogiques, silhouettes d’enfants, traitement au sol différencié des linéaires commerciaux, …
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.3.3', 'cae', '4.2.2.3.3', 'Mettre en place une signalisation aux entrées de ville pour signaler la politique générale de la ville', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.3.4', 'cae', '4.2.2.3.4', 'Collaborer avec les acteurs du territoire et des citoyens pour la mise en place des actions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.4', 'cae', '4.2.2.4', 'Evaluer les actions en faveur de la régulation du trafic et la valorisation de l’espace public', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.4.1', 'cae', '4.2.2.4.1', 'Avoir de nombreux axes intégrés dans l’outil de gestion du trafic', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.4.2', 'cae', '4.2.2.4.2', 'Avoir un pourcentage important de voirie apaisée (majorité des voies en centre-ville, voire généralisation de la zone 30 en ville avec zone d’exception à 50)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.4.3', 'cae', '4.2.2.4.3', 'Évaluer et suivre les actions mises en œuvre avec des enquêtes périodiques comme les contrôles de la vitesse, la statistique des accidents, la réduction du danger, des sondages de popularité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.2.4.4', 'cae', '4.2.2.4.4', 'Utiliser les résultats des enquêtes et les intégrer dans les processus d''amélioration', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1', 'cae', '4.2.1', 'Élaborer une politique de stationnement volontariste', 'La collectivité souhaite mieux partager l''espace public entre les différents modes de transport, en particulier au niveau des zones centres, en s''appuyant sur une politique de stationnement des véhicules motorisés raisonnée. Ainsi la collectivité met en place une stratégie efficace, elle gère l’ensemble du stationnement sur l’espace public, y compris en dehors du centre (payant).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.1', 'cae', '4.2.1.1', 'Réaliser un diagnostic de l’offre de stationnement et des pratiques', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.1.1', 'cae', '4.2.1.1.1', 'Réaliser un diagnostic de l''offre en stationnement par exemple l’inventaire des places de stationnement (publiques a minima et privées si possible) et du potentiel (jour et nuit)', '(en collaboration avec les acteurs locaux tels que les commerçants et les riverains)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.1.2', 'cae', '4.2.1.1.2', 'réaliser un inventaire des pratiques actuelles (a minima en zone centre et de forte attractivité)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.2', 'cae', '4.2.1.2', 'Mettre en place une stratégie sur les zones analysées', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.2.1', 'cae', '4.2.1.2.1', 'Mettre en place une politique de stationnement dans le centre-ville et/ ou les zones de forte pression (commerces de quartier, etc.)', 'Sur la base des besoins analysés: résidents, places réservées à la logistique, places réservées aux PMR, co-voiturage, etc.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.2.2', 'cae', '4.2.1.2.2', 'Mettre en place une tarification différenciée si besoin', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.2.3', 'cae', '4.2.1.2.3', 'Pour les communes en zones rurale, mettre l''accent sur la sensibilisation et l’information auprès des habitants sur le besoin de gérer l’usage de la voiture et de marquer les places réservées au stationnement et le développement de zones bleues le cas échéant autour des zones de trafic.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.2.4', 'cae', '4.2.1.2.4', 'Faire respecter la réglementation via de la prévention et de la verbalisation', '(suivi à titre informatif du nombre de verbalisation)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.3', 'cae', '4.2.1.3', 'Favoriser les véhicules et les espaces de stationnement plus respectueux de l''environnement', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.3.1', 'cae', '4.2.1.3.1', 'Mettre en place une tarification et une signalisation différenciée selon la qualité environnementale des véhicules (co-voiturage, véhicules peu émetteurs, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.3.2', 'cae', '4.2.1.3.2', 'Installer des bornes ou services de recharge pour les véhicules électriques sur les sites stratégiques, et dans les ensembles d''habitations et locaux tertiaires (anticipant ou allant plus loin que les obligations réglementaires), en veillant à la cohérence avec la stratégie de rationalisation du stationnement établie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.3.3', 'cae', '4.2.1.3.3', 'Mettre en place un système de guidage pour limiter la recherche de place (si nécessaire)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.3.4', 'cae', '4.2.1.3.4', 'Ombrager les espaces de stationnement pour limiter le recours à la climatisation, tout en limitant l’imperméabilisation des sols', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.4', 'cae', '4.2.1.4', 'Repenser la stratégie de stationnement afin de favoriser l''intermodalité et le recours aux modes actifs de déplacement', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.4.1', 'cae', '4.2.1.4.1', 'Inscrire des objectifs maximaux de stationnement privé dans les documents d''urbanisme', 'Par exemple: normes minimales de stationnement privé sur parcelle.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.4.2', 'cae', '4.2.1.4.2', 'Ne pas favoriser la voiture à tout prix en centre-ville ou dans les situations complexes en terme de stationnement', 'Au cas par cas : privilégier les parkings relais en périphérie ou à proximité des gares (cf. mesure 4.3.4), parking déporté de plusieurs centaines de mètres bien reliés aux Pôles Générateurs de Trafic par des itinéraires piétons ou cycles, aux abords des écoles par exemple.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.4.3', 'cae', '4.2.1.4.3', 'Encourager le stationnement dans les parkings souterrains ou fermés plutôt qu''en voirie pour rééquilibrer l''occupation de l''espace public au profit des cheminements piétons et cyclables et réduire le nombre de voitures ventouses.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.4.4', 'cae', '4.2.1.4.4', 'Réserver une partie des places « libérées » en voirie aux personnes à mobilité réduite, afin d’améliorer leur accessibilité.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.4.5', 'cae', '4.2.1.4.5', 'Convertir les places de stationnement sur voirie situées juste avant un passage piéton en espace de stationnement cyclable (favorisant au passage la visibilité des piétons)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.4.6', 'cae', '4.2.1.4.6', 'Affecter les recettes du stationnement payant pour la mobilité durable (par ex. dédier les recettes du stationnement au financement de vélos en libre-service)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.5', 'cae', '4.2.1.5', 'Elargir la réflexion sur le stationnement', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.5.1', 'cae', '4.2.1.5.1', 'Intégrer à la réflexion le stationnement de nuit et le stationnement privé (pour les nouvelles places), réglementer le stationnement en dehors des zones centrales ou de forte attractivité', 'Pour les collectivités situées dans des unités urbaines >50 000 habitants.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.5.2', 'cae', '4.2.1.5.2', 'Mettre en place des espaces de stationnement dans une perspective d’usages multiples (scolaire, commercial, associatif)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.6', 'cae', '4.2.1.6', 'Evaluer et communiquer sur la politique de stationnement', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.6.1', 'cae', '4.2.1.6.1', 'Mener des enquêtes périodiques sur le taux de rotation et sur la régularité du stationnement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.6.2', 'cae', '4.2.1.6.2', 'Analyser le taux d’occupation dans le centre-ville', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.6.3', 'cae', '4.2.1.6.3', 'Suivre un indicateur en nombre de place de stationnement par habitant et le comparer à des valeurs de références, pour viser un juste dimensionnement de l’offre selon le contexte et les modes alternatifs disponibles', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.6.4', 'cae', '4.2.1.6.4', 'Prouver l’efficacité de la tarification sur le trafic', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.6.5', 'cae', '4.2.1.6.5', 'Utiliser et intégrer les résultats des enquêtes dans les processus d’amélioration', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.2.1.6.6', 'cae', '4.2.1.6.6', 'Communiquer les résultats aux acteurs du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3', 'cae', '4.3', 'Mobilité alternative', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2', 'cae', '4.3.2', 'Développer le réseau et les infrastructures cyclables', 'Pour réduire les consommations d''énergie et les émissions de gaz à effet de serre, la collectivité développe le réseau cyclable, la signalisation et le stationnements associés et aménage la voirie afin d''encourager le développement de ce mode de déplacement.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.1', 'cae', '4.3.2.1', 'Connaître et analyser les déplacements et stationnements cyclistes du territoire', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.1.1', 'cae', '4.3.2.1.1', 'Analyser la situation en particulier à proximité des entreprises, magasins, lieux touristiques, installations fortement fréquentées', 'Par exemple : occupation des places de stationnement et environnement proche, durée du stationnement, règlement pour les vélos, points noirs, discontinuités dans le réseau, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.1.2', 'cae', '4.3.2.1.2', 'Mener des enquêtes périodiques sur la part modale des cyclistes et utiliser les études réalisées par d’autres organismes', 'Exemple INSEE pour les motifs travail et études ; baromètre des villes cyclables de la FUB tous les 2 ans.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.2', 'cae', '4.3.2.2', 'Consulter et informer les habitants', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.2.1', 'cae', '4.3.2.2.1', 'Évaluer le ressenti des habitants au regard du mode de déplacement cycliste', 'Par exemple: sondage de popularité, sentiment de sécurité, stationnement, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.2.2', 'cae', '4.3.2.2.2', 'Evaluer les besoins des cyclistes (notamment enquêtes spécifiques pour les déplacements de loisirs et de tourisme)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.2.3', 'cae', '4.3.2.2.3', 'Informer les cyclistes sur le réseau existant (notamment informations spécifiques à destination des touristes)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.2.4', 'cae', '4.3.2.2.4', 'Faire connaître les nouvelles règles du décret PAMA 2015 (cf. réglementation) et verbaliser les enfreintes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.3', 'cae', '4.3.2.3', 'Adopter un schéma directeur cyclable', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.3.1', 'cae', '4.3.2.3.1', 'Disposer d’un schéma directeur cyclable (ou modes actifs dans une approche mixte marche et vélo)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.3.2', 'cae', '4.3.2.3.2', 'Désigner au sein de l''administration un responsable du réseau cyclable et stationnement cyclable', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.3.3', 'cae', '4.3.2.3.3', 'Voter et adopter un budget pour le développement du réseau et des places de stationnement structurant (investissement et fonctionnement)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4', 'cae', '4.3.2.4', 'Mettre en œuvre son schéma directeur cyclable', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.1', 'cae', '4.3.2.4.1', 'Disposer de la compétence pour mettre en œuvre des actions', 'Pour les EPCI : prise de compétence optionnelle « voirie cyclable d’intérêt communautaire »
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.2', 'cae', '4.3.2.4.2', 'Anticiper et/ou réduire les conflits d’usage, notamment en traitant les points noirs du réseau pouvant freiner le développement de la mobilité cyclable', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.3', 'cae', '4.3.2.4.3', 'Traiter les discontinuités cyclables (plus important que l’augmentation du linéaire d’aménagements)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.4', 'cae', '4.3.2.4.4', 'Densifier le réseau cyclable en veillant à garantir un maillage desservant efficacement l''ensemble des lieux à forte fréquentation (notamment touristique)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.5', 'cae', '4.3.2.4.5', 'Limiter les détours et les obstacles pour les cyclistes en retravaillant le plan de circulation des voiries', 'Par exemple: autorisation du contre-sens sur les rues à sens unique, cédez le passage cycliste au feu,  goulottes dans les escaliers, barrière pivotante…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.6', 'cae', '4.3.2.4.6', 'Eliminer les endroits dangereux à la circulation cycliste (autour des écoles, traversée des routes principales, etc.), sécuriser et différencier les réseaux', 'Par exemple: pistes cyclables séparées de la voirie et larges lorsque les vitesses sont élevées, ouvrir les couloirs de bus et de tramway aux cyclistes, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.7', 'cae', '4.3.2.4.7', 'S''appuyer sur la signalisation des itinéraires afin d''accompagner et guider les cyclistes et penser le réseau cyclable conjointement aux autres réseaux de modes actifs de déplacements afin de favoriser l''intermodalité', 'Par exemple en s’appuyant sur les schémas directeurs touristiques
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.8', 'cae', '4.3.2.4.8', 'S''assurer de la qualité des aménagements cyclables et de leur entretien', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.4.9', 'cae', '4.3.2.4.9', 'Réaliser des enquêtes périodiques (comptages) et auprès d’un panel de cycliste (sondages, entretiens, …) pour évaluer l’évolution des pratiques.', 'Les conclusions de ces enquêtes sont utilisées et intégrées dans le cadre de l’amélioration continue du schéma directeur.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.5', 'cae', '4.3.2.5', 'Proposer des offres de stationnement vélo de qualité', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.5.1', 'cae', '4.3.2.5.1', 'Proposer des locaux vélos sécurisés, équipés et accessibles dans tous les lieux d’attractivité (les établissements collectifs, les entreprises, les commerces, les écoles)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.5.2', 'cae', '4.3.2.5.2', 'Soutenir des installations privées en particulier autour des hôtels et des complexes touristiques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.5.3', 'cae', '4.3.2.5.3', 'Sécuriser les stationnements en particulier à proximité de pôles multimodaux, des écoles et commerces principaux', 'Par exemple situer les aires de stationnement vélo en amont des passages piétons pour sécuriser la traversée des piétons, proposer un service d’ordre, un système électronique, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.6', 'cae', '4.3.2.6', 'Développer un ou des réseaux express vélo (REV) pour faciliter les déplacements entre les grandes villes et leur périphérie', 'Non évalué dans les DOM.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.7', 'cae', '4.3.2.7', 'Suivre et évaluer les résultats', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.7.1', 'cae', '4.3.2.7.1', 'Augmenter le % de voirie aménagée ou à défaut la longueur cumulée de pistes cyclables pour 1000 habitants', 'Voirie aménagée: pistes et bandes cyclables, les zones 30, les aires piétonnes, etc...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.7.2', 'cae', '4.3.2.7.2', 'Augmenter le nombre de places de stationnement vélo pour 100 habitants', 'Objectif : >2 pour les EPCI, >4 pour les communes.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.2.7.3', 'cae', '4.3.2.7.3', 'Augmenter le passage de vélos sur les pistes (nombre par jour)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3', 'cae', '4.3.3', 'Proposer une offre de transports publics attractive et de qualité', 'La collectivité souhaite développer et améliorer les performances du service public de transports publics dans une perspective d''augmentation de son usage et de diminution du recours à la voiture. Le développement et l''amélioration du service public passe par une augmentation du niveau de fréquence, la qualité des connexions, une densification du maillage, une tarification attractive, un bon service de bus de nuit, la qualité des points d''arrêt ainsi que des véhicules de transport.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.1', 'cae', '4.3.3.1', 'S’organiser en interne', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.1.1', 'cae', '4.3.3.1.1', 'Mettre en place une organisation interne et des moyens dédiés au développement et au suivi des transports en commun (budget alloué, personnel identifié)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.2', 'cae', '4.3.3.2', 'Évaluer et analyser les besoins en transports collectifs', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.2.1', 'cae', '4.3.3.2.1', 'Se rapprocher des AOM (autorités organisatrices de la mobilité) locales afin d''évaluer les besoins des usagers actuels', 'Celles-ci pourront fournir des éléments de diagnostic concernant : l''usage des transports collectifs (fréquentation des points d''arrêt en semaine et le week-end, origine et destination des usagers, types de titres de transport utilisés, difficultés rencontrées (en particulier sur les heures de pointe), l''offre de transport (type et nombre de véhicules en exploitation), les éléments contractuels (type de conventionnement, date du prochain conventionnement, montant de la subvention d''équilibre, recettes tarifaires), l''historique des éventuels dysfonctionnements (horaires non ajustés, retards, sous-capacité ou surcapacité des véhicules), la fiabilité lors d''événements climatiques extrêmes.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.2.2', 'cae', '4.3.3.2.2', 'Classer ses besoins par ordre de priorité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.2.3', 'cae', '4.3.3.2.3', 'Identifier les principaux générateurs de déplacement', 'Par exemple les entreprises privées ou encore les acteurs touristiques qui peuvent parfois générer des flux ponctuels très importants.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.2.4', 'cae', '4.3.3.2.4', 'Présenter les résultats de l''analyse aux responsables d''AOT-AOM, aux élus des collectivités concernées, aux associations d''usagers des transports, aux commerçants, aux gestionnaires de zones d''activités et aux entreprises de transport.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.2.5', 'cae', '4.3.3.2.5', 'Pour les collectivités concernées (littoral, DOM…), intègrer au diagnostic l’offre maritime/fluviale.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.3', 'cae', '4.3.3.3', 'Orienter l’achat de véhicules des transports publics et le renouvellement de flotte vers les carburants alternatifs (bioGNV, électrique et hydrogène)', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.3.1', 'cae', '4.3.3.3.1', 'Déployer des flottes de transport public à très faibles émissions en adaptant le choix du carburant alternatif aux ressources du territoire. Restreindre le cahier des charges aux carburants susnommés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.3.2', 'cae', '4.3.3.3.2', 'Augmenter le % de véhicules faiblement émetteurs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.3.3', 'cae', '4.3.3.3.3', 'S’engager dans une opération type « Objectif CO2 les transporteurs s’engagent »', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4', 'cae', '4.3.3.4', 'Améliorer l''efficacité du réseau de transports collectifs en collaboration avec les AOM et les principaux générateurs de déplacements', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4.1', 'cae', '4.3.3.4.1', 'Optimiser l''offre existante', 'Amélioration de la qualité de service – amplitude et couverture du territoire, cadencement et augmentation des fréquences, du maillage, ajustement de la localisation des arrêts, adaptation de la desserte, confort des véhicules, en associant les AOM (des indicateurs chiffrés attestent de la qualité de l’offre).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4.2', 'cae', '4.3.3.4.2', 'Donner la priorité aux transports publics dans le trafic, en lien avec les collectivités compétentes en matière de voirie', 'Par exemple: voies en site propre, commande de la signalisation, priorité aux feux, lors d''événements climatiques extrêmes… (l''évolution des vitesses commerciales peut en attester)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4.3', 'cae', '4.3.3.4.3', 'Développer des systèmes d''information réactifs pour informer en temps réel ou quasiment les usagers de l''état du trafic sur le réseau de TC', 'Par exemple: affichage dans les arrêts, réseaux sociaux, site internet...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4.4', 'cae', '4.3.3.4.4', 'Prendre des mesures pour assurer la fiabilité lors d''événements climatiques extrêmes, et la qualité des transports', 'En particulier le confort thermique et olfactif dans les transports en commun, en hiver et en été (ex : ombrage des stations de remisage).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4.5', 'cae', '4.3.3.4.5', 'Associer les principaux générateurs de déplacements à la réflexion, travailler en amont avec eux afin de les sensibiliser et les inciter à ajuster leur organisation et leurs horaires', 'Sans perdre de vue les éventuels risques d''une saturation du réseau de transports collectifs. La sensibilisation de ces acteurs porte notamment sur les enjeux associés à la mobilité et sur les bénéfices qu''ils peuvent en tirer (amélioration de la ponctualité des salariés, diminution de la fatigue, etc.).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4.6', 'cae', '4.3.3.4.6', 'Adapter et/ou améliorer l''accessibilité de l''offre de transports publics aux PMR', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.4.7', 'cae', '4.3.3.4.7', 'S’engager dans une opération type « Objectif CO2 les transporteurs s’engagent »', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.5', 'cae', '4.3.3.5', 'Diversifier l’offre de transport collectif, pour plus de souplesse', 'Les actions concernant l’utilisation de voitures partagées (co-voiturage, auto-partage) sont évaluées dans la mesure 4.3.4.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.5.1', 'cae', '4.3.3.5.1', 'Mettre en place des offres propres à la collectivité ou soutenir les offres/dessertes existantes (utilisation des transports scolaires…)', 'Par exemple: bus local, navettes y compris fluviales ou maritimes, TAD...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.6', 'cae', '4.3.3.6', 'Mettre en place une tarification adaptée', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.6.1', 'cae', '4.3.3.6.1', 'Créer une billettique unique', 'Par exemple: système de « pass » unique pour tous les transports en commun du territoire, voire pour plusieurs modes de transport.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.6.2', 'cae', '4.3.3.6.2', 'Mettre en place une offre de cartes journalières pour les transports publics et gratuité des transports en commun en cas d''épisodes de pollution', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.6.3', 'cae', '4.3.3.6.3', 'Adapter la tarification aux enjeux sociaux / aux classes d’âges', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.7', 'cae', '4.3.3.7', 'Evaluer et communiquer les résultats', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.7.1', 'cae', '4.3.3.7.1', 'Évaluer la progression de l''utilisation des transports publics', 'Enquêtes périodiques montrant une évolution favorable de la fréquentation des transports en commun : voyage/hab.an, nb d’usagers… (cf. indicateurs)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.7.2', 'cae', '4.3.3.7.2', 'Utiliser les résultats des enquêtes et les intégrer dans les processus d''amélioration', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.3.7.3', 'cae', '4.3.3.7.3', 'Communiquer sur les résultats', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4', 'cae', '4.3.4', 'Développer l''offre multimodale et l''intermodalité', 'La collectivité et ses partenaires proposent des offres multiples et complémentaires favorisant la multimodalité et l''intermodalité (auto-partage, co-voiturage, parkings relais, location de vélos...). La tarification et les modalités d''utilisation des services doivent être attractives et simples pour limiter le recours à la voiture individuelle.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.1', 'cae', '4.3.4.1', 'Analyser les besoins et l’offre multimodale existante', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.1.1', 'cae', '4.3.4.1.1', 'Analyser la situation en matière de co-voiturage, d’auto-partage, de location de vélos et services associés', 'Par exemple: offre existante sur place, comparaison avec d''autres collectivités...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.1.2', 'cae', '4.3.4.1.2', 'Connaître les enjeux de l’intermodalité et formaliser la volonté de faciliter l’intermodalité dans un document (ou est démontrée par des premières réalisations)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.1.3', 'cae', '4.3.4.1.3', 'Mettre à disposition des moyens pour la mise en œuvre d’actions en faveur de la multi-modalité et de l’intermodalité (budget alloué, personnel identifié)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.2', 'cae', '4.3.4.2', 'Faciliter le changement de mode de transport', '(En l’absence de transport en commun – contexte non adapté à son développement, les points sont reportés en totalité ou partiellement sur la voiture partagée)
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.2.1', 'cae', '4.3.4.2.1', 'Développer des parkings relais (P+R)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.2.2', 'cae', '4.3.4.2.2', 'Autoriser et faciliter l’embarquement des vélos dans les transports en commun lorsque les conditions d’exploitation le permettent', 'Par exemple: modulation éventuellement selon les heures, les lignes, les places disponibles, les types de véhicules…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.2.3', 'cae', '4.3.4.2.3', 'Mettre en place une tarification et une billettique unique/coordonnée entre les différentes offres de transport (TC, location de vélo, auto-partage…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.2.4', 'cae', '4.3.4.2.4', 'Installer un pôle d’échanges multimodal, combinant en même lieu plusieurs modes de transport alternatifs différents', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.3', 'cae', '4.3.4.3', 'Développer l''usage de la voiture partagée', 'La collectivité soutient techniquement les modes de transport en voiture partagée, comme le covoiturage, l''autopartage, l’auto-stop organisé : création d’aires de co-voiturage, signalisation des arrêts et des véhicules, garantie de déficit, assurance retour, participation à des SCIC, auto-partage des véhicules professionnels de la collectivité aux habitants du territoire le week-end.…
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.4', 'cae', '4.3.4.4', 'Equiper les habitants en vélo', 'Les actions de sensibilisation/animation autour du vélo sont traitées dans la mesure 4.1.1.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.4.1', 'cae', '4.3.4.4.1', 'Développer des offres de locations vélo afin de provoquer l’essai par la population (système de vélos en libre-service et/ou système de location longue durée)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.4.2', 'cae', '4.3.4.4.2', 'Aider directement les habitants en subventionnant l’achat de vélos classiques, vélos utilitaires, vélos pliants (pour l’intermodalité avec les transports en commun), ou de vélos à assistance électrique (VAE)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.5', 'cae', '4.3.4.5', 'Accompagner les cyclistes avec des services associés au vélo', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.5.1', 'cae', '4.3.4.5.1', 'Encourager l''apprentissage du vélo et inciter la réappropriation des deux roues notamment en milieu rural', 'Par exemple à l''école primaire : faciliter la mise en place de « bus cyclistes » animés par des employés municipaux ou des parents d''élèves pour amener les enfants à l''école de manière groupée.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.5.2', 'cae', '4.3.4.5.2', 'Soutenir les vélos-écoles pour déployer leurs missions d''accompagnement et d''apprentissage du vélo', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.5.3', 'cae', '4.3.4.5.3', 'Encourager le marquage des vélos « bicycode » et informer sur les meilleures astuces contre le vol', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.5.4', 'cae', '4.3.4.5.4', 'Soutenir les ateliers de réparation, les associations de recyclage, les coursiers à vélo, les maisons du vélo, etc.', '', '', '', '', null, null);
