insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.6.2', 'cae', '4.3.4.6.2', 'Constater de l''augmentation du parc de vélos en partage (vélo libre-service ou location longue durée)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.6.3', 'cae', '4.3.4.6.3', 'Réaliser des enquêtes périodiques (par exemple sur l''utilisation des différentes offres, sondages de popularité)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.4.6.4', 'cae', '4.3.4.6.4', 'Utiliser les résultats des enquêtes et les intégrer dans le processus d''amélioration', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1', 'cae', '4.3.1', 'Développer le réseau piétonnier', 'La collectivité développe le réseau piétonnier, la signalisation associée et aménage la voirie afin d''encourager le développement de ce mode de déplacement et favoriser le report modal.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.1', 'cae', '4.3.1.1', 'Se doter d’outils pour adopter une stratégie de développement du réseau piétonnier s''appuyant sur un apaisement de la circulation', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.1.1', 'cae', '4.3.1.1.1', 'Voter et adopter un budget pour le développement et l’amélioration du réseau', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.1.2', 'cae', '4.3.1.1.2', 'Désigner un responsable des cheminements pour piétons', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.1.3', 'cae', '4.3.1.1.3', 'Évaluer le ressenti des piétons sur l''usage de ce mode de déplacements* enquête période sur la part modale des piétons* sondages de popularité, sentiment de sécurité, etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.1.4', 'cae', '4.3.1.1.4', 'Élaborer un schéma directeur de la mobilité piétonne en pensant le réseau piéton conjointement aux autres réseaux de modes actifs de déplacements afin de favoriser l''intermodalité. Les collectivités rurales orientent leurs actions sur les lieux de fréquentation (centres bourgs, écoles, etc).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.2', 'cae', '4.3.1.2', 'Améliorer prioritairement la situation des zones à forts enjeux', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.2.1', 'cae', '4.3.1.2.1', 'Identifier les zones à enjeux importants en matière de mobilité piétonne (centre, zones résidentielles) et mettre en place des mesures (aménagement et signalétique) visant à l''apaisement de celles-ci (zones limitées à 30 km/h, zone de rencontres…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.2.2', 'cae', '4.3.1.2.2', 'Repérer les points noirs pouvant freiner le développement de la mobilité piétonne dans certaines zones et les traiter (aménager et sécuriser davantage de passages piétons, les abords des écoles, supprimer le maximum de stop et de feux tricolores en zone apaisée et rétablir la priorité à droite)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.3', 'cae', '4.3.1.3', 'Densifier et améliorer globalement la qualité du réseau piéton sur le territoire', 'La mise en place de supports de communication/promotion telle une carte piétonne intégrant les temps de parcours est valorisée dans la mesure 4.1.1.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.3.1', 'cae', '4.3.1.3.1', 'Densifier le réseau piéton en veillant à garantir un maillage desservant efficacement l''ensemble des lieux à forte fréquentation, les détours et lacunes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.3.2', 'cae', '4.3.1.3.2', 'S''appuyer sur la signalisation afin d''accompagner et guider les piétons', 'Accompagnement des piétons : jalonnement directionnel indiquant les temps d’accès, signalisation spécifique, commande des feux de signalisation favorable aux piétons, aide pour la traversée des routes principales, déblayage de la neige sur les chemins piétons en hiver, ombrage pour le confort d''été...
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.3.3', 'cae', '4.3.1.3.3', 'Accompagner les personnes à mobilité réduite (par ex. abaissement des trottoirs) plus que le minimum requis (outil : schémas d''accessibilité)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.4', 'cae', '4.3.1.4', '(20% dans les DOM) Devenir un territoire exemplaire en matière de mobilité piétonne', 'La généralisation de la zone 30 en ville est également valorisée dans l’action 4.2.2 « Réguler le trafic, réduire la vitesse et valoriser l''espace public ».
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.4.1', 'cae', '4.3.1.4.1', 'Se saisir des outils réglementaires, avec des scénarios ambitieux (large périmètre géographique, suffisamment de catégories de véhicules visés par les restrictions…) et des moyens de contrôle (respect par les usagers)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.4.2', 'cae', '4.3.1.4.2', 'Créer une ou plusieurs aires piétonnes', 'C’est une zone dédiée où seuls les tramways ont la priorité sur les piétons. Les cyclistes et les véhicules doivent rouler au pas. La circulation des véhicules motorisés est exceptionnelle, et conditionnée à la desserte de la zone, de même que le stationnement, y compris pour les résidents. Un accès temporaire à la zone piétonne peut être maintenu pour les véhicules motorisés des riverains et les livreurs qui disposent d''un badge pour passer les bornes automatiques.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.4.3', 'cae', '4.3.1.4.3', 'Initier des opérations d’usage dynamique de la voirie', 'Par exemple fermeture de tronçons à certains horaires ciblés ; entrées, sorties des écoles, opération évènementielle unique
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.4.4', 'cae', '4.3.1.4.4', 'Attester de l''exemplarité par l’augmentation du nombre de piétons', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_4.3.1.4.5', 'cae', '4.3.1.4.5', 'Communiquer autour des actions phares mises en place en faveur des piétons', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1', 'cae', '1', 'Planification territoriale', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1', 'cae', '1.1', 'Stratégie globale climat-air-énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3', 'cae', '1.1.3', 'Réaliser un diagnostic de vulnérabilité et s’engager dans un programme d''adaptation au changement climatique du territoire', 'La collectivité analyse les impacts potentiels du changement climatique et la vulnérabilité de son territoire et s’engage dans une démarche d’adaptation.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.1', 'cae', '1.1.3.1', 'Formaliser l’intérêt de la collectivité dans sa volonté de réduire la vulnérabilité du territoire au changement climatique', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.1.1', 'cae', '1.1.3.1.1', 'Prendre en compte les études réalisées au niveau régional ou interrégional (SRCAE/SRADDET, études MEDCIE ou autre), voire national (PNACC2)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.1.2', 'cae', '1.1.3.1.2', 'Allouer un budget et/ou des moyens humains', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.2', 'cae', '1.1.3.2', 'Connaître la vulnérabilité du territoire au changement climatique', 'Cette étude justifie des entretiens avec les acteurs du territoire (secteurs du bâtiment, de la santé, du tourisme, de l’agriculture et de la forêt, syndicats et gestionnaires d’infrastructures et réseaux d’eau, d’énergie, de transport, etc.) et n’est pas seulement réalisée à partir de bibliographie.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.2.1', 'cae', '1.1.3.2.1', 'Connaître les évolutions climatiques du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.2.2', 'cae', '1.1.3.2.2', 'Connaître les impacts économiques du changement climatique sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.2.3', 'cae', '1.1.3.2.3', 'Connaître les impacts sociaux du changement climatique sur le territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.2.4', 'cae', '1.1.3.2.4', 'Connaître les impacts en termes de biodiversité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.2.5', 'cae', '1.1.3.2.5', 'Connaître les impacts sur les réseaux : eau, assainissement, approvisionnement en énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.2.6', 'cae', '1.1.3.2.6', 'Connaître les impacts sur l’aménagement : îlots de chaleurs urbains', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.3', 'cae', '1.1.3.3', 'Adopter un programme d’actions d’adaptation et intégrer les effets du changement climatique dans les compétences et les politiques de la collectivité', 'La prise en compte de l’adaptation au changement climatique dans des actions opérationnelles plus précises est évaluée dans les mesures dédiées (opérations d’aménagement, bâtiments publics, eaux pluviales…).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.3.1', 'cae', '1.1.3.3.1', 'Définir un programme d’actions spécifique ou intégrer de façon transversale forte l’adaptation dans le programme d’actions climat-air-énergie', '', '', '', '', null, null);
