insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.1.3.4.2', 'cae', '1.1.3.4.2', 'Réaliser une revue de suivi périodique pour mesurer les progrès et l’état d’avancement de la politique d’adaptation au changement climatique', '', '', '', '', null, null);
