insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.3.3.5', 'cae', '1.3.3.3.5', 'Porter attention à la rénovation embarquée (vérification des justifications)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.3.3.6', 'cae', '1.3.3.3.6', 'Renvoyer vers les structures compétentes pour effectuer le suivi des consommations énergétiques avant et après travaux', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.3.4', 'cae', '1.3.3.4', 'Suivre les attestations de conformité', 'Les attestations de conformité (dépôt du PC ou à l''achèvement) servent à effectuer un suivi statistique des performances des constructions (suivi d''indicateurs, par ex : Cep ou BBio moyen sur tous les PC, nb de maisons avec Cep<RT2012-20%, statistique par quartier ou type de bâtiment, etc.).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2', 'cae', '1.3.2', 'Transcrire les enjeux climat-air-énergie et de préservation des ressources naturelles dans les opérations d''aménagement', 'L''efficacité énergétique, l’adaptation et la lutte contre le changement climatique et l''utilisation des énergies renouvelables, la préservation de la qualité de l''air, sont des critères importants dans les opérations d’aménagement de la collectivité.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.1', 'cae', '1.3.2.1', 'S’organiser pour bien piloter les opérations d’aménagement', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.1.1', 'cae', '1.3.2.1.1', 'Mettre en place un Système de Management d’Opération (SMO) pour la conduite et le suivi des projets', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.1.2', 'cae', '1.3.2.1.2', 'Mettre en place un dialogue partenarial et pluridisciplinaire en interne et en externe (CAUE, ALEC, agence d’urbanisme, espace conseil FAIRE, …) institutionnalisé (convention ou autres dispositifs cadrant la relation)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.1.3', 'cae', '1.3.2.1.3', 'Recourir à un assistant à maîtrise d’ouvrage doté de compétences climat-air-énergie si besoin de renforcer les compétences internes', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.2', 'cae', '1.3.2.2', 'Adopter une approche multithématique dans le diagnostic et la conception des projets urbains', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.2.1', 'cae', '1.3.2.2.1', 'Demander une approche intégrée de type AEU2 pour une analyse croisée des enjeux climat-air-énergie et urbains pour tous les projets d''urbanisme opérationnel maîtrisés par la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.2.2', 'cae', '1.3.2.2.2', 'Réaliser des études de prédéfinition des projets urbains et étude de l’approvisionnement énergétique et des productions d’énergie (y compris potentiel de développement des énergies renouvelable) pour les sites à enjeux (friches qui se libèrent en centre-bourg ou ville, extensions urbaines prévues dans le document d’urbanisme, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.2.3', 'cae', '1.3.2.2.3', 'Utiliser des outils de modélisation climat-air-énergie (de type Analyse de Cycle Vie sur l''échelle quartier dès la conception) pour aider à la décision', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.3', 'cae', '1.3.2.3', 'Elaborer un document "cadre" pour systématiser la qualité environnementale des opérations maîtrisées par la collectivité', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.3.1', 'cae', '1.3.2.3.1', 'Définir des préconisations à l’échelle du quartier ou de la ville inspirées notamment des outils nationaux existants (cahiers thématiques AEU2, label national Ecoquartier, HQE-Performance quartier…)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.3.2', 'cae', '1.3.2.3.2', 'Définir des critères à l’échelle du bâtiment : BEPos, label E+/C-, Maison passive, BBC, Effinergie, Ecodom, HQE avec objectifs énergie ou air définis, label bâtiment biosourcé, chauffage très performant (flamme verte 7 étoiles pour le bois ou équivalent) ou valorisation des rejets thermiques, confort d''été, qualité de l''air intérieur...', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.3.3', 'cae', '1.3.2.3.3', 'Utiliser ce document comme base de discussions et de réflexions entre les acteurs et l''adapter/décliner dans différents documents de conception opérationnels des projets (plan masse, notice environnementale, cahier des charges, cahier de prescriptions...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.4', 'cae', '1.3.2.4', 'Assurer la maîtrise d’ouvrage publique ou semi-publique des opérations d’aménagement clés pour mieux prendre en compte les enjeux climat-air-énergie et la préservation des ressources naturelles', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.4.1', 'cae', '1.3.2.4.1', 'Mettre en place une politique foncière active (droit de préemption urbain, zone d’aménagement différé -ZAD-, participation à un établissement foncier local –EPFL-...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.4.2', 'cae', '1.3.2.4.2', 'Intégrer des exigences climat-air-énergie dans les opérations d''aménagement clés (lotissements publics ou zones d’aménagement concertées) et les inscrire dans les cahiers des charges de cession de terrain', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.5', 'cae', '1.3.2.5', 'Intégrer des exigences climat-air-énergie dans tous les appels d’offre d’urbanisme et les cahiers des charges de cession des terrains', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.5.1', 'cae', '1.3.2.5.1', 'Intègrer dans ses critères de choix des offres l''impact environnemental de la proposition : kWh économisé, émissions de CO2 ou émissions/exposition aux polluants atmosphériques, etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.5.2', 'cae', '1.3.2.5.2', 'Utiliser des spécifications techniques pour fixer des objectifs de performances environnementales, de manière prescriptive, à l’échelle de la ville, du quartier (AEU2, HQE Performance Quartier...) et du bâtiment (niveau passif ou à énergie positive -label E+/C- demandé pour les bâtiments neufs, label BBC rénovation pour les travaux de rénovation, obligation si pertinent d’installation de panneaux solaires sur les nouvelles toitures de bâtiment ou de raccordement à un réseau de chaleur, exiger si pertinent davantage de places de vélos dans les constructions neuves, réalisation d’abris à vélo extérieurs attenants au bâtiment principal …).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.5.3', 'cae', '1.3.2.5.3', 'Intègrer dans les critères de jugement des offres les références environnementales et les pratiques internes de l''entreprise candidate (expérience requise en HQE ou équivalent, label de performance énergétique, formation à l’AEU2, certification ISO 14001/9001 de l’entreprise, démarche d''éco-responsabilité interne...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.5.4', 'cae', '1.3.2.5.4', 'Donner un caractère contraignant aux exigences (prescriptions et non simples recommandations, inscription dans les documents contractuels et opposables, contrôle, sanction prévue en cas de non-respect)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.6', 'cae', '1.3.2.6', 'Organiser la gestion des déchets des chantiers d''aménagement en partenariat avec les aménageurs', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.6.1', 'cae', '1.3.2.6.1', 'Fixer un objectif de recyclage à l''échelle des projets', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.6.2', 'cae', '1.3.2.6.2', 'Mettre en place une plateforme locale de récupération/échanges de matériaux de construction / démolition ou de terres excavées (à l''échelle territoriale ou du projet)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.7', 'cae', '1.3.2.7', 'Contrôler l’efficacité et le respect des exigences', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.7.1', 'cae', '1.3.2.7.1', 'Diffuser réellement les documents cadres et référentiels créés dans les services, s''assurer qu''ils sont appropriés et appliqués par les acteurs concernés', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.7.2', 'cae', '1.3.2.7.2', 'Mettre en place une approche d''amélioration continue pour améliorer les outils (référentiel, charte, etc.) selon le retour d''expérience', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.7.3', 'cae', '1.3.2.7.3', 'Prévoir le suivi réel des performances climat-air-énergie des bâtiments occupés pour certaines opérations emblématiques (instrumentation, prestation dédiée et soutenue/exigée par la collectivité)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.7.4', 'cae', '1.3.2.7.4', 'Montrer que l''intégration de critères environnementaux dans les consultations a déjà abouti réellement à faire basculer le choix en faveur du candidat le plus exemplaire sur ces aspects', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_1.3.2.7.5', 'cae', '1.3.2.7.5', 'Obtenir une reconnaissance nationale ou régionale d’une ou plusieurs opérations, certification d’une démarche (label économie circulaire, label éco-quartier…) ou de résultats obtenus, retombées presse ou visites de site par des acteurs extérieurs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5', 'cae', '5', 'Organisation interne', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1', 'cae', '5.1', 'Gouvernance', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2', 'cae', '5.1.2', 'Assurer le pilotage et le portage de la politique climat-air-énergie', 'Le comité de pilotage a pour tâche de traiter, au plus haut niveau, des questions climat air énergie.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.1', 'cae', '5.1.2.1', 'Définir un comité de pilotage transversal et légitime', '', '', '', '', null, 35.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.1.1', 'cae', '5.1.2.1.1', 'Légitimer la composition et le rôle du comité de pilotage par une décision de l’organe délibérant de la collectivité, de l’exécutif ou un autre document formel ayant fait l’objet de l’approbation du ou de la Président(e)/Maire de la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.1.2', 'cae', '5.1.2.1.2', 'Intégrer le comité de pilotage de manière pérenne dans le fonctionnement de la collectivité et dans les organes de direction politique.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.1.3', 'cae', '5.1.2.1.3', 'Identifier les pilotes techniques et politiques. En cas de COPIL conjoint ville-intercommunalité, des élus des deux collectivités participent et contribuent au comité de pilotage', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.1.4', 'cae', '5.1.2.1.4', 'Réunir au sein du comité de pilotage des élus et personnes référentes dans des thématiques variées, non restreintes uniquement à l’énergie ou l’environnement (mobilité, aménagement, énergie, eau, assainissement, organisation interne, partenariats et coopération, finances, etc.) afin de traiter les questions climat air énergie de façon transversale', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.1.5', 'cae', '5.1.2.1.5', 'Réunir d’autres acteurs du territoire au sein du comité de pilotage  (associations, organismes institutionnels, etc).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.2', 'cae', '5.1.2.2', 'Elaborer et valider les objectifs de la politique climat air énergie en comité de pilotage', '', '', '', '', null, 35.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.2.1', 'cae', '5.1.2.2.1', 'Réunir le comité de pilotage au minimum 2 fois par an avec ordre du jour puis rédiger un compte rendu de ses décisions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.2.2', 'cae', '5.1.2.2.2', 'Élaborer ou décider des objectifs climat air énergie au sein du comité de pilotage (a minima dans les 6 domaines Cit’ergie)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.2.3', 'cae', '5.1.2.2.3', 'Donner au comité de pilotage un rôle véritablement moteur pour la politique climat-air-énergie (décisions constructives et ambitieuses, lancement de nouvelles études ou actions…); Impulser via celui-ci une dynamique positive auprès des services', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.2.4', 'cae', '5.1.2.2.4', 'Porter les objectifs au plus haut niveau par les élus (maire ou président de la collectivité) et au sein des instances décisionnaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.2.5', 'cae', '5.1.2.2.5', 'Le/la Maire / Président(e) participe à certaines réunions (voire assure une présidence ou une co-présidence du COPIL) pour prouver l’implication politique au plus haut niveau dans la démarche', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.3', 'cae', '5.1.2.3', 'Assurer la mise en œuvre effective des décisions prises', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.3.1', 'cae', '5.1.2.3.1', 'Organiser le comité de pilotage pour suivre la mise en œuvre effective des décisions prises (tableau de suivi par exemple)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.3.2', 'cae', '5.1.2.3.2', 'Le comité de pilotage attribue des missions externes pour la réalisation des actions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.3.3', 'cae', '5.1.2.3.3', 'Les directeurs de service, et notamment DGS, DGA et/ou DST apportent du soutien au comité de pilotage pour la mise en place opérationnelle des décisions (relais au sein des différents services notamment)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.2.3.4', 'cae', '5.1.2.3.4', 'Le comité de pilotage veille à la bonne compréhension par les habitants et usagers du territoire des décisions prises (information par voie de presse, participation des membres du comité de pilotage à des réunions publiques, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3', 'cae', '5.1.3', 'Former et mobiliser les élus et les services sur les thématiques climat-air-énergie', 'La collectivité propose des actions de sensibilisation et de formation sur l’adaptation au changement climatique, la pollution de l’air, les émissions de GES (gaz à effet de serre), les énergies renouvelables et la maîtrise des consommations d''énergie à ses élus et à l''ensemble de ses agents (techniciens, directeurs, …).
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.1', 'cae', '5.1.3.1', 'Accompagner les élus et les agents dans la pratique des éco-gestes quotidiens', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.1.1', 'cae', '5.1.3.1.1', 'Accompagner les élus et les agents dans la pratique des éco-gestes quotidiens (politique de numérisation des documents, impression recto-verso, extinction des lumières et des ordinateurs, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.2', 'cae', '5.1.3.2', 'Formaliser le plan de formation/sensibilisation des agents et des élus', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.2.1', 'cae', '5.1.3.2.1', 'Décliner un plan de formation/sensibilisation pour chacun (techniciens, directeurs, élus, …) en fonction de ses besoins et de son degré d’implication dans l’atteinte des objectifs du programme d’actions climat-air-énergie de la collectivité et du territoire', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.2.2', 'cae', '5.1.3.2.2', 'Rendre visibles et varier dans la liste des formations proposées celles en lien avec le climat, l''air ou l''énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.3', 'cae', '5.1.3.3', 'Accompagner les élus et les agents sur des thématiques particulières', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.3.1', 'cae', '5.1.3.3.1', 'Les agents ont analysé l''impact du changement climatique sur leurs activités et sont accompagnés pour s''y adapter', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.3.2', 'cae', '5.1.3.3.2', 'Traiter le sujet de la qualité de l''air de manière transversale et ne pas le cantonner au service santé', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.4', 'cae', '5.1.3.4', 'Mettre en œuvre le plan de formation/sensibilisation climat-air-énergie', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.4.1', 'cae', '5.1.3.4.1', 'Encourager réellement les activités de formation/sensibilisation (prendre les frais à sa charge, attribuer le temps nécessaire)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.4.2', 'cae', '5.1.3.4.2', 'Les personnes identifiées participent effectivement aux formations climat-air-énergie sur lesquelles elles se sont positionnées', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.4.3', 'cae', '5.1.3.4.3', 'La liste des formations effectivement réalisées sur le sujet climat-air-énergie est variée (plusieurs thématiques et services concernés)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.4.4', 'cae', '5.1.3.4.4', 'Intégrer un système d’évaluation dans les formations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.5', 'cae', '5.1.3.5', 'Engager et motiver les élus et les agents', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.5.1', 'cae', '5.1.3.5.1', 'Fixer collectivement ou par service des objectifs concernant la performance climat air énergie de la collectivité (ils sont fixés par écrit, sont mesurables et ont un délai)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.5.2', 'cae', '5.1.3.5.2', 'Disposer d''un système de reconnaissance des initiatives (individuelles ou collectives) ou de l''atteinte des objectifs', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.5.3', 'cae', '5.1.3.5.3', 'Les agents font part de leurs idées d''améliorations (objectifs ou bonnes pratiques)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.5.4', 'cae', '5.1.3.5.4', 'Organiser des évènements "festifs" ou mettre en place un dispositif d’émulation pour former et sensibiliser les agents et les élus (concours inter-services, semaines de l''efficacité énergétique, semaine de la lutte contre le changement climatique ou de la qualité de l''air, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.5.5', 'cae', '5.1.3.5.5', 'Les élus participent régulièrement à des visites de site, des conférences, etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.6', 'cae', '5.1.3.6', 'Evaluer et valoriser l’efficacité des actions de formation et de mobilisation', '', '', '', '', null, 15.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.6.1', 'cae', '5.1.3.6.1', 'Valoriser l’atteinte des objectifs de performances ou les bonnes pratiques des services (newsletter, bulletin de paie, intranet, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.6.2', 'cae', '5.1.3.6.2', 'Porter à connaissance de l’ensemble des services de la collectivité le plan de formation (formations proposées) et le bilan quantitatif et qualitatif des formations (nombre de sessions, nombre d’inscrits, évaluation de la satisfaction des stagiaires, ...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.3.6.3', 'cae', '5.1.3.6.3', 'Suivre et mesurer l’efficacité des formations/sensibilisation à travers des indicateurs spécifiques (évaluation des acquis de la formation, évaluation du passage à l’action suite à la formation, enquête de satisfaction, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4', 'cae', '5.1.4', 'Suivre l''avancement et évaluer régulièrement la politique climat-air-énergie', 'Cette mesure porte sur le suivi et l’évaluation du programme d''actions climat air énergie.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.1', 'cae', '5.1.4.1', 'Se doter d’outils et de moyens pour suivre et évaluer son plan d’action', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.1.1', 'cae', '5.1.4.1.1', 'Disposer d’un tableau de bord opérationnel pour le suivi du plan d’action', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.1.2', 'cae', '5.1.4.1.2', 'Prévoir précisément les modalités du suivi : fréquence annuelle de réunion à minima, date de mise à jour du tableau de bord, constitution de l’équipe de suivi, modalités de collecte des données nécessaires à l’actualisation du tableau de bord, etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.1.3', 'cae', '5.1.4.1.3', 'Constituer et mettre en place une instance spécifique de suivi-évaluation: comité de pilotage établi ou instance spécifique, cela peut être une instance neutre (citoyenne)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.1.4', 'cae', '5.1.4.1.4', 'Planifier l''information des résultats annuels au public', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.2', 'cae', '5.1.4.2', 'Suivre et évaluer régulièrement l’atteinte des objectifs', '', '', '', '', null, 40.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.2.1', 'cae', '5.1.4.2.1', 'Disposer d''un suivi effectif : Vérifier l''atteinte des objectifs régulièrement pour l’ensemble des thématiques climat-air-énergie à travers des indicateurs qualitatifs et quantitatifs (pas de fréquents reports et espacements des réunions de suivi)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.2.2', 'cae', '5.1.4.2.2', 'Actualiser annuellement le calendrier, le budget, les personnes responsables et l''état d''avancement des actions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.2.3', 'cae', '5.1.4.2.3', 'Partager le tableau de bord au sein de l’équipe projet qui le renseigne et le fait évoluer', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.3', 'cae', '5.1.4.3', 'Communiquer et informer les acteurs du territoire de l’avancement du programme d’actions', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.3.1', 'cae', '5.1.4.3.1', 'Synthétiser les résultats des actions', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.3.2', 'cae', '5.1.4.3.2', 'Publier les résultats et le suivi annuel : en interne, dans les instances concernées (commissions, conseil municipal, bureau, conseil communautaire..); en externe sur le site de la collectivité, lors de réunions publiques, dans le rapport de DD, dans le rapport d’activité, etc.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.3.3', 'cae', '5.1.4.3.3', 'Intégrer des acteurs du territoire (habitants, acteurs socio-économiques) au dispositif d''évaluation par exemple dans une instance spécifique (évaluation participative)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.3.4', 'cae', '5.1.4.3.4', 'Aller au-delà de l''exigence règlementaire de mise à disposition, au bout de 3 ans, d''un rapport au public sur la mise en œuvre du PCAET', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.4', 'cae', '5.1.4.4', 'Faire évoluer son programme d’actions grâce au dispositif de suivi et d’évaluation', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.4.1', 'cae', '5.1.4.4.1', 'Actualiser le programme d''actions et l''adapter chaque année', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.4.4.2', 'cae', '5.1.4.4.2', 'Mettre en place un cycle d''amélioration continue (objectif-planification-réalisation-contrôle-objectif)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1', 'cae', '5.1.1', 'Organiser les ressources humaines pour mener la politique climat-air-énergie', 'La collectivité met à disposition les ressources nécessaires en personnel pour les questions liées au climat, à l''air et à l''énergie. Les responsabilités, les rôles et les tâches ainsi que les pouvoirs de décision sont fixés et formalisés.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.1', 'cae', '5.1.1.1', 'Doter la politique climat-air-énergie de moyens humains', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.1.1', 'cae', '5.1.1.1.1', 'Disposer d''une ou plusieurs personnes responsables des questions climat-air-énergie au niveau technique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.1.2', 'cae', '5.1.1.1.2', 'Attribuer une mission de conseil à des personnes ou entreprises externes spécialisées (par exemple  conseiller FAIRE, Agence de mobilité, Agence locale, association spécialisée, ...) ou procèder à un recrutement en interne si les ressources humaines sont insuffisantes.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.1.3', 'cae', '5.1.1.1.3', 'Désigner un chef de projet climat-air-énergie ; en cas d’indisponibilité (arrêt maladie longue durée, maternité, départ…), son remplacement est une priorité (pas d’absence de longue durée (ex : plus de 3 mois)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.2', 'cae', '5.1.1.2', 'Décliner finement les responsabilités et les référents', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.2.1', 'cae', '5.1.1.2.1', 'Des référents par service sont identifiés pour les différents sujets', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.2.2', 'cae', '5.1.1.2.2', 'Identifier une ou des personnes pour garantir le bon fonctionnement des services et fournir des services spécifiques en cas de phénomènes extrêmes liés au changement climatique (canicule, tempête...).', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.2.3', 'cae', '5.1.1.2.3', 'Compléter systématiquement les instructions de travail et les fiches de poste pour les agents concernés avec des tâches couvrant l’ensemble des thématiques climat-air-énergie (urbanisme, bâtiments, mobilité, adaptation, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.3', 'cae', '5.1.1.3', 'Travailler en transversalité', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.3.1', 'cae', '5.1.1.3.1', 'Aménager le poste du chef de projet climat-air-énergie pour lui permettre de se consacrer pleinement à sa mission et de travailler en transversalité et de manière légitime avec les autres services (positionnement stratégique dans l''organigramme)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.3.2', 'cae', '5.1.1.3.2', 'Disposer d''une équipe projet transversale, pilotée par le chef de projet, chargée d’assurer la mise en œuvre de la stratégie climat-air-énergie ; elle se réunit régulièrement et dynamise la démarche en interne', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.3.3', 'cae', '5.1.1.3.3', 'Avoir des DGS, DGA et/ou DST qui sont impliqués et contribuent au portage et soutiennent le chef de projet (notamment vis à vis de la transversalité et de la mobilisation des autres agents)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.3.4', 'cae', '5.1.1.3.4', 'Former ponctuellement des groupes de travail techniques mêlant différent services selon les besoins et projets', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.4', 'cae', '5.1.1.4', 'Mesurer l’efficacité de l’organisation interne et l’ajuster si nécessaire', '', '', '', '', null, 30.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.4.1', 'cae', '5.1.1.4.1', 'Mettre en place une organisation interne qui permet une circulation rapide des informations', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.4.2', 'cae', '5.1.1.4.2', 'Évaluer si le personnel et des référents spécialisés sont actifs et efficaces (ils sont sollicités par les autres services, leurs collègues, leurs supérieurs hiérarchiques ou le grand public et ils répondent de manière réactive et pertinente) ; si non, réajuster l’organisation interne', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.4.3', 'cae', '5.1.1.4.3', 'Les agents améliorent la qualité de l''organisation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.4.4', 'cae', '5.1.1.4.4', 'Les fiches de postes sont complétées et améliorées', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.1.1.4.5', 'cae', '5.1.1.4.5', 'Le nombre d’équivalents temps plein (ETP) dédiés à la mise en œuvre de la politique climat-air-énergie est maintenu ou en augmentation', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2', 'cae', '5.2', 'Finances et commande publique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1', 'cae', '5.2.1', 'Financer la politique Climat Air Energie et réaliser une évaluation climat du budget', 'La collectivité identifie et se dote chaque année d''un budget directement lié à sa politique climat-air-énergie. Il s''agit du budget destiné à des actions de soutien (techniques ou financières) à la maîtrise de la demande d''énergie, à l''efficacité énergétique, aux énergies renouvelables, à la baisse des émissions de GES, des polluants atmosphériques et à l''adaptation au changement climatique, en fonctionnement et en investissement.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.1', 'cae', '5.2.1.1', 'Sensibiliser les élu.e.s et former les équipes à la prise en compte de la politique climat-air-énergie dans le processus d’élaboration budgétaire', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.1.1', 'cae', '5.2.1.1.1', 'Sensibiliser l’ensemble des élu.e.s à l’importance de piloter la politique climat-air-énergie dans ses aspects budgétaires, et leur présenter l’intérêt de réaliser une évaluation climat du budget', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.1.2', 'cae', '5.2.1.1.2', 'Nommer une équipe transversale pour prendre en charge les aspects budgétaires liés à la politique climat-air-énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.1.3', 'cae', '5.2.1.1.3', 'Former l''équipe en charge des aspects budgétaires liés à la politique climat-air-énergie sur l’évaluation climat du budget, la budgétisation de la politique climat-air-énergie, et aux dispositifs de financement de cette politique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.1.4', 'cae', '5.2.1.1.4', 'Sensibiliser l’ensemble des services à l’importance de prendre en compte les enjeux climat-air-énergie lors de toute programmation de dépenses', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.2', 'cae', '5.2.1.2', 'Attribuer un budget pour la politique climat-air-énergie', '', '', '', '', null, 25.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.2.1', 'cae', '5.2.1.2.1', 'Faire correspondre à chaque étape de la construction de la politique climat-air-énergie un budget : action de sensibilisation, de communication, diagnostic, suivi, évaluation, programme d’actions. Ce budget de la politique climat-air-énergie est traduit en une trajectoire annuelle de dépenses.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.2.2', 'cae', '5.2.1.2.2', 'Disposer d’un plan pluriannuel d’investissement qui intègre des actions climat-air-énergie en ligne avec ses documents programme', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.2.3', 'cae', '5.2.1.2.3', 'Allouer le budget chaque année par le conseil de la collectivité. Acter un document donnant une lecture transversale de l’ensemble des recettes et dépenses qui concourent aux objectifs de la politique climat-air-énergie', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.3', 'cae', '5.2.1.3', 'Réaliser chaque année une évaluation climat du budget', 'La collectivité mène chaque année une évaluation globale de son budget sous l’angle “climat” en déterminant les actions en faveur de la neutralité carbone et celles qui contribuent à émettre des gaz à effet de serre, en suivant la méthodologie proposée par I4CE par exemple. Les résultats de cette évaluation climat du budget sont présentés aux élu.e.s en amont des discussions budgétaires et du vote du budget.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.4', 'cae', '5.2.1.4', 'Analyser les marges de manœuvre budgétaires de la collectivité sur la base de l’évaluation climat du budget', 'Commission thématique par commission thématique, une analyse est réalisée des marges de manœuvre possible pour l’amélioration de l’impact climat du budget de la collectivité, à la fois pour augmenter les dépenses favorables d’un point de vue climatique et diminuer celles qui sont défavorables.
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.5', 'cae', '5.2.1.5', 'Diversifier les modes de financement pour atteindre les objectifs climat air énergie et innover', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.5.1', 'cae', '5.2.1.5.1', 'La collectivité étudie l’ensemble des sources de financement possibles pour les actions de sa politique climat-aire-énergie.', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.5.2', 'cae', '5.2.1.5.2', 'La collectivité fait preuve d’innovation pour le financement des actions de sa politique climat-air-énergie (CEE, CPE, MDE et réaffectation de recettes, projets citoyens)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.6', 'cae', '5.2.1.6', 'Suivre annuellement le budget', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.6.1', 'cae', '5.2.1.6.1', 'Suivre annuellement le budget total pour la politique climat-air-énergie par un indicateur en euros/an/habitant', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.6.2', 'cae', '5.2.1.6.2', 'Orienter les dépenses budgétaires grâce à l’analyse des marges de manœuvre tirée de l’évaluation climat du budget', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.7', 'cae', '5.2.1.7', 'Être cohérent dans sa politique de soutien financier', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.7.1', 'cae', '5.2.1.7.1', 'Apporter du soutien financier à des thématiques environnementales variées (bâtiments, énergie, air, eau, agriculture, ENR, mobilité...), à des cibles variées (particuliers, association, entreprises, agriculteurs...), et sous différentes formes (partenariat avec des banques, tiers-investissement, fonds de garantie, fonds d''investissement, prise de participation dans des SCIC/SEM œuvrant pour le DD ou des SAS/SA portant des projets de production d’énergie renouvelable sur leur territoire...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.7.2', 'cae', '5.2.1.7.2', 'Diposer d''un règlement des aides clair et bien le diffuser', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.7.3', 'cae', '5.2.1.7.3', 'Ne pas octroyer d’aides pour des projets en contradiction avec les enjeux climatiques et énergétiques (ex : courses automobiles, soutien aux aéroports, grands projets routiers,...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.1.7.4', 'cae', '5.2.1.7.4', 'Pratiquer l''éco-conditionnement des aides habituelles de la collectivité lorsque cela est possible (bonification, différenciation des montants d’aides selon des critères)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2', 'cae', '5.2.2', 'Etre exemplaire en matière d''écoresponsabilité de la commande publique', 'La collectivité intègre systématiquement dans ses marchés publics d''achat de biens matériels ou de services, des critères d''efficacité énergétique, de réduction de l''impact environnemental (y compris qualité de l''air), de coût du cycle de vie s''ils existent, par exemple pour :
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.1', 'cae', '5.2.2.1', 'S''organiser et mobiliser en interne', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.1.1', 'cae', '5.2.2.1.1', 'Identifier un agent référent en interne : cet agent a été formé sur les possibilités d''intégration du développement durable dans les marchés publics (référent "achat durable”)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.1.2', 'cae', '5.2.2.1.2', 'Sensibiliser et mobiliser les élus, les inciter à adopter une politique volontariste : connaissance de la logique cycle de vie des produits et services et de la notion de coût du cycle de vie, connaissance des éco-labels', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.2', 'cae', '5.2.2.2', 'Voter une délibération sur la mise en œuvre de clauses environnementales (et sociales) dans les marchés publics', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.2.1', 'cae', '5.2.2.2.1', 'Décider de prendre en compte les aspects énergie et environnement, notamment l''impact énergétique et GES de la fabrication et du transport des marchandises, et les coûts globaux lors des achats, incluant la consommation d’énergie à l’utilisation (délibération, note de services)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.2.2', 'cae', '5.2.2.2.2', 'Mettre à disposition des agents et élus des directives générales avec critères pour les marchés publics', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.3', 'cae', '5.2.2.3', 'Mener les actions simples', '', '', '', '', null, 10.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.3.1', 'cae', '5.2.2.3.1', 'Identifier et changer les pratiques pour les produits faciles à éviter ou substituer', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.3.2', 'cae', '5.2.2.3.2', 'Effectuer les achats d''articles en papier (papier à imprimer, papier hygiénique), de détergents et d''appareils de bureau selon des critères écologiques (exemple : écolabel et notamment écolabel européen)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.3.3', 'cae', '5.2.2.3.3', 'Privilégier les produits de saison, locaux et biologiques pour les évènements ponctuels organisés par la collectivité, les achats alimentaires', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.4', 'cae', '5.2.2.4', 'Contribuer à un réseau local achats responsables', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.4.1', 'cae', '5.2.2.4.1', 'Entrer dans un réseau local d''achats responsables et y participer activement', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.4.2', 'cae', '5.2.2.4.2', 'Favoriser les plateformes locales de distribution', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.5', 'cae', '5.2.2.5', 'Adopter une délibération définissant un plan pour les achats responsables, recenser les pratiques, effectuer un état des lieux de la production locale', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.5.1', 'cae', '5.2.2.5.1', 'Connaître et formaliser dans un document (listing) l''étendue de l''offre locale pour répondre aux besoins de la collectivité (producteurs, fournisseurs...)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.5.2', 'cae', '5.2.2.5.2', 'Identifier dans cette délibération, les pratiques d''achats et les améliorations à apporter', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.5.3', 'cae', '5.2.2.5.3', 'Détailler les directives pour les achats dans les domaines du matériel du bureau, entretien des bâtiments, véhicules et mobilité, matériaux de construction', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6', 'cae', '5.2.2.6', 'Appliquer des clauses environnementales variées et systématiser la logique cycle de vie des produits et services, afin de déterminer les dispositions environnementales pertinentes selon les familles d''achat', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6.1', 'cae', '5.2.2.6.1', 'Inviter les candidats à répondre aux appels d''offres de manière électronique', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6.2', 'cae', '5.2.2.6.2', 'Évaluer les fournisseurs sur l’application de leurs engagements environnementaux pendant le déroulement de leur prestation (par exemple prestataires de restauration collective ou travaux)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6.3', 'cae', '5.2.2.6.3', 'Encourager la fourniture du bilan GES des produits/services achetés dans les appels d’offres', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6.4', 'cae', '5.2.2.6.4', 'Effectuer l''entretien des bâtiments et des espaces publics selon des critères écologiques', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6.5', 'cae', '5.2.2.6.5', 'Effectuer le choix des matériaux de construction selon des critères écologiques (le bois est notamment systématiquement étudié) et repose sur les gisements locaux (Pisé, paille, etc.)', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6.6', 'cae', '5.2.2.6.6', 'Choisir les techniques et matériaux de finition, ainsi que le mobilier pour limiter la pollution de l''air intérieur (faibles émissions de COV et formaldéhydes notamment), soit en privilégiant des produits avec étiquette A+, soit en achetant des produits d’occasion', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.6.7', 'cae', '5.2.2.6.7', 'Inclure la prise en compte de la durée de vie et de la fin de vie des produits achetés dans les critères d’achats', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.7', 'cae', '5.2.2.7', 'Participer à l''amélioration de l''offre en services et produits responsables / entraîner les acteurs locaux', '', '', '', '', null, 5.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.7.1', 'cae', '5.2.2.7.1', 'Informer les fournisseurs et producteurs de la politique d’achats responsables par une communication ciblée auprès des entreprises concernées, voire via la plateforme d’achat de la collectivité', '', '', '', '', null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.8', 'cae', '5.2.2.8', 'Passer au crible l''ensemble des achats publics', '', '', '', '', null, 20.0);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, points, pourcentage) values ('cae_5.2.2.8.1', 'cae', '5.2.2.8.1', '% des marchés (en nombre) intégrant des clauses environnementales (et sociales) dans les spécifications techniques ou les critères d’attribution en augmentation', '', '', '', '', null, null);
